INSERT INTO 'NhomCauHoi' (Id,Nhom) VALUES
(1,'Khái niệm'),
(2,'Quy tắc giao thông'),
(3,'Tốc độ'),
(4,'Nghiệp vụ vận tải'),
(5,'Văn hóa, đạo đức nghề nghiệp người lái xe'),
(6,'Kỹ thuật lái xe & Cấu tạo và sửa chữa xe ô tô'),
(7,'Hệ thống biển báo'),
(8,'Giải các thế sa hình');
INSERT INTO 'CauHoi' (Id,IdNhomCauHoi,CauHoi,GiaiThich,A1,A2,A3A4,B1) VALUES
(1,1,'Khái niệm đường bộ" được hiểu như thế nào là đúng?"','Theo Luật GTĐB, Điều 3.1, thì đường bộ gồm: đường, cầu đường bộ, hầm đường bộ, bến phà đường bộ; chứ không bao gồm các công trình phụ trợ khác". Do đó, đáp án đúng là 1 và 2. Đáp án 3 sai vì có "các công trình phụ trợ khác"."',1,1,1,1),
(2,1,'Khái niệm Công trình đường bộ" được hiểu như thế nào là đúng ?"','Theo Luật GTĐB, Điều 3.2, công trình đường bộ gồm nội dung của đáp án 1 và 2, nhưng không có nhà ga đường sắt, cảng hàng không". Nếu suy luận thông thường, ga đường sắt và cảng hàng không thì không phải là đường bộ. Do đó, đáp án đúng là 1 và 2. Còn đáp án 3 sai vì gồm cả "... ga đường sắt, cảng hàng không"."',0,1,1,1),
(3,1,'Vạch kẻ đường" được hiểu thế nào là đúng?"','Vạch kẻ đường chỉ xét vị trí dừng, không tính đến vị trí đỗ (Luật GTĐB, Điều 10.5). Đáp án 2 sai vì có cụm từ đỗ trên đường". Đáp án 3 sai vì liệt kê cả phương án 2. Đáp án đúng: 1."',1,1,1,1),
(4,1,'Khái niệm Phần đường xe chạy" được hiểu như thế nào là đúng?"','Đáp án 1 đúng: khái niệm phần đường xe chạy được quy định tại Luật GTĐB, Điều 3.6. Theo đó, phần đường xe chạy không bao gồm dải đất dọc hai bên đường". Đáp án 2 và 3 có cụm từ "dải đất" nên đều sai."',1,1,1,1),
(5,1,'Khái niệm làn đường" được hiểu như thế nào là đúng ?"','Khái niệm làn đường" quy định tại Điều 3.7 Luật GTĐB. Đáp án 1 sai vì chia làn theo chiều "ngang" đường. Đáp án 2 đúng: chia theo chiều "dọc" và có bề rộng đủ cho xe "chạy"."',1,1,1,1),
(6,1,'Khái niệm khổ giới hạn của đường bộ" được hiểu như thế nào là đúng?"','Điều 3.8 Luật GTĐB 2008 quy định:“Khổ giới hạn của đường bộ là khoảng trống có kích thước giới hạn về chiều cao, chiều rộng của đường, cầu, bến phà, hầm đường bộ để các xe kể cả hàng hóa xếp trên xe đi qua được an toàn.”Như vậy khổ giới hạn của đường bộ gồm cả “chiều cao” và “chiều rộng”.Đáp án 2 sai vì chỉ có “chiều rộng”, đáp án 3 sai vì chỉ có “chiều cao”. Đáp án 1 đúng.',0,1,1,1),
(7,1,'Khái niệm "đường phố" được hiểu như thế nào là đúng?','Theo Điều 3.9 Luật Giao thông đường bộ 2008: “Đường phố là đường đô thị, gồm lòng đường và hè phố”.Đáp án 2 sai vì có cả đường bộ “ngoài” đô thị. Đáp án 1 đúng, vì đúng theo luật.',1,1,1,1),
(8,1,'Khái niệm “Dải phân cách”được hiểu như thế nào là đúng?','Theo điều 3.10 Luật GTĐB 2008:“Dải phân cách là bộ phận của đường để phân chia mặt đường thành hai chiều xe chạy riêng biệt hoặc để phân chia phần đường của xe cơ giới và xe thô sơ. Dải phân cách gồm loại cố định và loại di động.”Đáp án 2 và 3 sai, do không có “phân chia mặt đường” hoặc “phân chia phần đường”. Đáp án 1 đúng.',1,1,1,1),
(9,1,'Dải phân cách trên đường bộ có những loại nào?','Theo Điều 3.10 Luật GTĐB 2008: “Dải phân cách gồm loại cố định và loại di động.”Như vậy đáp án đúng là cả 1 và 2.',1,1,1,1),
(10,1,'Khái niệm “Đường cao tốc” được hiểu như thế nào là đúng?','Điều 3.12 Luật GTĐB 2008 quy định:“Đường cao tốc là đường dành cho xe cơ giới, có dải phân cách chia đường cho xe chạy hai chiều riêng biệt; không giao nhau cùng mức với một hoặc các đường khác; được bố trí đầy đủ trang thiết bị phục vụ, bảo đảm giao thông liên tục, an toàn, rút ngắn thời gian hành trình và chỉ cho xe ra, vào ở những điểm nhất định.”Đáp án 2 sai, do thừa chữ trong cụm từ “hai chiều ngược nhau riêng biệt”, và thiếu cụm từ “chỉ cho xe ra, vào ở những điểm nhất định”. Đáp án 1 đúng.',0,1,1,1),
(11,1,'Khái niệm "đường chính" được hiểu như thế nào là đúng?','Điều 3.13 Luật GTĐB 2008 quy định:“Đường chính là đường bảo đảm giao thông chủ yếu trong khu vực”.Như vậy đáp án 1 đúng. Đáp án 2 sai do nội dung khác biệt và có cụm từ “đường quốc lộ”. Đáp án 3 sai nội dung: thừa “là đường trong đô thị hoặc đường tỉnh”.',0,1,1,1),
(12,1,'Khái niệm "đường ưu tiên" được hiểu như thế nào là đúng?','Điều 3.15 Luật GTĐB 2008 quy định:“Đường ưu tiên là đường mà trên đó phương tiện tham gia giao thông đường bộ được các phương tiện giao thông đến từ hướng khác nhường đường khi qua nơi đường giao nhau, được cắm biển báo hiệu đường ưu tiên.”Như vậy xe đi trên đường ưu tiên sẽ được xe đi đến từ hướng khác nhường đường.Đáp án 2 đúng.Đáp án 1 sai vì xe trên đường ưu tiên “phải nhường đường”. Đáp án 3 sai, vì xe trên đường ưu tiên không giới hạn phương tiện nào, chứ không phải “chỉ dành cho một số loại phương tiện tham gia giao thông”.',1,1,1,1),
(13,1,'"Phương tiện giao thông đường bộ" gồm những loại nào?','Điều 3.17 Luật GTĐB 2008 quy định:“Phương tiện giao thông đường bộ gồm phương tiện giao thông cơ giới đường bộ, phương tiện giao thông thô sơ đường bộ.”Đáp án 1 đúng, vì giống hệt như trong luật. Đáp án 2 sai vì thừa “xe máy chuyên dùng”. Xe máy chuyên dùng thực ra chỉ là phương tiện tham gia giao thông đường bộ thôi (khác nhau chữ “tham gia”). Đáp án 3 sai vì liệt kê cả đáp án 2.',1,1,1,1),
(14,1,'Khái niệm "giao thông cơ giới đường bộ" được hiểu như thế nào là đúng?','Theo như Điều 3.18 của Luật GTĐB 2008, thì“Phương tiện giao thông cơ giới đường bộ gồm xe ô tô; máy kéo; rơ moóc hoặc sơ mi rơ moóc được kéo bởi xe ô tô, máy kéo; xe mô tô hai bánh; xe mô tô ba bánh; xe gắn máy (kể cả xe máy điện) và các loại xe tương tự.”Đáp án 1 sai vì liệt kê cả “xe máy chuyên dùng” (như trong câu 16 của Bộ 450 câu hỏi). Đáp án 2 đúng, vì giống y như trong luật.',1,1,1,1),
(15,1,'Khái niệm: "Phương tiện giao thông thô sơ đường bộ" được hiểu thế nào là đúng?','Theo như Điều 3.19 của Luật GTĐB 2008, thì:“Phương tiện giao thông thô sơ đường bộ (sau đây gọi là xe thô sơ) gồm xe đạp (kể cả xe đạp máy), xe xích lô, xe lăn dùng cho người khuyết tật, xe súc vật kéo và các loại xe tương tự.”Đáp án 2 và 3 đều sai, vì liệt kê cả xe cơ giới như “ô tô, máy kéo…”.Đáp án 1 đúng vì giống trong luật: chỉ toàn những thứ chạy bằng cơm (hay cỏ), uống bằng nước thôi.',0,1,1,1),
(16,1,'“Phương tiện tham gia giao thông đường bộ” gồm những loại nào?','Điều 3.21 Luật GTĐB 2008 nêu rõ:“Phương tiện tham gia giao thông đường bộ gồm phương tiện giao thông đường bộ và xe máy chuyên dùng.”Như vậy đáp án đúng gồm cả 1 và 2. Lưu ý câu này dễ nhầm với câu 13, chỉ khác chữ “tham gia”, nên bao gồm cả “xe máy chuyên dùng”.',1,1,1,1),
(17,1,'“Người tham gia giao thông đường bộ” gồm những đối tượng nào?','Điều 3.22 Luật GTĐB 2008 giải thích rằng:"Người tham gia giao thông gồm người điều khiển, người sử dụng phương tiện tham gia giao thông đường bộ; người điều khiển, dẫn dắt súc vật; người đi bộ trên đường bộ.”Theo nội dung luật nêu trên, thì đáp án đúng gồm cả 1 và 2. Nghĩa là bất cứ ai điều khiển hoặc sử dụng phương tiện, hoặc đi bộ, hay dẫn dắt súc vật đi trên đường thì đều “tham gia” vào giao thông đường bộ.',1,1,1,1),
(18,1,'“Người điều khiển phương tiện tham gia giao thông” gồm những đối tượng nào?','Để ý từ “tham gia”, bạn sẽ nhớ ra câu hỏi số 16 (trong Bộ 450 câu) về “phương tiện tham gia giao thông đường bộ” sẽ gồm 3 loại: xe cơ giới, xe thô sơ, và xe máy chuyên dùng. Như vậy người điều khiển phương tiện tham gia giao thông đương nhiên gồm người điều khiển 3 loại phương tiện trên.Do đó, đáp án đúng sẽ là cả 1 và 2.',1,1,1,1),
(19,1,'Khái niệm "người điều khiển giao thông" được hiểu như thế nào là đúng?','Căn cứ theo Điều 3.23 Luật GTĐB 2008:“Người điều khiển giao thông là cảnh sát giao thông; người được giao nhiệm vụ hướng dẫn giao thông tại nơi thi công, nơi ùn tắc giao thông, ở bến phà, tại cầu đường bộ đi chung với đường sắt.”Như vậy thì đáp án 1 sai, vì “người điều khiển phương tiện” không phải là người điều khiển giao thông. Bạn lái xe đi đường thì phải theo sự hướng dẫn của “người điều khiển giao thông” như cảnh sát. Đáp án 4 cũng sai vì liệt kê tất cả các phương án trả lời.Đáp án đúng là 2 và 3.',0,1,1,1),
(20,1,'Khái niệm "dừng xe" được hiểu như thế nào là đúng quy tắc giao thông?','Theo Điều 18.1 Luật GTĐB 2008:“Dừng xe là trạng thái đứng yên tạm thời của phương tiện giao thông trong một khoảng thời gian cần thiết đủ để cho người lên, xuống phương tiện, xếp dỡ hàng hóa hoặc thực hiện công việc khác.”Đáp án 1 sai do thiếu chữ “tạm thời” và “cần thiết”. Đáp án 2 đúng.',0,1,1,1),
(21,1,'Khái niệm "đỗ xe" được hiểu như thế nào là đúng quy tắc giao thông?','Điều 18.2 Luật GTĐB 2008 nêu rõ:“Đỗ xe là trạng thái đứng yên của phương tiện giao thông không giới hạn thời gian.”Như vậy đáp án 1 sai vì “có giới hạn trong một khoảng thời gian”. Đáp án 2 đúng, vì giống như trong luật.',0,1,1,1),
(22,2,'Các hành vi nào ghi dưới đây bị nghiêm cấm?','Các hành vi “phá hoại” trong cả 2 đáp án đều bị cấm. Câu này thực ra chỉ cần suy luận thông thường cũng có thể làm được: đương nhiên “phá hoại” là bị cấm rồi.Theo Luật GTĐB 2008 - Điều 8.1 thì các hành vi bị nghiêm cấm có:“Phá hoại đường, cầu, hầm, bến phà đường bộ, đèn tín hiệu, cọc tiêu, biển báo hiệu, gương cầu, dải phân cách, hệ thống thoát nước và các công trình, thiết bị khác thuộc kết cấu hạ tầng giao thông đường bộ.”',1,1,1,1),
(23,2,'Hành vi đưa xe cơ giới, xe máy chuyên dùng không bảo đảm tiêu chuẩn an toàn kỹ thuật và bảo vệ môi trường tham gia giao thông đường bộ có bị nghiêm cấm hay không?','Hành vi đưa xe cơ giới, xe máy chuyên dùng không bảo đảm tiêu chuẩn vào tham gia giao thông là bị cấm. Nội dung này quy định rõ trong Điều 8.4 - Luật GTĐB 2008.Do đó, đáp án 2 đúng. ',0,1,1,1),
(24,2,'Những hành vi nào ghi ở dưới đây bị nghiêm cấm?','Theo cách hiểu thông thường chúng ta cũng biết: hành vi đua xe, lạng lách đánh võng... là bị cấm. Trong Luật GTĐB 2008, Điều 8.6 cũng khẳng định điều này.Do đó cả 2 đáp án 1 và 2 đều đúng.',1,1,1,1),
(25,2,'Người điều khiển phương tiện tham gia giao thông đường bộ mà bên trong cơ thể có chất ma túy có bị nghiêm cấm không?','Trong người có chất ma túy là bị cấm lái xe, đó là quy định tại điều 8.7 Luật GTĐB 2008. Đáp án 1 đúng, đáp án 2 sai.',1,1,1,1),
(26,2,'Người điều khiển xe ô tô, máy kéo, xe máy chuyên dùng trên đường mà trong máu hoặc hơi thở có nồng độ cồn vượt quá bao nhiêu thì bị cấm?','Với xe ô tô, cứ có cồn trong người là bị cấm lái, dù nồng độ là bao nhiêu. Đó là quy định trong Luật GTĐB 2008 - Điều 8.8.Bạn lưu ý phân biệt với đi xe máy, luật cho phép nồng độ cồn ở mức thấp - đủ an toàn.Do đó, chỉ đáp án 2 đúng. Đáp án 1 và 3 sai, vì cho phép có nồng độ cồn trong người khi lái xe ô tô, máy kéo, xe máy chuyên dùng.',1,1,1,1),
(27,2,'Người điều khiển xe mô tô, xe gắn máy trên đường mà trong máu hoặc hơi thở có nồng độ cồn vượt quá bao nhiêu thì bị cấm?','Với ô tô thì nồng độ cồn trong người dù ít cũng bị cấm. Nhưng với xe máy thì trong người có “chút chút” hơi men vẫn được lái xe. Nhưng chút chút là bao nhiêu?Luật GTĐB 2008 - Điều 8.8 quy định ngưỡng cho phép là 50 miligam/100 mililít máu. Vậy đáp án 1 đúng, đáp án 2 và 3 đều sai.',1,1,1,1),
(28,2,'Người điều khiển xe mô tô, xe gắn máy trên đường mà khí thở có nồng độ cồn vượt quá bao nhiêu thì bị cấm?','Với ô tô thì nồng độ cồn trong người dù ít cũng bị cấm. Nhưng với xe máy thì nếu bạn chỉ uống chút xíu vẫn được lái xe, nhưng mức bao nhiêu là đúng luật?Luật GTĐB 2008 - Điều 8.8 cấm người đi xe máy nếu nồng độ cồn vượt quá 0,25 miligam/1 lít khí thở.Vậy đáp án 1 đúng, đáp án 2 và 3 đều sai.',1,1,1,1),
(29,2,'Hành vi giao xe cơ giới, xe máy chuyên dùng cho người không đủ điều kiện để điều khiển xe tham gia giao thông có bị nghiêm cấm hay không?','Rõ ràng việc giao xe cho người không đủ điều kiện để lái, chẳng hạn chưa có bằng lái, thì đương nhiên bị cấm. Luật GTĐB 2008 - Điều 8.10 quy định về điều này.Đáp án 2 đúng, đáp án 1 và 3 sai.',1,1,1,1),
(30,2,'Hành vi điều khiển xe cơ giới chạy quá tốc độ quy định, giành đường, vượt ẩu có bị nghiêm cấm hay không?','Chạy quá tốc độ, giành đường vượt ẩu… là những hành vi bị cấm vì gây mất an toàn giao thông. Luật GTĐB 2008 - Điều 8.11 nêu rõ nội dung này.Do đó, đáp án 1 đúng, đáp án 2 và 3 sai.',1,1,1,1),
(31,2,'Những hành vi nào sau đây bị cấm?','Những hành vi trong cả 2 đáp án đều bị cấm, theo quy định trong Điều 8.12 của Luật GTĐB 2008:Cấm “bấm còi, rú ga liên tục; bấm còi trong thời gian từ 22 giờ đến 5 giờ, bấm còi hơi, sử dụng đèn chiếu xa trong đô thị và khu đông dân cư, trừ các xe được quyền ưu tiên đang đi làm nhiệm vụ.”Như vậy cả 2 đáp án đều đúng.',1,1,1,1),
(32,2,'Các hành vi nào sau đây bị nghiêm cấm?','Câu này khá dễ, vì đọc qua là biết cả 2 ý đều đúng: sử dụng thiết bị “không đúng thiết kế” hoặc “gây mất trật tự” tất nhiên là đều bị cấm. Theo luật thì đó là nội dung Điều 8.13 Luật GTĐB 2008.Vậy đáp án đúng là cả 1 và 2.',1,1,1,1),
(33,2,'Trong hoạt động vận tải đường bộ, các hành vi nào sau đây bị nghiêm cấm?','Nếu bạn để ý những từ như “hàng cấm lưu thông” và “vận chuyển trái phép”... thì biết những hành vi vận chuyển này đều bị cấm. Điều 8.14 Luật GTĐB 2008 quy định rõ:cấm “vận chuyển hàng cấm lưu thông, vận chuyển trái phép hoặc không thực hiện đầy đủ các quy định về vận chuyển hàng nguy hiểm, động vật hoang dã.”Như vậy, đáp án đúng là cả 1 và 2.',0,1,1,1),
(34,2,'Trong hoạt động vận tải khách, những hành vi nào ghi ở dưới đây bị cấm?','Những hành vi như “đe dọa, xúc phạm… hành khách” hay “trốn tránh phát hiện xe trở quá tải” rõ ràng là sai và bị cấm. Điều này cũng được quy định trong Điều 8.15 Luật GTĐB 2008.Và do đó, đáp án đúng là cả 1 và 2.',0,1,1,1),
(35,2,'Hành vi bỏ trốn sau khi gây tai nạn để trốn tránh trách nhiệm hoặc khi có điều kiện mà cố ý không cứu giúp người bị tai nạn giao thông có bị nghiêm cấm hay không?','Về mặt đạo đức mà nói, khi gây tai nạn rồi bỏ trốn hoặc cố ý không giúp nạn nhân là sai. Như vậy có thể suy ra những hành vi này sẽ bị cấm.Theo Điều 8.17 và 8.18 Luật GTĐB 2008, hành vi bị cấm: “Bỏ trốn sau khi gây tai nạn để trốn tránh trách nhiệm” và “Khi có điều kiện mà cố ý không cứu giúp người bị tai nạn giao thông”.Vậy đáp án đúng là cả 1 và 2.',1,1,1,1),
(36,2,'Khi xảy ra tai nạn giao thông, những hành vi nào ghi ở dưới đây bị nghiêm cấm?','Bằng suy luận thông thường, chúng ta cũng có thể thấy những hành vi bị cấm như: "Xâm phạm tính mạng, sức khoẻ, tài sản" hay "hành hung, đe doạ, xúi giục..." Trong Luật giao thông đường bộ 2008, Điều 8.19 và 8.20 cũng quy định rõ những nội dung này.Như vậy đáp án đúng là cả 1 và 2.',1,1,1,1),
(37,2,'Những hành vi nào sau đây ghi ở dưới đây bị nghiêm cấm?','Điều 8.3 Luật giao thông đường bộ 2008: cấm “sử dụng lòng đường, lề đường, hè phố trái phép.”Như vậy đáp án 2 và 3 là đúng. Đáp án 1 sai vì luật không cấm “sử dụng hè phố để đi bộ”.',0,1,1,1),
(38,2,'Việc sản xuất, mua bán, sử dụng biển số xe cơ giới, xe máy chuyên dùng được quy định như thế nào trong Luật Giao thông đường bộ?','Điều 8.22 Luật GTĐB 2008 cấm “sản xuất, sử dụng trái phép hoặc mua, bán biển số xe cơ giới, xe máy chuyên dùng.”Như vậy đáp án 3 là đúng. Đáp án 1 và 2 là sai, vì có từ “được phép sử dụng” và “cho phép sử dụng”.',1,1,1,1),
(39,2,'Người lái xe không được vượt xe khác khi gặp trường hợp nào ghi ở dưới đây?','Đáp án đúng của câu này là cả 1 và 2, vì các xe không được vượt xe khác trong những điều kiện không thuận lợi quy định tại Điều 14.5 Luật GTĐB 2008:+Trên cầu hẹp có một làn xe;+Nơi đường giao nhau, đường bộ giao nhau cùng mức với đường sắt;+Khi điều kiện thời tiết hoặc đường không bảo đảm an toàn cho việc vượt;+Xe được quyền ưu tiên đang phát tín hiệu ưu tiên đi làm nhiệm vụ.',1,1,1,1),
(40,2,'Ở những nơi nào không được quay đầu xe?','Đáp án 1 và 3 đúng, theo quy định tại Điều 15.4 Luật GTĐB 2008:“Không được quay đầu xe ở phần đường dành cho người đi bộ qua đường, trên cầu, đầu cầu, gầm cầu vượt, ngầm, trong hầm đường bộ, đường cao tốc, tại nơi đường bộ giao nhau cùng mức với đường sắt,đường hẹp, đường dốc, đoạn đường cong tầm nhìn bị che khuất.”Đáp án 2 sai, vì được phép quay đầu xe tại nơi đường bộ giao nhau cùng mức.',0,1,1,1),
(41,2,'Ở những nơi nào không được lùi xe?','Điều 16.2 Luật GTĐB 2008 quy định“Không được lùi xe ở khu vực cấm dừng, trên phần đường dành cho người đi bộ qua đường, nơi đường bộ giao nhau, đường bộ giao nhau cùng mức với đường sắt, nơi tầm nhìn bị che khuất, trong hầm đường bộ, đường cao tốc.”Như vậy, đáp án 2 và 3 đúng. Đáp án 1 sai, vì ở khu vực cho phép đỗ xe, thì cũng được phép lùi xe.',0,1,1,1),
(42,2,'Người điều khiển phương tiện giao thông trên đường phố có được dừng xe, đỗ xe trên đường xe điện, trên miệng cống thoát nước, miệng hầm của đường điện thoại, điện cao thế, chỗ dành riêng cho xe chữa cháy lấy nước hay không?','“Không được dừng xe, đỗ xe trên đường xe điện,trên miệng cống thoát nước, miệng hầm của đường điện thoại, điện cao thế, chỗ dành riêng cho xe chữa cháy lấy nước. Không được để phương tiện giao thông ở lòng đường, hè phố trái quy định.”Như vậy, không được dừng hoặc đỗ xe trong những trường hợp câu hỏi nêu. Do đó, đáp án 1, 3 và 4 đều sai. Đáp án 2 đúng.',0,1,1,1),
(43,2,'Khi điều khiển xe trên đường cao tốc, những việc nào không cho phép?','Điều 26.1c-d Luật GTĐB 2008 quy định“Không được cho xe chạy ở làn dừng xe khẩn cấp và phần lề đường; Không được cho xe chạy quá tốc độ tối đa và dưới tốc độ tối thiểu ghi trên biển báo hiệu, sơn kẻ trên mặt đường.”Như vậy, đáp án 1 đúng.Xe phải chạy trên tốc độ tối thiểu, dưới tốc độ tối đa nên hành vi này không bị cấm (được phép), do đó đáp án 2 sai.Cũng theo điều 26.3, trên đường cao tốc thì “Chỉ được dừng xe, đỗ xe ở nơi quy định”, nghĩa là hành vi này được phép. Do đó, đáp án 3 sai.',0,1,1,1),
(44,2,'Khi xe kéo xe và xe kéo rơ moóc, những hành vi nào không được phép?','Điều 29.3 Luật GTĐB 2008 quy định xe kéo rơ moóc, xe kéo sơ mi rơ moóc không được kéo thêm rơ moóc hoặc xe khác; và không được chở người trên xe được kéo.Do đó, đáp án đúng là cả 1 và 2.Luật không cấm “chở hàng trên rơ moóc hoặc sơ mi rơ moóc”, do đó đáp án 3 sai',0,1,1,1),
(45,2,'Người điều khiển xe mô tô hai bánh, ba bánh, xe gắn máy có được phép sử dụng, xe để kéo đẩy các xe khác, vật khác khi tham gia giao thông không?','null',1,1,1,1),
(46,2,'Người ngồi trên xe mô tô hai bánh, ba bánh, xe gắn máy khi tham gia giao thông không được thực hiện những hành vi nào dưới đây?','Theo điều 30.4 Luật GTĐB 2008, người ngồi trên xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy khi tham gia giao thông không được thực hiện các hành vi như: sử dụng ô; bám, kéo hoặc đẩy các phương tiện khác; đứng trên yên, giá đèo hàng hoặc ngồi trên tay lái. Như vậy đáp án đúng là cả 2 và 3.Ngoài ra, khi đi xe mô tô 2-3 bánh thì người lái bắt buộc phải đội mũ bảo hiểm, do đó đáp án 1 sai.',1,1,1,1),
(47,2,'Người ngồi trên xe mô tô hai bánh, ba bánh, xe gắn máy khi tham gia giao thông có được mang, vác vật cồng kềnh hay không?','Theo điều 30.4 Luật GTĐB 2008, người ngồi trên xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy khi tham gia giao thông không được “Mang, vác vật cồng kềnh”.Do đó, đáp án 2 đúng, những đáp án còn lại sai. ',1,1,1,1),
(48,2,'Khi điều khiển xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy, những hành vi nào không được phép?','Theo điều 30.4 Luật GTĐB 2008, người ngồi trên xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy khi tham gia giao thông không được “buông cả 2 tay” hoặc “kéo đẩy xe khác”.Do vậy, đáp án 1 và 2 đúng.Còn đi xe máy bắt buộc phải chạy đúng tốc độ quy định và chấp hành đúng quy tắc giao thông đường bộ. Vì thế, đáp án 3 sai.',1,1,1,1),
(49,2,'Việc họp chợ, tụ tập đông người trái phép, thả rông súc vật trên đường bộ có được phép hay không?','Việc họp chợ, tụ tập đông người trái phép, thả rông súc vật trên đường bộ là những hành vi gây cản trở và mất an toàn giao thông, và trái với quy định của Luật GTĐB 2008 - Điều 35.2.Do đó đáp án 1 đúng, đáp án 2 và 3 là sai.',0,1,1,1),
(50,2,'Bảo đảm trật tự an toàn giao thông đường bộ là trách nhiệm của ai?','Theo quy định trong Luật GTĐB 2008 - Điều 4.4 thì“Bảo đảm trật tự, an toàn giao thông đường bộ là trách nhiệm của cơ quan, tổ chức, cá nhân”Do đó đáp án 2 đúng, vì nó giống y như luật. Còn đáp án 1 và 3 là sai vì thiếu ý.Ghi chú: Thực ra, đáp án câu hỏi này chưa được rõ ràng. Nội dung đáp án 2 đã bao gồm cả đáp án 1 và 3: ngành Giao thông vận tải, ngành công an, hay CSGT thì cũng đều nằm trong “cơ quan, tổ chức, cá nhân”. Như vậy thì đáp án 1 và 3 cũng không sai, chỉ không đủ ý. Do đó, nhiều người sẽ chọn nhầm cả 3 đáp án đều đúng.',1,1,1,1),
(51,2,'Người tham gia giao thông phải chấp hành quy tắc nào dưới đây?','Đáp án 1 sai vì có cụm từ “phải đi bên trái của đường đi” - phải đi bên phải mới đúng chứ. Từ nhỏ đã học bài hát “em đi học em đi đường bên phải” là gì.Đáp án 2 đúng, vì nó giống y như trong Điều 9.1 Luật GTĐB 2008.',0,1,1,1),
(52,2,'Tại nơi đường giao nhau, khi đèn giao thông có tín hiệu vàng, người điều khiển phương tiện phải thực hiện như thế nào?','Khi gặp đèn vàng là “phải dừng lại trước vạch dừng”, chứ cho xe nhanh chóng vượt qua là sai luật (Điều 10.3.c Luật GTĐB 2008). Nhiều người thực tế vẫn hiểu nhầm đèn vàng là chuẩn bị dừng nên cho xe chạy cố.Đáp án 1 đúng, 2 đáp án còn lại sai.',1,1,1,1),
(53,2,'Biển báo hiệu đường bộ gồm những nhóm nào, ý nghĩa của từng nhóm?','Biển báo hiệu đường bộ gồm 5 nhóm (Điều 10.4 Luật GTĐB 2008): biển báo cấm, biển báo nguy hiểm, biển hiệu lệnh, biển chỉ dẫn, biển phụ. Do đó đáp án đúng là cả 1 và 2. Bạn có thể tham khảo thêm trong loạt bài viết về Biển báo giao thông.',1,1,1,1),
(54,2,'Tại nơi đường giao nhau, khi người điều khiển giao thông ra hiệu lệnh bằng hai tay hoặc một tay giang ngang để báo hiểu thì người tham gia giao thông phải đi như thế nào là đúng qui tắc giao thông?','Khi người điều khiển giao thông giang ngang tay thì nghĩa là hai bên được đi, phía trước sau dừng lại. Hiệu lệnh này được quy định tại Điều 10.2.b Luật GTĐB 2008.Đáp án đúng là 1. Đáp án 2 sai.',1,1,1,1),
(55,2,'Tại nơi đường giao nhau, khi người điều khiển giao thông ra hiệu lệnh giơ tay thẳng đứng để báo hiểu thì người tham gia giao thông phải đi như thế nào là đúng qui tắc giao thông?','Người điều khiển giao thông giơ tay thẳng đứng để báo hiệu “...các hướng dừng lại”, quy định tại Điều 10.2.a Luật GTĐB 2008.Do đó đáp án 2 đúng, đáp án 1 và 3 sai.',1,1,1,1),
(56,2,'Tại nơi đường giao nhau, khi người điều khiển giao thông ra hiệu lệnh bằng tay phải giơ về phía trước để báo hiệu thì người tham gia giao thông phải đi như thế nào?','Luật GTĐB 2008 Điều 10.2.c quy định: Khi người điều khiển giao thông giơ tay phải về phía trước, thì...“người tham gia giao thông ở phía sau và bên phải người điều khiến phải dừng lại; người ở phía trước người điều khiển được rẽ phải; người ở phía bên trái người điều khiển giao thông được đi tất cả các hướng; ngườí đi bộ qua đường phải đi sau lưng người điều khiển giao thông.”Đáp án 2 đúng. Đáp án 1 sai vì cho phép “phía trước… rẽ trái”.',1,1,1,1),
(57,2,'Trên đường giao thông, khi hiệu lệnh của người điều khiển giao thông trái với hiệu lệnh của đèn hoặc biển báo hiệu thì người tham gia giao thông chấp hành theo hiệu lệnh nào?','Theo Điều 11 Luật GTĐB 2008, người tham gia giao thông phải tuân thủ theo thứ tự ưu tiên như sau: Hiệu lệnh người điều khiển, Đèn giao thông, Biển báo hiệu & Vạch kẻ đường.Do đó, đáp án 1 đúng. Đáp án 2 và 3 đều sai.',1,1,1,1),
(58,2,'Tại nơi có biển báo hiệu cố định lại có biển báo hiệu tạm thời thì người tham gia giao thông phải chấp hành hiệu lệnh của báo hiệu nào?','Theo Điều 11.3 Luật GTĐB 2008, thì biển báo hiệu tạm thời được ưu tiên hơn biển báo cố định.Do đó đáp án 2 đúng, đáp án 1 sai.',1,1,1,1),
(59,2,'Trên đường có nhiều làn đường cho xe đi cùng chiều được phân biệt bằng vạch kẻ phân làn đường, người điều khiển phải cho xe đi như thế nào?','Điều 13.1 Luật GTĐB 2008 quy định người đi xe phải “đi trong một làn đường”, “chỉ được chuyển làn đường ở những nơi cho phép” và “phải có tín hiệu báo trước”. Do đó đáp án 2 đúng.Đáp án 1 sai vì “cho xe đi trên bất kỳ làn đường nào”. Đáp án 3 sai vì thiếu ý "chuyển làn đường ở những nơi cho phép".',1,1,1,1),
(60,2,'Trên đường một chiều có vạch kẻ phân làn đường, xe thô sơ và xe cơ giới phải đi như thế nào là đúng quy tắc giao thông?','Điều 13.1 Luật GTĐB 2008 quy định “Xe thô sơ phải đi trên làn đường bên phải trong cùng; xe cơ giới, xe máy chuyên dùng đi trên làn đường bên trái.”Do đó, đáp án 2 đúng, đáp án còn lại sai.',1,1,1,1),
(61,2,'Trên đường có nhiều làn đường, người điều khiển phương tiện tham gia giao thông sử dụng làn đường như thế nào là đúng?','Điều 13 Luật GTĐB 2008 quy định với đường có nhiều làn, xe phải “đi trong một làn đường và chỉ đựợc chuyển làn đường ở những nơi cho phép; khi chuyển làm đường phải có tín hiệu báo trước và đảm bảo an toàn.”Ngoài ra, còn có nội dung: xe thô sơ, xe tốc độ thấp - đi làn bên phải; xe cơ giới, xe máy chuyên dùng - đi làn bên trái. Kiểu đi chậm thì nép vào lề, đi nhanh thì vượt sang trái ấy.Do đó, đáp án 1 và 3 đều đúng. Đáp án 2 sai vì nói: xe đi chậm - đi về bên trái.',1,1,1,1),
(62,2,'Khi điều khiển xe chạy trên đoạn đường vòng, đầu dốc và các vị trí có tầm nhìn hạn chế người lái xe muốn vượt xe khác thì phải xử lý như thế nào?','Câu này có thể trả lời theo suy luận thông thường: đoạn đường nào nguy hiểm thì không được vượt xe khác. Điều 14.5.c Luật GTĐB 2008 cũng quy định rõ: không được vượt xe tại đoạn “đường vòng, đầu dốc và các vị trí có tầm nhìn hạn chế”.Như vậy đáp án đúng là 2. Đáp án 1 và 3 cho vượt nên đều sai.',0,1,1,1),
(63,2,'Khi vượt xe khác người lái xe phải thực hiện như thế nào?','Rõ ràng khi vượt xe khác phải làm báo hiệu bằng còi hoặc đèn (hoặc cả 2), và chỉ được vượt xe khác khi thấy không có chướng ngại vật. Vậy đáp án đúng là cả 1 và 2.Có thể tham khảo thêm tại Điều 14 khoản 1 và 2 - Luật GTĐB 2008, và bài viết Vượt xe thế nào cho đúng luật và an toàn?',0,1,1,1),
(64,2,'Khi điều khiển xe trên đường biết có xe sau xin vượt nếu đủ điều kện an toàn người lái xe phải làm gì?','Khi có xe khác muốn vượt, mà “tăng tốc độ” hoặc “tránh về bên trái” là sai, vì làm vậy thì sao mà người ta vượt được. Do đó đáp án 1 và 3 là sai.Đáp án đúng là 2. Khi đó, bạn cần phải giảm tốc độ + tránh về bên phải + không gây cản trở xe kia vượt.Tham khảo thêm tại Điều 14.3 Luật GTĐB 2008',0,1,1,1),
(65,2,'Xe sau có thể vượt lên bên phải xe khác đang chạy phía trước trong trường hợp nào?','Điều 14.4 Luật GTĐB 2008 cho phép xe vượt bên phải trong 3 trường hợp: Xe trước rẽ trái hoặc tín hiệu rẽ trái, Xe điện đang chạy giữa đường, Xe chuyên dùng đang làm việc, và không thể vượt trái được.Như vậy đáp án 1 và 2 đều đúng.Đáp án 3 sai vì “đủ khoảng trống” chưa chắc đã đủ an toàn để vượt, và Luật không cho phép vượt phải trong trường hợp này.',1,1,1,1),
(66,2,'Trong khu dân cư, ở nơi nào cho phép người lái xe, người điểu khiển xe máy chuyên dùng được quay đầu xe?','Trong khu dân cư, thì chỉ được quay đầu xe ở nơi đường giao nhau và nơi có biển báo cho phép quay đầu xe. Điều này được quy định tại Điều 15.3 Luật GTĐB 2008. Do đó câu 1 đúng.Câu 2 và 3 sai, vì ở nơi có đường rộng để cho các loại xe chạy một chiều, thì có thể vẫn không đủ rộng và an toàn cho xe quay đầu, và Luật cũng không cho phép hành vi này.',0,1,1,1),
(67,2,'Người lái xe phải làm gì khi quay đầu xe trên cầu, gầm cầu vượt, đường ngầm hay khu vực đường bộ giao nhau cùng mức với đường sắt?','Chúng ta đều biết rằng tại những nơi như: trên cầu, gầm cầu vượt, đường ngầm… rủi ro va chạm rất cao. Vì thế, tại những nơi như vậy, xe không được quay đầu. Điều 15.4 Luật GTĐB 2008 cũng quy định nội dung này. Đáp án 1 đúng.Đáp án 2 và 3 đều sai, vì cho phép quay đầu tại những nơi nguy hiểm như trên đã nêu.',1,1,1,1),
(68,2,'Khi muốn chuyển hướng, người lái xe phải thực hiện như thế nào?','Khi muốn chuyển hướng xe mà “tăng tốc độ” thì gây nguy hiểm cho mình và phương tiện xung quanh. Do đó đáp án 1 sai.Thực tế, khi đó phải “giảm tốc độ” +“tín hiệu báo rẽ”, đồng thời "nhường đường cho người đi bộ, xe đạp trên phần đường của họ" + "nhường xe ngược chiều". Đáp án đúng là cả 2 và 3.Tham khảo nội dung này tại Điều 15 khoản 1 và 2 - Luật GTĐB 2008.',1,1,1,1),
(69,2,'Khi lùi xe, người lái xe phải làm gì để bảo đảm an toàn?','Khi lùi xe rõ ràng cần phải “quan sát phía sau”, do đó đáp án 1 sai. Cũng không được lùi xe ở “nơi đường giao nhau” vì gây mất an toàn, nên đáp án 2 sai.Luật GTĐB 2008 Điều 16 quy định khi lùi xe “phải quan sát phía sau, có tín hiệu cần thiết và chỉ khi nào thấy không nguy hiểm mới được lùi”. Do đó, đáp án 3 đúng.',0,1,1,1),
(70,2,'Khi tránh xe đi ngược chiều, các xe phải nhương đường như thế nào là đúng?','Quy tắc nhường đường được quy định tại Điều 17.2 Luật GTĐB 2008, theo đó cần phải nhường đường tại "nơi đường hẹp" + "có chướng ngại vật" và "xe xuống dốc nhường xe lên dốc". Như vậy, đáp án 1 và 2 đúng.Đáp án 3 sai, vì “xe lên dốc phải nhường đường” thì dễ mất an toàn do đang phải duy trì ga & số, nếu nhường đường có thể cần ngắt côn về số, giảm tốc độ, hoặc dừng xe, sẽ dễ bị chết máy và trôi dốc nguy hiểm.',1,1,1,1),
(71,2,'Ban đêm xe cơ giới đi ngược chiều gặp nhau, đèn chiếu sáng phải được sử dụng như thế nào?','Xe đi ngược chiều cần chuyển đèn chiếu xa (pha) sang chiếu gần (cốt) để giảm lóa cho tài xế. Do đó đáp án 2 đúng.Đáp 1 sai, vì nếu chuyển đèn từ chiếu gần sang xa, sẽ gây lóa mắt và mất an toàn.Tham khảo quy định tại Điều 17.3 Luật GTĐB 2008.',1,1,1,1),
(72,2,'Trên đường không phân chia thành hai chiều xe chạy riêng biệt, hai xe đi ngược chiều tránh nhau như thế nào?','Khi tránh nhau, cần giảm tốc độ và nép về bên phải mới an toàn. Do đó, đáp án 3 đúng.Đáp án 1 sai, vì tránh xe ngược chiều mà “tăng tốc” rất nguy hiểm. Đáp án 2 sai vì nếu “tránh về bên trái” là dễ đâm trực diện vào xe đi ngược chiều, còn gì là an toàn nữa.Bạn có thể đọc quy định tại Điều 17.1 Luật GTĐB 2008.',0,1,1,1),
(73,2,'Bên trái đường một chiều, người lái xe có được dừng, đỗ xe hay không?','Phía bên trái đường 1 chiều thường là cho xe cơ giới di chuyển với tốc độ cao. Đậu hoặc đỗ xe phía bên đó sẽ gây cản trở giao thông và mất an toàn. Điều 18.4.a Luật GTĐB 2008 cấm hành vi đậu, đỗ xe như vậy.Do đó, đáp án 1 và 2 đều sai. Đáp án 3 đúng.',0,1,1,1),
(74,2,'Người lái xe được dừng, đỗ xe trong các trường hợp nào sau đây?','Điều 18.4. Mục h và i - Luật giao thông đường bộ 2008 cấm dừng đỗ xe:+Trước cổng và trong phạm vi 5 mét hai bên cổng trụ sở cơ quan, tổ chức.+Tại nơi phần đường có bề rộng chỉ đủ cho một làn xeNhư vậy, đáp án 2 và 3 sai vì bị Luật cấm.Đáp án 1 đúng, vì đỗ trên đường “đủ rộng cho 2 làn xe” và “ngoài phạm vi 5 mét” của cơ quan tổ chức.',0,1,1,1),
(75,2,'Người điều khiển phương tiện khi muốn dừng hoặc đỗ xe trên đường bộ phải thực hiện như thế nào?','Dừng đỗ xe phải đúng nơi quy định, và phải có tín hiệu báo cho người khác biết. Do đó, đáp án đúng là cả 1 và 2.Tham khảo chi tiết tại Điều 18.3 mục a và b Luật giao thông đường bộ 2008.',0,1,1,1),
(76,2,'Khi dừng xe, đỗ xe ở nơi được phép trên đường phố, người điều khiển phương tiện phải tuân theo những quy định nào dưới đây?','Đáp án câu này dài, khó nhớ. Nhưng có mấy từ khóa cần nhớ để trả lời cho đúng.Câu 1 sai vì cho xe dừng đỗ “bên trái”, cách lề đường không quá “0,3m”, cách xe bên kia đường tối thiểu “15m”.Đáp án 2 đúng vì cho xe dừng đỗ “bên phải”, cách lề đường không quá “0,25m”, cách xe bên kia đường tối thiểu “20m”.Chi tiết đáp án câu này được quy định tại Điều 19.1 Luật GTĐB 2008.',0,1,1,1),
(77,2,'Khi xếp hàng hóa vượt phía trước và phía sau của xe ô tô chở hàng tham gia giao thông, ban ngày và ban đêm phải có cờ, đèn báo hiệu như thế nào là đúng quy định?','Cờ và đèn báo hiệu đều “màu đỏ” - màu cảnh báo nguy hiểm. Thế nên đáp án 1 sai vì “cờ báo hiệu màu xanh”. Đáp án 2 đúng. Xem chi tiết trong Điều 20.2 Luật GTĐB 2008.',0,1,1,1),
(78,2,'Xe vận tải hàng hóa có thùng cố định, bảo đảm an toàn giao thông được phép chở người trong những trường hợp nào?','Xe tải có thùng chỉ được chở người trong những trường hợp khẩn cấp (kiểu như: phòng chống thiên tai, tai nạn cấp cứu…) hoặc đặc biệt (duy tu bảo dưỡng đường, người học lái xe…). Quy định chi tiết tại Điều 21.1 Luật GTĐB 2008. Do đó đáp án đúng là cả 1 và 2.Đáp án 3 sai, vì chở người tham gia giao thông bình thường là không được phép.',0,1,1,1),
(79,2,'Những loại xe nào khi đi làm nhiệm vụ khẩn cấp có tín hiệu còi, cờ, đèn theo quy định không bị hạn chế tốc độ, có thể đi vào đường ngược chiều, các đường khác có thể đi được và chỉ phải tuân theo chỉ dẫn của người điều khiển giao thông?','Theo Điều 22 khoản 1 và 2 Luật GTĐB 2008, “đoàn xe đang diễu hành có tổ chức” và “đoàn xe tang” không thuộc diện “không bị hạn chế tốc độ, có thể đi vào đường ngược chiều”. Do đó, Đáp án 1 và 3 đều sai.Đáp án 2 thực ra cũng chưa đủ ý, vì các loại xe như: chữa cháy, quân sự, công an… phải “đang làm nhiệm vụ” thì mới được ưu tiên như trong câu hỏi. Như vậy, có vẻ nội dung đề cũng chưa hoàn toàn chuẩn xác. Dù sao đáp án 2 cũng đầy đủ ý hơn cả, và sách in cũng chọn đáp án 2 đúng. Thế nên các bạn cứ thế mà theo là được.',0,1,1,1),
(80,2,'Khi có tín hiệu của xe ưu tiên, người tham gia giao thông phải làm gì?','Khi gặp xe ưu tiên, phải "giảm tốc độ" và tránh vào sát "lề đường bên phải". Đó là quy định trong Điều 22.3 Luật GTĐB 2008.Do đó, đáp án 1 sai vì tránh sát lề đường “bên trái”, đáp án 2 sai vì “tăng tốc độ”.  Đáp án 3 đúng.',1,1,1,1),
(81,2,'Xe nào được quyền đi trước khi giao nhau?','Thứ tự các xe ưu tiên quy định tại Điều 22.1 Luật GTĐB 2008 như sau:+Xe chữa cháy đi làm nhiệm vụ+Xe quân sự, xe công an đi làm nhiệm vụ, đoàn xe có cảnh sát dẫn đường+Xe cứu thương đang đi cấp cứu, v.v...Như vậy xe chữa cháy được ưu tiên cao nhất, và đáp án 3 đúng. Các đáp án còn lại đều sai.',0,1,1,1),
(82,2,'Khi qua phà, qua cầu phao, người lái xe phải làm gì để bảo đảm an toàn?','Việc qua phà, qua cầu phao được quy định tại Điều 23 Luật GTĐB 2008. Theo đó, người và xe phải "xếp hàng trật tự, đúng nơi quy định"; "mọi người phải xuống xe, trừ người điều khiển xe cơ giới, người bệnh, người già yếu và người khuyết tật"; khi xuống phà thì "xe cơ giới xuống trước, xe thô sơ và người đi bộ xuống sau".Như vậy, câu 3 sai vì “xe cơ giới phải xuống phà sau, xe thô sơ, người đi bộ xuống phà trước”.Đáp án 1 và 2 đúng, vì giống như trong Luật quy định.',0,1,1,1),
(83,2,'Tại nơi đường giao nhau có báo hiệu đi theo vòng xuyến, người điều khiển phương tiện phải nhường đường như thế nào?','Tại vòng xuyến thì nhường cho xe từ bên trái tới. Ở nơi giao cắt khác thì nhường cho xe từ bên phải tới (Điều 24.2 Luật GTĐB năm 2008).Theo đó, đáp án 3 đúng. Đáp án 1 và 2 đều sai.',1,1,1,1),
(84,2,'Tại nơi đường giao nhau, người lái xe đang đi trên đường không ưu tiên phải nhường đường như thế nào?','Xe trên đường không ưu tiên phải nhường đường cho xe trên đường ưu tiên, bất kể từ hướng nào tới (Điều 24.3 Luật giao thông đường bộ năm 2008).Đáp án 3 đúng. Đáp án 1 và 2 đều sai.',1,1,1,1),
(85,2,'Tại nơi đường giao nhau không có báo hiệu đi theo vòng xuyến, người điều khiển phương tiện phải nhường đường như thế nào là đúng quy tắc giao thông?','Không phải vòng xuyến, thì nhường đường cho xe từ bên phải tới. Tại vòng xuyến thì nhường cho xe bên trái tới. Đó là quy định tại Điều 24.1 Luật GTĐB năm 2008.Như vậy thì đáp án 1 đúng. Đáp án 2 và 3 đều sai.',1,1,1,1),
(86,2,'Trên đoạn đường bộ giao nhau cùng mức với đường sắt, thì loại phương tiện nào được quyền ưu tiên đi trước?','Phương tiện đường sắt luôn được ưu tiên, vì nó nặng nề và khó dừng gấp. Đáp án 3 đúng, đáp án 1 và 2 đều sai. Tham khảo chi tiết trong Luật GTĐB 2008 - Điều 25.1.',1,1,1,1),
(87,2,'Tại nơi đường bộ giao nhau cùng mức với đường sắt chỉ có đèn báo hiệu hoặc chuông báo hiệu, khi đèn tín hiệu màu đỏ đã bật sáng hoặc có tiếng chuông báo hiệu, người tham gia giao thông phải dừng lại ngay và giữ khoảng cách tối thiểu bao nhiêu mét tính từ ray gần nhất?','Dừng tối thiểu cách 5 mét, theo quy định tại Điều 25.3 Luật GTĐB năm 2008.Đáp án 1 đúng. Các đáp án còn lại đều sai. ',1,1,1,1),
(88,2,'Người lái xe phải làm gì khi điều khiển xe vào đường cao tốc?','Điều 26 .1a Luật GTĐB 2008 quy định khi vào đường cao tốc thì cần:+tín hiệu xin vào;+nhường xe đang chạy trên đường;+phải chạy trên làn tăng tốc trước, nếu có.Như vậy đáp án đúng là cả 1 và 3.Đáp án 2 sai vì ý “tăng tốc độ” là không đúng theo văn bản Luật (tất nhiên ai cũng hiểu khi vào đường cao tốc thì phải tăng tốc độ lên trên mức tối thiểu).',1,1,1,1),
(89,2,'Người lái xe phải làm gì khi điều khiển xe ra khỏi đường cao tốc?','Xe đang chạy nhanh, muốn ra khỏi đường cao tốc thì phải “chuyển dần” sang làn đường bên phải. Nếu “chuyển ngay” sang làn đường bên phải, thì sẽ gây nguy hiểm. Tham khảo quy định tại Điều 26.1b Luật GTĐB năm 2008.Do đó, đáp án 1 đúng, đáp án 2 sai.',1,1,1,1),
(90,2,'Trên đường cao tốc, người lái xe phải dừng xe, đỗ xe như thế nào?','Việc dừng đỗ trên đường cao tốc rất dễ gây mất an toàn, vì các phương tiện đều di chuyển với tốc độ cao. Do đó Điều 26.3 Luật GTĐB 2008 cho phép chỉ được dừng, đỗ “ở đúng nơi quy định”.Như vậy câu 1 và 3 đều sai vì cho dừng, đỗ xe ở “nơi đường rộng”, chứ không có ý “đúng nơi quy định”.Đáp án 2 đúng, vì giống hệt trong Luật. ',0,1,1,1),
(91,2,'Những trường hợp nào ghi ở dưới đây không được đi vào đường cao tốc trừ người, phương tiện, thiết bị phục vụ cho việc quản lý, bảo trì đường cao tốc?','Câu này dễ bị nhầm, do đáp án hơi lủng củng câu chữ. Để trả lời, ta hay xem Điều 26.4 Luật GTĐB năm 2008 quy định như sau:“Người đi bộ, xe thô sơ, xe gắn máy, xe mô tô và máy kéo; xe máy chuyên dùng có tốc độ thiết kế nhỏ hơn 70 km/h không được đi vào đường cao tốc, trừ người, phương tiện, thiết bị phục vụ việc quản lý, bảo trì đường cao tốc”.Điều đó có nghĩa là đường cao tốc chỉ cho xe ô tô và xe máy chuyên dùng tốc độ trên 70km/h chạy.Đáp án 1 sai, vì cấm cả “ô tô”. Đáp án 2 sai vì cấm xe chuyên dùng tốc độ trên 70km/h.Đáp án 3 đúng, vì đường cao tốc cấm các phương tiện như điều luật nêu trên quy định, gồm:+Người đi bộ, xe thô sơ, +Xe gắn máy, xe mô tô, +Xe máy chuyên dùng có tốc độ thiết kế nhỏ hơn 70km/h.',1,1,1,1),
(92,2,'Người điều khiển phương tiện tham gia giao thông trong hầm đường bộ ngoài việc phải tuân thủ các quy tắc giao thông còn phải thực hiện những quy định nào dưới đây?','Điều 15.4 Luật GTĐB 2008 cấm quay đầu xe trong hầm đường bộ. Theo đó, câu 2 và 3 đều sai vì nói “được quay đầu xe”.Đáp án 1 đúng, vì tuân theo quy định tại Điều 27 Luật GTĐB 2008.',1,1,1,1),
(93,2,'Xe quá tải trọng, quá khổ giới hạn tham gia giao thông cần tuân thủ quy định nào ghi ở dưới đây?','Xe quá khổ quá tải “phải được cơ quan quản lý đường bộ có thẩm quyền cấp phép”, theo Điều 28.2 Luật GTĐB 2008.Phương án 1 đúng, cả phương án 2 và 3 đều sai.',0,1,1,1),
(94,2,'Việc nối kéo với xe được kéo trong trường hợp hệ thống hãm của xe được kéo không còn hiệu lực thì phải dùng cách nào?','Điều 29.1b Luật GTĐB 2008 quy định: muốn kéo xe hỏng hệ thống phanh, thì “phải dùng thanh nối cứng” mới đảm bảo an toàn.Do đó, đáp án 3 đúng, đáp án 1 và 2 sai.',0,1,1,1),
(95,2,'Xe kéo rơ moóc khi tham gia giao thông phải tuân thủ điều kiện nào ghi ở dưới đây?','Xe kéo rơ moóc phải có trọng lượng “lớn hơn” và hệ thống hãm của rơ moóc phải “có hiệu lực”. Đó là quy định tại Điều 29.2 Luật giao thông đường bộ 2008.Như vậy câu 1 đúng. Câu 2 sai vì “trọng lương tương đương” và thiếu “có hiệu lực”.',0,1,1,1),
(96,2,'Xe ô tô kéo xe khác thế nào là đúng quy tắc giao thông?','Câu này căn cứ theo Điều 29.1 Luật GTĐB 2008: xe ô tô chỉ được kéo “một xe”, nối kéo “phải đảm bảo chắc chắn”, và phải “nối bằng thành nối cứng” nếu xe được kéo không có hệ thống hãm.Theo đó, đáp án 1 và 2 đúng. Đáp án 3 sai, vì cho phép kéo thêm “nhiều xe khác”.',0,1,1,1),
(97,2,'Người ngồi trên xe mô tô 2 bánh, xe mô tô 3 bánh, xe gắn máy phải đội mũ bảo hiểm có cài quai đúng quy cách khi nào?','Người ngồi trên xe mô tô 2 bánh, xe mô tô 3 bánh, xe gắn máy phải đội mũ bảo hiểm có cài quai bất cứ khi nào “tham gia giao thông” (Điều 30.2 Luật GTĐB 2008). Như vậy đáp án 1 đúng.Đáp án 2 sai, vì xe mô tô không được đi vào đường cao tốc (theo Điều 26.4 Luật GTĐB 2008).',1,1,1,1),
(98,2,'Người điều khiển xe mô tô hai bánh, xe mô tô gắn máy được phép chở tối đa 2 người trong trường hợp nào?','Cả 2 đáp án đều đúng theo quy định của Luật GTĐB 2008 - Điều 30.1',1,1,1,1),
(99,2,'Người điều khiển xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy không được thực hiện những hành vi nào dưới đây?','Cả đáp án 1 và 2 đều đúng, theo Điều 30.3 Luật GTĐB 2008.Đáp án 3 sai, vì người đi mô tô, xe gắn máy được phép “Chở người bệnh đi cấp cứu; trẻ em dưới 14 tuổi, áp giải người có hành vi vi phạm pháp luật”. Thậm chí còn được chở 2 người thuộc diện đó, cũng theo Điều 30.2 nêu trên.',1,1,1,1),
(100,2,'Người điều khiển, người ngồi trên xe đạp máy phải đội mũ bảo hiểm có cài quai đúng quy cách khi nào?','Người đi xe đạp máy cũng phải đội mũ bảo hiểm khi tham gia giao thông (Điều 31.2 Luật GTĐB 2008). Đáp án 1 đúng. Đáp án 2 sai.',1,1,1,1),
(101,2,'Cơ quan nào quy định các đoạn đường cấm đi, đường một chiều, nơi cấm dừng, cấm đỗ, cấm quay đẩu xe, lắp đặt báo hiệu đường bộ thuộc địa phương nào quản lý?','Điều 37.1b và 37.2b Luật GTĐB 2008 quy định “Chủ tịch Ủy ban nhân dân cấp tỉnh chịu trách nhiệm tổ chức giao thông…” như: đường cấm đi, đường đi một chiều, nơi cấm dừng, cấm đỗ…Do đó, đáp án 2 đúng. Các đáp án còn lại đều sai',1,1,1,1),
(102,2,'Người điều khiển phương tiện và những người liên quan trực tiếp đến vụ tai nạn có trách nhiệm gì?','Người liên quan trực tiếp đến vụ tai nạn phải có trách nhiệm, thể hiện như: giữ nguyên hiện trường, cấp cứu người, có mặt tại nơi xảy ra vụ việc… Đó là quy định tại Điều 38.1 Luật GTĐB 2008.Như vậy, đáp án 3 sai vì cho phép “rời khỏi hiện trường”. Cả đáp án 1 và 2 đều đúng.',1,1,1,1),
(103,2,'Những người có mặt tại nơi xảy ra vụ tai nạn giao thông có trách nhiệm gì?','Điều 38.2 Luật GTĐB 2008 yêu cầu người có mặt tại nơi xảy ra tai nạn: bảo vệ hiện trường, giúp đỡ nạn nhân, báo tin cho cơ quan hữu quan… Vì vậy, cả 2 đáp án đều đúng.',1,1,1,1),
(104,2,'Người phát hiện công trình đường bộ hư hỏng, hoặc bị xâm hại, hành lang an toàn đường bộ bị lấn chiếm phải có trách nhiệm gì?','Điều 52.5 Luật GTĐB 2008 quy định: vừa phải báo cho cơ quan hữu quan, vừa phải báo hiệu cho người khác. Vậy đáp án đúng là cả 1 và 2.',0,1,1,1),
(105,2,'Khi điều khiển xe chạy trên đường, người lái xe phải mang theo những loại giấy tờ gì?','Câu này có thể trả lời bằng phương pháp loại trừ.Nếu chỉ mang theo “giấy phép lái xe” mà không đúng hạng bằng thì vẫn chẳng có ý nghĩa gì. Chẳng hạn lái ô tô mà chỉ mang theo bằng xe máy là hỏng rồi. Quang trọng là phải mang theo loại bằng “phù hợp với loại xe đó”. Câu 1 thiếu ý này nên sai.Có xe tải mới cần mang theo “lệnh vận chuyển, xe con hoặc xe máy chẳng mang loại giấy tờ đó làm gì. Như vậy, câu 2 sai.Còn câu 3 đúng, đương nhiên thôi vì 2 câu đầu sai rồi. Ngoài ra, câu 3 đầy đủ ý, và cũng đúng theo quy định trong Luật GTĐB 2008 - Điều 58.2',1,1,1,1),
(106,2,'Khi tập lái xe ô tô, người và xe tập lái phải đảm bảo các điều kiện gì?','Câu này cả 2 đáp án đều đúng, vì theo Điều 58.1 Luật GTĐB 2008 và Điều 5.12 Thông tư số 46/2012/TT-BGTVT, thì xe tập lái phải đảm bảo: có giáo viên đi kèm; xe phải đủ giấy tờ, có phanh phụ, giấy phép xe tập lái…',0,1,1,1),
(107,2,'Người có giấy phép lái xe hạng B2 được điều khiển loại xe nào?','Bạn cần nhớ con số yêu cầu với mỗi hạng bằng lái xe (quy định tại Điều 59.4 và 60.1 - Luật GTĐB 2008):+B2: đến 9 chỗ - dưới 3,5 tấn - 18 tuổi; +C: Đến 9 chỗ, - 3,5 tấn trở lên - 21 tuổi; +D: Đến 30 chỗ - 3,5 tấn trở lên - 24 tuổi; +E: Trên 30 chỗ - 3,5 tấn trở lên - 27 tuổi. Câu này hỏi về B2 (đến 9 chỗ - dưới 3,5 tấn), nên đáp án 2 đúng. Đáp án 1 và 3 đều sai.',0,1,1,1),
(108,2,'Người có giấy phép lái xe hạng C được điều khiển loại xe nào?','Với câu hỏi loại này, bạn cần nhớ con số yêu cầu với mỗi hạng bằng lái xe (quy định tại Điều 59.4 và 60.1 - Luật GTĐB năm 2008):+B2: đến 9 chỗ - dưới 3,5 tấn - 18 tuổi; +C: Đến 9 chỗ, - 3,5 tấn trở lên - 21 tuổi; +D: Đến 30 chỗ - 3,5 tấn trở lên - 24 tuổi; +E: Trên 30 chỗ - 3,5 tấn trở lên - 27 tuổi. Câu này hỏi về bằng lái xe hạng C: Đến 9 chỗ, - 3,5 tấn trở lên. Đáp án 3 đúng, đáp án 1 và 2 đều sai.Lưu ý: đáp án 1 và 3 chỉ khác nhau đúng 1 từ: “đến” và “trên” 9 chỗ. Cẩn thận không bị nhầm bạn nhé!',0,1,1,1),
(109,2,'Người lái xe phải giảm tốc độ đến mức không gây nguy hiểm (có thể dừng lại một cách an toàn) trong trường hợp nào?','Bạn có thể thấy ngay đáp án 3 sai, vì khi vượt xe khác thì cần “tăng” chứ không phải là “giảm tốc độ”.Đáp án 1 và 2 đều đúng, vì cần giảm tốc độ khi qua những đoạn đường nhiều nguy hiểm như: đường giao cắt, vướng tầm nhìn, trơn trượt, nơi đông người…',0,1,1,1),
(110,2,'Khi gặp một đoàn xe, một đoàn xe tang hoặc gặp đoàn người có tổ chức theo đội ngũ, người lái xe phải xử lý như thế nào?','Khi gặp đoàn xe hay đoàn người có tổ chức thành đội ngũ, bạn phải nhường đường, “không được cắt ngang qua”. Đó là quy định của Luật GTĐB 2008 Điều 22Đáp án 2 đúng, đáp án 1 và 3 sai.',0,1,1,1),
(111,2,'Người đủ bao nhiêu tuổi trở lên thì được điều khiển xe ô tô tải, máy kéo có trọng tải từ 3,5 tấn trở lên; xe hạng B2 kéo rơ moóc (FB2)?','Trước hết, với câu hỏi loại này, bạn cần nhớ con số yêu cầu với mỗi hạng bằng lái xe (quy định tại Điều 59.4 và 60.1 - Luật GTĐB 2008):+B2: đến 9 chỗ - dưới 3,5 tấn - 18 tuổi; +C: Đến 9 chỗ, - 3,5 tấn trở lên - 21 tuổi; +D: Đến 30 chỗ - 3,5 tấn trở lên - 24 tuổi; +E: Trên 30 chỗ - 3,5 tấn trở lên - 27 tuổi. Câu hỏi với xe “từ 3,5 tấn trở lên”, vậy cần hạng bằng C trở lên, và phải đủ 21 tuổi. Ghi chú thêm: đương nhiên đó là mức tối thiểu, hạng cao hơn (D, E), và nhiều tuổi hơn (24, hay 27 tuổi) thì càng đúng luật.Như vậy, đáp án 2 đúng. Đáp án 1 và 3 sai.',0,1,1,1),
(112,2,'Người đủ bao nhiêu tuổi trở lên thì được điều khiển xe mô tô hai bánh, xe mô tô ba bánh có dung tích xi lanh từ 50 cm3 trở lên và các loại xe có kết cấu tương tự; xe ô tô tải, máy kéo có trọng tải dưới 3,5 tấn; xe ô tô chở người 9 chỗ ngồi?','Để trả lời câu hỏi dạng tuổi lái xe hay hạng bằng, bạn cần nhớ con số yêu cầu với mỗi hạng bằng lái xe (quy định tại Điều 59.4 và 60.1 -  Luật GTĐB 2008):+B2: đến 9 chỗ - dưới 3,5 tấn - 18 tuổi; +C: Đến 9 chỗ, - 3,5 tấn trở lên - 21 tuổi; +D: Đến 30 chỗ - 3,5 tấn trở lên - 24 tuổi; +E: Trên 30 chỗ - 3,5 tấn trở lên - 27 tuổi.Câu hỏi về xe “dưới 3,5 tấn” và “đến 9 chỗ ngồi” đề cập đến hạng bằng B2. Và tuổi tối thiểu là 18.Như vậy đáp án 2 đúng. Đáp án 1 và 3 đều sai.',1,1,1,1),
(113,2,'Tại ngã ba hoặc ngã tư không có đảo an toàn, người lái xe phải nhường đường như thế nào là đúng trong các trường hợp dưới đây?','Ở ngã tư không có đảo an toàn, Điều 11.4, Điều 22.3 Điều 24.3 - Luật GTĐB 2008 quy định phải nhường đường cho:+người đi bộ đi trên đường dành riêng;+xe trên đường ưu tiên, đường chính;+xe ưu tiên;+xe đi từ bên phải đến;Từ đó suy ra, đáp án 2 và 3 đúng.Đáp án 1 sai, vì không thể nhường cho xe đi trên “đường nhánh” - phải là “đường chính” mới đúng.',1,1,1,1),
(114,2,'Người lái xe ô tô chở người trên 30 chỗ ngồi (hạng E), lái xe hạng D kéo rơ moóc (FD) phải đủ bao nhiêu tuổi trở lên?','Với câu hỏi dạng tuổi lái xe hay hạng bằng, bạn cần nhớ con số yêu cầu với mỗi hạng bằng lái xe (quy định tại Điều 59.4 và 60.1 - Luật GTĐB 2008):+B2: đến 9 chỗ - dưới 3,5 tấn - 18 tuổi; +C: Đến 9 chỗ, - 3,5 tấn trở lên - 21 tuổi; +D: Đến 30 chỗ - 3,5 tấn trở lên - 24 tuổi; +E: Trên 30 chỗ - 3,5 tấn trở lên - 27 tuổi.Câu này hỏi về xe hạng E, nên tuổi tối thiểu là 27. Đáp án 3 đúng, đáp án 1 và 2 đều sai.',0,1,1,1),
(115,2,'Tuổi tối đa của người lái xe ô tô chở người trên 30 chỗ ngồi (hạng E) là bao nhiêu tuổi?','Với câu hỏi tuổi tối đa của lái xe, bạn tham khảo Điều 60.1e Luật GTĐB 2008. Theo đó tuổi tối đa với Nam: 55 tuổi – Nữ: 50 tuổiVậy đáp án 1 đúng, đáp án 2 và 3 đều sai.',0,1,1,1),
(116,2,'Người lái xe chở người từ 10 đến 30 chỗ ngồi (hạng D), lái xe hạng C kéo rơ moóc (FC) phải đủ bao nhiêu tuổi trở lên?','Điều 60.1d Luật GTĐB 2008 quy định tuổi cho người có bằng lái hạng D là phải đủ 24 tuổi.Mẹo: tuổi tối thiểu cho hạng bằng B, C, D, E lần lượt là 18, 21, 24, 27 (cách nhau 3 tuổi, bắt đầu từ 18).Như vậy đáp án 2 đúng. Đáp án 1 và 3 sai.',0,1,1,1),
(117,2,'Người đủ 16 tuổi được điều khiển các loại xe nào dưới đây?','Người đủ 16 tuổi chỉ được điều khiển xe gắn máy có dung tích từ 50cm3 trở xuống (Điều 60.1a Luật GTĐB 2008).Đáp án 2 đúng. Các đáp án còn lại đều sai.',1,1,1,1),
(118,2,'Việc sát hạch cấp giấy phép lái xe ô tô phải thực hiện ở đâu và như thế nào?','Theo quy định tại Điều 61.7 Luật GTĐB 2008: việc sát hạch phải ở “trung tâm sát hạch”. Còn ở “cơ sở đào tạo” thì chỉ để dạy và học lái xe mà thôi, không phải để thi sát hạch lấy bằng lái.Như vậy thì đáp án 3 đúng. Đáp án 1 và 2 sai.',0,1,1,1),
(119,2,'Người có giấy phép lái xe hàng FC được điều khiển các loại xe nào?','Đáp án 2 đúng, theo quy định tại Điều 59.4g Luật GTĐB 2008.Đáp án 1 sai, vì người có giấy phép lái xe hạng FC không được điều khiển xe "ô tô chở khách nối toa". Đáp án 3 sai vì bằng FC không cho phép điều khiển mô tô 2 bánh (phải là bằng A1 mới đúng). Đáp án 4 đường nhiên sai, vì liệt kê cả phương án sai.',0,1,0,1),
(120,2,'Người có giấy phép lái xe hạng FE được điều khiển các loại xe nào?','Câu này quy định tại Điều 59.4g Luật GTĐB 2008. Theo đó, bằng FE không cho phép lái "đầu kéo kéo sơ mi rơ moóc" (phải là bằng FC). Như vậy đáp án 2 sai.Đáp án 1 đúng, vì y như luật quy định.',0,1,0,1),
(121,2,'Khi điều khiển xe cơ giới, người lái xe phải bật đèn tín hiệu báo rẽ trong trường hợp nào sau đây?','Khi lái xe trên đường, trước khi thay đổi làn được phải bật xi nhan (Điều 13.1 Luật GTĐB 2008). Còn đi thẳng, hoặc sau khi đã thay đổi làn đường thì chẳng cần bật xi nhan làm gì.Đáp án 2 đúng. Đáp án 1 và 3 sai.',0,1,1,1),
(122,2,'Trong các trường hợp nào sau đây, người lái xe không được vượt xe khác?','Khi đảm bảo các yếu tố an toàn, trong đó có “Xe bị vượt giảm tốc độ và nhường đường” thì được vượt xe khác. Do đó đáp án 2 sai.Đáp án 1 và 3 đều đúng, do không đảm bảo an toàn khi “xe bị vượt bất ngờ tăng tốc”, hoặc “có xe đi ngược chiều thì không được vượt.Tham khảo thêm trong Điều 14.2 Luật GTĐB 2008',0,1,1,1),
(123,2,'Trên đường một chiều, trước khi đến vạch người đi bộ qua đường có một xe tải đang dừng trước làn đường bên phải, người lái xe phải làm gì nếu xe của mình đang đi trên làn đường bên trái?','Lái xe luôn cần phải để ý nhường đường cho người đi bộ qua đường tại vị trí dành riêng cho họ (Điều 11.4 Luật GTĐB năm 2008). Khi thấy có xe tải đỗ, gây vướng tầm nhìn, có thể có người đi bộ đang sang đường, nên không đảm bảo an toàn để vượt qua.Đáp án 1 và 3 sai. Đáp án 2 đúng.',0,1,1,1),
(124,2,'Khi lái xe trên đường vắng mà cảm thấy buồn ngủ, người lái xe nên chọn cách xử lý như thế nào cho phù hợp?','Khi lái xe, yếu tố an toàn cần đặt lên hàng đầu. Những hành vi ảnh hưởng xấu đến sức khỏe sẽ gây mất an toàn, trong đó có việc “uống rượu bia” và “buồn ngủ” mà “tăng tốc độ kết hợp với nghe nhạc.Như vậy đáp án 2 đúng, đáp án 1 và 3 sai.',0,1,1,1),
(125,2,'Trên đường cao tốc, người lái xe xử lý như thế nào khi vượt quá lối ra của đường định rẽ?','Trên đường cao tốc cấm không được “quay đầu” hay “lùi xe” (Điều 15.4 và 16.2 Luật GTĐB 2008). Câu 1 và 2 sai.Nếu lỡ đi quá lối ra, thì chỉ có cách “Tiếp tục chạy đến lối ra tiếp theo”. Đáp án 3 đúng.',0,1,1,1),
(126,2,'Khi phát hiện thấy hành vi giả khai báo mất hoặc tẩy xóa, làm sai lệch các thông tin trên giấy phép lái xe; sử dụng giấy phép lái xe hoặc đổi hồ sơ lái xe giả; có hành vi cố tình gian dối để được đổi, cấp mới, cấp lại giấy phép lái xe, ngoài việc bị cơ quan quản lý giấy phép lái xe ra quyết định thu hồi giấy phép lái xe và hồ sơ gốc còn phải chịu trách nhiệm trước pháp luật, người lái xe không được cấp giấy phép lái xe trong thời gian bao lâu?','Các hành vi gian dối nêu trên, nếu bị phát hiện, sẽ bị cấm lấy bằng trong thời gian 5 năm (quy định tại Điều 49.9 Thông tư 46/2012/TT-BGTVT).Do đó, đáp án 1 đúng. Đáp án 2 và 3 sai.',1,1,1,1),
(127,2,'Người lái xe mô tô xử lý như thế nào khi cho xe mô tô phía sau vượt?','Muốn xe phía sau vượt thì phải đi về “bên phải” và “giảm tốc độ”. Đáp án 2 và 3 sai, vì có hành vi ngăn cản vượt: đi về “bên trái” và “tăng tốc độ”. Tham khảo thêm quy định tại Điều 14.3 Luật GTĐB 2008.Đáp án 1 đúng, đáp án 2 và 3 sai.',1,1,1,1),
(128,2,'Khi xe đi ngược chiều đến rất gần, xe ô tô cùng chiều cố tình vượt, người lái xe xử lý như thế nào?','Trong tình huống khá nguy hiểm này, cần khẩn trương “giảm tốc độ” và đi sát vào “lề bên phải” để tránh va quệt. Đáp án 2 đúng.Đáp án 1 và 3 sai, do có hành vi gây nguy hiểm “giữ nguyên làn đường”, “tăng tốc độ”, “không giảm tốc độ”.Tham khảo thêm quy định tại Điều 14 Luật GTĐB 2008.',0,1,1,1),
(129,2,'Khi xe ô tô bị hỏng tại vị trí giao nhau giữa đường bộ và đường sắt, người lái xe xử lý như thế nào?','Tình huống này rất nguy hiểm, và cần làm mọi cách để tránh tai nạn như: nhanh chóng đưa xe ra khỏi đường sắt, VÀ (chứ không phải hoặc) tìm cách cảnh báo để đoàn tàu dừng lại (hy vọng trước khi quá muộn). Như vậy đáp án 1 đúng.Đáp án 2 sai vì nếu đặt biển cảnh báo cho đoàn tàu thì phải đặt cách xa đó thì lái tàu mới kịp dừng chứ đặt “tại vị trí xe bị hỏng”, khi lái tàu nhìn thấy thì e đã quá muộn để phanh và dừng.',0,1,1,1),
(130,2,'Trong các trường hợp dưới đây, để đảm bảo an toàn khi tham gia giao thông, người lái xe mô tô cần thực hiện như thế nào?','Đi xe máy phải đội mũ bảo hiểm, quần áo gọn gàng, KHÔNG sử dụng ô, điện thoại di động, thiết bị âm thanh… đồng thời cấm “buông cả 2 tay” hoặc đi “bằng 1 bánh”. Như vậy vừa đảm bảo an toàn, mà cũng đúng quy định trong Luật GTĐB 2008 - Điều 30Đáp án 1 và 2 đúng. Đáp án 3 và 4 sai.',1,1,1,1),
(131,2,'Đường bộ trong khu vực đông dân cư gồm những đường nào?','Theo quy định trong Thông tư 13/2009/TT-BGTVT - Điều31, Đường bộ trong khu vực đông dân cư gồm đường:+Nằm trong khu vực nội thành phố, nội thị xã;+Nằm từ nơi có biển "Bắt đầu khu đông dân cư" đến nơi có biển "Hết khu đông dân cư"Như vậy đáp án 2 đúng, vì đầy đủ ý.Câu 1 sai vì thiếu ý đường cắm biển báo đông dân cư. Đáp án 3 sai vì bao gồm cả khái niệm chung chung “đường bộ có đông người".',1,1,1,1),
(132,3,'Xe máy kéo, xe công nông, xe lam, xe lôi máy, xe xích lô máy, xe ba gác máy, xe chuyên dùng và các loại xe tương tự (loại đang được phép hoạt động) tham gia giao thông với tốc độ tối đa cho phép là bao nhiêu?','Theo Điều 8 - Thông tư số 13/2009/TT-BGTVT, các loại xe cơ giới như: máy kéo, các loại xe tương tự, xe máy chuyên dùng thì tốc độ tối đa cho phép là 30 km/h. Vậy đáp án 4 đúng. Các đáp án còn lại đều sai.Ghi chú: Nội dung trên đây theo quy định cũ. Từ ngày 01/03/2016, tốc độ tối đa đã có sự thay đổi, theo Thông tư 91/2015/TT-BGTVT.',0,1,1,1),
(133,3,'Trên đường bộ, trong khu vực đông dân cư, xe mô tô hai bánh, xe gắn máy tham gia giao thông với tốc độ tối đa cho phép là bao nhiêu?','Với câu hỏi tốc độ trong khu đông dân cư, Điều 6 - Thông tư số 13/2009/TT-BGTVT quy định:+Ô tô chở người đến 30 chỗ ngồi; ô tô tải có trọng tải dưới 3.500 kg. Tối đa: 50km/h+Ô tô chở người trên 30 chỗ ngồi; ô tô tải có trọng tải từ 3.500 kg trở lên; ô tô sơ mi rơ moóc; ô tô kéo rơ moóc; ô tô kéo xe khác; ô tô chuyên dùng; xe mô tô; xe gắn máy. Tối đa: 40km/hXe máy chạy tối đa 40km/h. Đáp án 3 đúng. Các đáp án còn lại đều sai.Ghi chú: Nội dung trên đây theo quy định cũ. Từ ngày 01/03/2016, tốc độ tối đa đã có sự thay đổi, theo Thông tư 91/2015/TT-BGTVT.',1,1,1,1),
(134,3,'Trên đường bộ trong khu vực đông dân cư, loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 40km/h?','Với câu hỏi tốc độ trong khu đông dân cư, Điều 6 - Thông tư số 13/2009/TT-BGTVT quy định:+Ô tô chở người đến 30 chỗ ngồi; ô tô tải có trọng tải dưới 3.500 kg. Tối đa: 50km/h+Ô tô chở người trên 30 chỗ ngồi; ô tô tải có trọng tải từ 3.500 kg trở lên; ô tô sơ mi rơ moóc; ô tô kéo rơ moóc; ô tô kéo xe khác; ô tô chuyên dùng; xe mô tô; xe gắn máy. Tối đa: 40km/hĐáp án 1 đúng.Đáp án 2 sai, do xe tải “dưới 3,5 tấn” chạy tối đa 50km/h. Đáp án 3 sai, vì những loại xe máy kéo, công nông… chỉ được chạy tối đa 30km/h.Ghi chú: Nội dung trên đây theo quy định cũ. Từ ngày 01/03/2016, tốc độ tối đa đã có sự thay đổi, theo Thông tư 91/2015/TT-BGTVT. ',1,1,1,1),
(135,3,'Trên đường bộ trong khu vực đông dân cư, loại xe cơ giới nào tham gia giao thông với tốc độ cho phép là 50km/h?','Với câu hỏi tốc độ trong khu đông dân cư, Điều 6 - Thông tư số 13/2009/TT-BGTVT quy định:+Ô tô chở người đến 30 chỗ ngồi; ô tô tải có trọng tải dưới 3.500 kg. Tối đa: 50km/h+Ô tô chở người trên 30 chỗ ngồi; ô tô tải có trọng tải từ 3.500 kg trở lên; ô tô sơ mi rơ moóc; ô tô kéo rơ moóc; ô tô kéo xe khác; ô tô chuyên dùng; xe mô tô; xe gắn máy. Tối đa: 40km/hNhư vậy, đáp án 3 đúng.Đáp án 1 sai, vì xe khách trên 30 chỗ, xe tải trên 3,5 tấn… chỉ được chạy tối đa 40km/h. Đáp án 2 sai,  vì những loại xe máy kéo, công nông… chỉ được chạy tối đa 30km/h. Đáp án 4 sai, vì liệt kê cả phương án sai.Ghi chú: Nội dung trên đây theo quy định cũ. Từ ngày 01/03/2016, tốc độ tối đa đã có sự thay đổi, theo Thông tư 91/2015/TT-BGTVT.',0,1,1,1),
(136,3,'Trên đường bộ ngoài khu vực đông dân cư, các loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 50km/h?','Với câu hỏi tốc độ ngoài khu đông dân cư, Điều 7 - Thông tư số 13/2009/TT-BGTVT quy định:+Ô tô chở người đến 30 chỗ ngồi (trừ ô tô buýt); ô tô tải có trọng tải dưới 3.500 kg. Tốc độ tối đa: 80km/h.+Ô tô chở người trên 30 chỗ ngồi (trừ tô buýt); ô tô tải có trọng tải từ 3.500 kg trở lên. Tốc độ tối đa: 70km/h.+Ô tô buýt; tô sơ mi rơ moóc; ô tô chuyên dùng; xe mô tô. Tốc độ tối đa: 60km/h.+Ô tô kéo rơ moóc; ô tô kéo xe khác; xe gắn máy. Tốc độ tối đa: 50km/h.Câu này hỏi về tốc độ 50km/h. Vậy, đáp án 3 đúng. Các đáp án còn lại đều sai.Ghi chú: Nội dung trên đây theo quy định cũ. Từ ngày 01/03/2016, tốc độ tối đa đã có sự thay đổi, theo Thông tư 91/2015/TT-BGTVT. ',1,1,1,1),
(137,3,'Trên đường bộ ngoài khu vực đông dân cư, loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 60km/h?','Với câu hỏi tốc độ ngoài khu đông dân cư, Điều 7 - Thông tư số 13/2009/TT-BGTVT quy định:+Ô tô chở người đến 30 chỗ ngồi (trừ ô tô buýt); ô tô tải có trọng tải dưới 3.500 kg. Tốc độ tối đa: 80km/h.+Ô tô chở người trên 30 chỗ ngồi (trừ tô buýt); ô tô tải có trọng tải từ 3.500 kg trở lên. Tốc độ tối đa: 70km/h.+Ô tô buýt; tô sơ mi rơ moóc; ô tô chuyên dùng; xe mô tô. Tốc độ tối đa: 60km/h.+Ô tô kéo rơ moóc; ô tô kéo xe khác; xe gắn máy. Tốc độ tối đa: 50km/h.Câu này hỏi về tốc độ 60km/h. Vậy, đáp án 4 đúng. Các đáp án còn lại đều sai.Ghi chú: Nội dung trên đây theo quy định cũ. Từ ngày 01/03/2016, tốc độ tối đa đã có sự thay đổi, theo Thông tư 91/2015/TT-BGTVT.',0,1,1,1),
(138,3,'Trên đường bộ ngoài khu vực đông dân cư, loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 70km/h?','Với câu hỏi tốc độ ngoài khu đông dân cư, Điều 7 - Thông tư số 13/2009/TT-BGTVT quy định:+Ô tô chở người đến 30 chỗ ngồi (trừ ô tô buýt); ô tô tải có trọng tải dưới 3.500 kg. Tốc độ tối đa: 80km/h.+Ô tô chở người trên 30 chỗ ngồi (trừ tô buýt); ô tô tải có trọng tải từ 3.500 kg trở lên. Tốc độ tối đa: 70km/h.+Ô tô buýt; tô sơ mi rơ moóc; ô tô chuyên dùng; xe mô tô. Tốc độ tối đa: 60km/h.+Ô tô kéo rơ moóc; ô tô kéo xe khác; xe gắn máy. Tốc độ tối đa: 50km/h.Câu này hỏi về tốc độ 60km/h. Vậy, đáp án 2 đúng. Các đáp án còn lại đều sai.Ghi chú: Nội dung trên đây theo quy định cũ. Từ ngày 01/03/2016, tốc độ tối đa đã có sự thay đổi, theo Thông tư 91/2015/TT-BGTVT. ',0,1,1,1),
(139,3,'Trên đường bộ ngoài khu vực đông dân cư, loại xe nào tham gia giao thông với tốc độ tối đa cho phép là 80km/h?','Với câu hỏi tốc độ ngoài khu đông dân cư, Điều 7 - Thông tư số 13/2009/TT-BGTVT quy định:+Ô tô chở người đến 30 chỗ ngồi (trừ ô tô buýt); ô tô tải có trọng tải dưới 3.500 kg. Tốc độ tối đa: 80km/h.+Ô tô chở người trên 30 chỗ ngồi (trừ tô buýt); ô tô tải có trọng tải từ 3.500 kg trở lên. Tốc độ tối đa: 70km/h.+Ô tô buýt; tô sơ mi rơ moóc; ô tô chuyên dùng; xe mô tô. Tốc độ tối đa: 60km/h.+Ô tô kéo rơ moóc; ô tô kéo xe khác; xe gắn máy. Tốc độ tối đa: 50km/h.Câu này hỏi về tốc độ 60km/h. Vậy, đáp án 1 đúng. Các đáp án còn lại đều sai.Ghi chú: Nội dung trên đây theo quy định cũ. Từ ngày 01/03/2016, tốc độ tối đa đã có sự thay đổi, theo Thông tư 91/2015/TT-BGTVT.',0,1,1,1),
(140,3,'Trên đường cao tốc, trừ khi nhập làn và tách làn, với điều kiện mặt đường khô ráo, xe cơ giới đang chạy với tốc độ đến 60km/h, người lái xe phải duy trì khoảng cách an toàn với xe đang chạy phía trước tối thiểu là bao nhiêu?','Với câu hỏi liên quan giữa tốc độ và khoảng cách các xe trên đường cao tốc, Điều 12 - Thông tư số 13/2009/TT-BGTVT quy định:+Tốc độ đến 60km/h: tối thiểu 30m+Trên 60 đến 80km/h: tối thiểu 50m+Trên 80 đến 100km/h: tối thiểu 70m+Trên 100 đến 120km/h: tối thiểu 90mCâu này hỏi về tốc độ đến 60km/h: tối thiểu 30m. Vậy, đáp án 1 đúng. Các đáp án còn lại đều sai.Mẹo: Khoảng cách 2 xe trên đường cao tốc bằng tốc độ tối đa trừ đi 30, như câu này: 60 - 30 = 30',0,1,1,1),
(141,3,'Trên đường cao tốc, trừ khi nhập làn và tách làn, với điều kiện mặt đường khô ráo, xe cơ giới đang chạy với tốc độ trên 60km/h đến 80km/h, người lái xe phải duy trì khoảng cách an toàn với xe đang chạy phía trước tối thiểu là bao nhiêu?','Với câu hỏi liên quan giữa tốc độ và khoảng cách các xe trên đường cao tốc, Điều 12 - Thông tư số 13/2009/TT-BGTVT quy định:+Tốc độ đến 60km/h: tối thiểu 30m+Trên 60 đến 80km/h: tối thiểu 50m+Trên 80 đến 100km/h: tối thiểu 70m+Trên 100 đến 120km/h: tối thiểu 90mCâu này hỏi về tốc độ 60 - 80km/h: tối thiểu 50m. Vậy, đáp án 2 đúng. Các đáp án còn lại đều sai.Mẹo: Khoảng cách 2 xe trên đường cao tốc bằng tốc độ tối đa trừ đi 30, như câu này: 80 - 30 = 50',0,1,1,1),
(142,3,'Trên đường cao tốc, trừ khi nhập làn và tách làn, với điều kiện mặt đường khô ráo, xe cơ giới đang chạy với tốc độ trên 80km/h đến 100km/h, người lái xe phải duy trì khoảng cách an toàn với xe đang chạy phía trước tối thiểu là bao nhiêu?','Với câu hỏi liên quan giữa tốc độ và khoảng cách các xe trên đường cao tốc, Điều 12 - Thông tư số 13/2009/TT-BGTVT quy định:+Tốc độ đến 60km/h: tối thiểu 30m+Trên 60 đến 80km/h: tối thiểu 50m+Trên 80 đến 100km/h: tối thiểu 70m+Trên 100 đến 120km/h: tối thiểu 90mCâu này hỏi về tốc độ 80 - 100km/h: tối thiểu 70m. Vậy, đáp án 3 đúng. Các đáp án còn lại đều sai.Mẹo: Khoảng cách 2 xe trên đường cao tốc bằng tốc độ tối đa trừ đi 30, như câu này: 100 - 30 = 70',0,1,1,1),
(143,3,'Trên đường cao tốc, trừ khi nhập làn và tách làn, với điều kiện mặt đường khô ráo, xe cơ giới đang chạy với tốc độ trên 100km/h đến 120km/h, người lái xe phải duy trì khoảng cách an toàn với xe đang chạy phía trước tối thiểu là bao nhiêu?','Với câu hỏi liên quan giữa tốc độ và khoảng cách các xe trên đường cao tốc, Điều 12 - Thông tư số 13/2009/TT-BGTVT quy định:+Tốc độ đến 60km/h: tối thiểu 30m+Trên 60 đến 80km/h: tối thiểu 50m+Trên 80 đến 100km/h: tối thiểu 70m+Trên 100 đến 120km/h: tối thiểu 90mCâu này hỏi về tốc độ 100 - 120km/h: tối thiểu 90m. Vậy, đáp án 4 đúng. Các đáp án còn lại đều sai.Mẹo: Khoảng cách 2 xe trên đường cao tốc bằng tốc độ tối đa trừ đi 30, như câu này: 120 - 30 = 90',0,1,1,1),
(144,3,'Người lái xe phải giảm tốc độ thấp hơn tốc độ tối đa cho phép đến mức không nguy hiểm trong các trường hợp nào dưới đây?','Điều 5 Thông tư số 13/2009/TT-BGTVT quy định những trường hợp phải giảm tốc độ, trong đó có trường hợp “có biển báo nguy hiểm”. Như vậy đáp án 1 đúng.Đáp án 2 và 3 đều sai vì không nằm trong điều luật trên. Hơn nữa, biển báo “dừng đỗ xe” và “cấm vượt” cũng không yêu cầu phải giảm tốc độ.',0,1,1,1),
(145,3,'Người lái xe phải giảm tốc độ thấp hơn tốc độ tối đa cho phép đến mức không nguy hiểm trong các trường hợp nào dưới đây?','Khi vượt xe khác thì cần tăng tốc độ mới vượt được. Còn khi cho xe chạy sau vượt mới cần “giảm tốc độ”.Do đó, đáp án 2 đúng, đáp án 1 và 3 sai. Tham khảo thêm các trường hợp phải giảm tốc độ trong Điều 5 Thông tư số 13/2009/TT-BGTVT',0,1,1,1),
(146,4,'Khái niệm xe chạy quá tải trọng của đường được hiểu như thế nào là đúng?','Thông tư 07/2010/TT-BGTVT về tải trọng đường bộ, Điều 9.1 quy định rõ về xe quá tải có “tổng trọng lượng” (gồm xe + hàng) vượt quá. Đáp án 1 đúng theo quy định.Câu 2 sai vì chỉ nêu “trọng lượng hàng”, chứ chưa phải tổng trọng lượng.',0,0,1,0),
(147,4,'Vận tải đa phương thức được hiểu như thế nào là đúng?','“Đa phương thức” nghĩa là phải có “ít nhất 2 phương thức” (quy định tại Điều 81 Luật GTĐB 2008).Đáp án 1 sai vì chỉ nêu 1 phương thức “vận tải bằng đường bộ”. Đáp án 2 là đúng theo điều luật nêu trên.',0,0,0,0),
(148,4,'Khái niệm "hàng nguy hiểm" được hiểu như thế nào là đúng?','Điều 3.29 Luật GTĐB 2008 quy định về hàng nguy hiểm“Là hàng hóa có chứa các chất nguy hiểm, khi chở trên đường có khả năng gây nguy hại tới tính mạng, sức khỏe con người, môi trường, an toàn và an ninh quốc gia.”Như vậy đáp án 2 đúng. Đáp án 1 và 3 đều sai.',0,0,1,0),
(149,4,'Trong trường hợp đặc biệt, xe quá tải trọng, quá khổ của đường bộ, xe bánh xích có thể lưu hành trên đường nhưng phải xin phép cơ quan nào?','Xe quá khổ quá tải, bánh xích “phải được cơ quan quản lý đường bộ có thẩm quyền cấp phép”, theo Điều 28.2 Luật GTĐB 2008.Đáp án 2 đúng. Đáp án 1 và 3 đều sai.',0,0,1,0),
(150,4,'Hoạt động vận tải đường bộ được hiểu như thế nào là đúng?','Hoạt động vận tải đường bộ gồm cả hoạt động kinh doanh và không kinh doanh.Đáp án 1 sai vì thiếu hoạt động “không kinh doanh”. Đáp án 2 đúng.Tham khảo chi tiết tại Điều 64.1 Luật GTĐB 2008.',0,0,1,0),
(151,4,'Thời gian làm việc trong một ngày của người lái xe được quy định là bao nhiêu?','Theo quy định tại Điều 65.1 - Luật GTĐB 2008, thời gian làm việc tối đa trong ngày của lái xe là “10 giờ” và không liên tục quá “4 giờ”.Như vậy đáp án 2 đúng. Đáp án 1 và 3 sai.',0,0,1,0),
(152,4,'Kinh doanh vận tải hành khách bằng ô tô bao gồm những loại hình nào ghi ở dưới đây?','Điều 166.1 - Luật GTĐB 2008 quy định kinh doanh vận tải khách bao gồm tất cả các loại hình nêu trong 2 đáp án. Như vậy, cả 2 đáp án đều đúng.',0,0,0,0),
(153,4,'Hàng nguy hiểm, hàng có mùi hôi thối hoặc động vật, hàng hóa khác có ảnh hưởng đến sức khỏe hành khách có được chở trên xe khách hay không?','Nếu bạn đi xe khách thì sẽ thấy cảm giác cực kỳ khó chịu khi trên xe có mùi lạ, chưa nói đến có hàng gây nguy hiểm. Như vậy, có thể suy luận ra là quy định sẽ không được chở hàng hóa có ảnh hưởng đến sức khỏe hành khách như: hàng nguy hiểm, hàng có mùi hồi thối… Đó là nội dung tại Điều 68.1c Luật GTĐB 2008.Như vậy đáp án 2 đúng, đáp án 1 sai.',0,0,1,0),
(154,4,'Người vận tải, người lái xe khách phải chấp hành những quy định nào ghi ở dưới đây?','Điều 68.1 Luật giao thông đường bộ năm 2008 quy định xe khách phải đảm bảo những nội dung trong cả 2 đáp án. Do đó, cả đáp án 1 và 2 đều đúng.',0,0,1,0),
(155,4,'Người kinh doanh vận tải hành khách có những nghĩa vụ nào theo quy định?','Câu này có thể trả lời bằng suy luận cũng được.Đáp án 2 sai, vì thực hiện “trái quy định của luật” mà “không phải bồi thường thiệt hại” là vô lý.Điều 1 và 3 nếu những nội dung đều là nghĩa vụ hợp lý của người làm vận tải: đúng cam kết, mua bảo hiểm hành khách, giao vé… Đó cũng tuân theo quy định tại Điều 69.2 Luật GTĐB 2008.',0,0,1,0),
(156,4,'Người kinh doanh vận tải hành khách có những quyền hạn nào theo quy định?','Đáp án 1 sai vì cho phép “tự ý thay đổi vị trí đón trả khách” so với hợp đồng.Đáp án 2 và 3 đều đúng, vì đó là những quyền hợp lý của người vận tải: thu cước, phí vận tải, từ chối vận chuyển người có hành vi “gây rối” hoặc “bị dịch bệnh nguy hiểm”... Đây cũng là nội dung của Điều 69.1 Luật giao thông đường bộ 2008.',0,0,1,0),
(157,4,'Hành khách có các quyền gì khi đi trên xe vận tải hành khách?','Theo quy định tại Điều 71.1 Luật GTĐB 2008, hành khách được miễn cước với hành lý không quá 20kg. Do đó đáp án 2 sai vì cho phép “không quá 50kg”. Đáp án 3 sai. Đáp án 1 đúng.',0,0,1,0),
(158,4,'Hành khách có các nghĩa vụ gì khi đi trên xe vận tải hành khách?','Đáp án 2 sai vì cho hành khách mang theo hành lý mà pháp luật “cấm lưu thông”.Đáp án 1 và 3 đúng, theo Điều 71.2 Luật giao thông đường bộ 2008.',0,0,1,0),
(159,4,'Việc vận chuyển hàng hóa bằng xe ô tô phải chấp hành các quy định nào ghi ở dưới đây?','Điều 72 Luật GTĐB 2008 không cho phép chở hàng vượt quá “kích thước giới hạn cho phép của xe”. Nghĩa là nếu hàng hóa “vượt quá chiều cao quy định” cũng bị cấm. Như vậy đáp án 1 sai.Cả đáp án 2 và 3 đều đúng, vì hợp lý và được quy định tại điều luật nêu trên.',0,0,1,0),
(160,4,'Người kinh doanh vận tải hàng hóa có các quyền gì theo quy định được ghi ở dưới đây?','Điều 72.2a cấm người kinh doanh vận tải chở hàng “vượt quá kích thước giới hạn của xe”, nghĩa là cũng không được xếp hàng “vượt quá chiều cao quy định”. Do đó, câu 2 sai.Câu 1 và 3 đúng theo quy định tại Điều 73.1 Luật GTĐB 2008.',0,0,1,0),
(161,4,'Người kinh doanh vận tải hàng hóa có các nghĩa vụ gì theo quy định được ghi ở dưới đây?','Điều 73.2 Luật GTĐB 2008 quy định người kinh doanh vận tải hàng hóa phải có tất cả các nghĩa vụ nêu trong cả 2 đáp án. Như vậy, cả đáp án 1 và 2 đều đúng.',0,0,1,0),
(162,4,'Người thuê vận tải hàng hóa có các quyền hạn gì theo quy định được ghi ở dưới đây?','Quyền của người thuê vận tải hàng hóa được quy định trong Điều 74 Luật giao thông đường bộ 2008. Theo đó đáp án 1 đúng.Đáp án 2 sai, vì người thuê không có quyền yêu cầu "giao hàng trước thời hạn đã thỏa thuận trong hợp đồng".',0,0,1,0),
(163,4,'Người thuê vận tải hàng hóa có các nghĩa vụ gì theo quy định được ghi ở dưới đây?','Điều 74.2 Luật giao thông đường bộ 2008 quy định người thuê vận tải hàng hóa phải có tất cả các nghĩa vụ nêu trong các đáp án trên. Vì vậy, cả đáp án 1 và 2 đều đúng.',0,0,1,0),
(164,4,'Người nhận hàng có các quyền gì được ghi ở dưới đây?','Đáp án 3 sai vì cho nhận hàng “không theo giấy vận chuyển”. Thực tế, khi nhận hàng hóa phải theo giấy vận chuyển hoặc chứng từ tương đương khác.Cả đáp án 1 và 2 đều đúng, theo quy định tại Điều 75.1 Luật giao thông đường bộ 2008.',0,0,1,0),
(165,4,'Người nhận hàng có các nghĩa vụ gì được ghi ở dưới đây?','Điều 75.2 Luật giao thông đường bộ 2008 quy định người nhận hàng phải có nghĩa vụ nêu trong cả 2 đáp án. Do đó, đáp án 1 và 2 đều đúng.',0,0,1,0),
(166,4,'Hàng siêu trường, siêu trọng được hiểu như thế nào là đúng?','Điều 76.1 Luật giao thông đường bộ 2008 quy định“Hàng siêu trường, siêu trọng là hàng có kích thước hoặc trọng lượng vượt quá giới hạn quy định nhưng không thể tháo rời ra được.”Như vậy đáp án 3 đúng. Đáp án 1 sai vì vượt quá kích thước trọng lượng “của xe” thay vì quá “giới hạn quy định”. Đáp án 2 sai vì nêu hàng “có thể tháo rời”.',0,0,0,0),
(167,4,'Việc vận chuyển động vật sống phải tuân theo những quy định nào ghi ở dưới đây?','Điều 77 Luật giao thông đường bộ 2008 quy định về việc vận chuyển động vật sống. Trong đó không bắt buộc “phải sử dụng xe cơ giới chuyên dùng” để chở hàng. Đáp án 2 sai.Đáp án 1 và 3 đều đúng, vì được nêu rõ trong điều luật trên.',0,0,1,0),
(168,4,'Xe vận chuyển hàng nguy hiểm phải chấp hành những quy định nào ghi ở dưới đây?','Xe chở hàng nguy hiểm “phải có giấy phép” và không được “dừng, đỗ nơi đông người” (quy định tại Điều 78 Luật giao thông đường bộ 2008). Đáp án 1 đúng.Đáp án 2 sai vì luật không bát buộc xe “phải chạy liên tục không được dừng, đỗ trong quá trình vận chuyển”. Đáp án 3 sai vì liệt kê phương án 2.',0,0,1,0),
(169,4,'Trong đô thị, người lái xe buýt, xe taxi khách, xe taxi tải, xe chở hàng phải thực hiện những quy định gì ghi ở dưới đây?','Với câu hỏi này, đáp án 2 sai vì cho phép taxi đón trả khách hoặc hàng tại “điểm dừng đỗ của xe buýt”. Điều 70.1+2 Luật GTĐB quy định những nội dung của 2 đáp án đầu.Vì vậy đáp án 1 và 2 đều đúng.',0,0,1,0),
(170,4,'Xe vệ sinh môi trường, xe chở phế thải, vật liệu rời khi hoạt động vận tải trong đô thị phải thực hiện những quy định gì ghi ở dưới đây?','Những loại xe này phải chạy “trong thời gian phù hợp trong ngày” và phải được “phủ kín để không rơi vãi…”. Do đó, đáp án 1 và 2 đều đúng.Tham khảo chi tiết trong 79.4 Luật giao thông đường bộ 2008.',0,0,1,0),
(171,4,'Những ký hiệu hàng hóa dưới đây, ký hiệu nào chống mưa?','Câu hỏi loại này có thể nhìn hình vẽ để suy luận ý nghĩa. +Hình 1: mặt trời - tránh ánh nắng. +Hình 2: ô che mưa - tránh mưa. +Hình 3: ly thủy tinh - dễ vỡCâu này hỏi về chống mưa. Như vậy đáp án 2 đúng. Đáp án 1 và 3 sai.',0,0,1,0),
(172,4,'Ký hiệu chuyên dùng nào biểu hiện hàng chuyên chở phải tránh ánh nắng mặt trời?','Câu hỏi loại này có thể nhìn hình vẽ để suy luận ý nghĩa.+Hình 1: mặt trời - tránh ánh nắng. +Hình 2: ô che mưa - tránh mưa. Câu này hỏi về tránh ánh nắng. Như vậy đáp án 1 đúng. Đáp án 2 sai.',0,0,1,0),
(173,4,'Hình nào dưới đây biểu hiện hàng chuyên chở dễ vỡ phải cẩn thận?','Câu hỏi loại này có thể nhìn hình vẽ để suy luận ý nghĩa. +Hình 1: mặt trời - tránh ánh nắng. +Hình 2: chai lọ - chất lỏng. Câu này hỏi về hàng dễ vỡ. Như vậy đáp án 1 đúng. Đáp án 2 sai.',0,0,1,0),
(174,4,'Hình nào dưới đây đòi hỏi hàng xếp theo hướng dẫn thẳng đứng?','Câu hỏi loại này có thể nhìn hình vẽ để suy luận ý nghĩa. +Hình 1: mặt trời - tránh ánh nắng. +Hình 2: chai lọ - chất lỏng.+Hình 3: mũi tên chỉ lên trên - xếp theo hướng lên trênCâu này hỏi về xếp hàng theo hướng thẳng đứng. Như vậy đáp án 3 đúng. Đáp án 1 và 2 sai.',0,0,1,0),
(175,4,'Hình nào dưới đây biểu hiện hàng chuyên chở là chất lỏng?','Câu hỏi loại này có thể nhìn hình vẽ để suy luận ý nghĩa. +Hình 1: ly thủy tinh - dễ vỡ +Hình 2: chai lọ - chất lỏngCâu này hỏi về hàng chất lỏng. Như vậy đáp án 2 đúng. Đáp án 1 sai.',0,0,1,0),
(176,5,'Đạo đức cơ bản của mỗi con người, người lái xe thể hiện ở những điểm nào ghi dưới đây?','Câu hỏi này có thể dùng suy luận: những đức tính nào tốt thì người lái xe đều cần. Do đó, đáp án 1 và 2 đều đúng.',0,1,1,1),
(177,5,'Một số nét đẹp về truyền thống đạo đức của mỗi con người Việt Nam theo tư tưởng của Chủ tịch Hồ Chí Minh là?','Những gì tốt đẹp đều là truyền thống đạo đức của người Việt Nam: gồm cả cần kiệm, liêm chính (hơi khuôn sáo nhỉ?!). Như vậy đáp án 1 và 2 đều đúng.',0,1,1,1),
(178,5,'Khái niệm về đạo đức nghề nghiệp của người lái xe kinh doanh vận tải được hiểu như thế nào?','Người lái xe vận tải cần tất cả các đức tính và hành vi nêu trên. Đáp án đúng là cả 1 và 2.',0,1,1,1),
(179,5,'Người lái xe kinh doanh vận tải khi thực hiện tốt việc rèn luyện, nâng cao trách nhiệm, đạo đức nghề nghiệp sẽ thu được kết quả như thế nào?','Kết quả thu được gồm tất cả các ý nêu trên. Đáp án đúng là cả 1 và 2.',0,1,1,1),
(180,5,'Hãy nêu trách nhiệm cơ bản của các tổ chức trong việc quản lý, giáo dục và nâng cao đạo đức nghề nghiệp cho người lái xe?','Trách nhiệm cơ bản của tổ chức với người lái xe bao gồm tất cả các nội dung nêu trên. Do đó đáp án đúng là cả 1 và 2.',0,1,1,1),
(181,5,'Người lái xe khách, xe buýt cần thực hiện những nhiệm vụ gì ghi dưới đây?','Người lái xe khách, xe buýt cần thực hiện tất cả các nhiệm vụ nêu trên. Đáp án đúng là cả 1 và 2.',0,1,1,1),
(182,5,'Người lái xe vận tải hàng hóa cần thực hiện những nhiệm vụ gì ghi dưới đây?','Người lái xe tải cần thực hiện những nhiệm vụ nêu ở trên. Đáp án đúng là cả 1 và 2.',0,1,1,1),
(183,5,'Người lái xe kinh doanh vận tải cần thực hiện những công việc gì ghi ở dưới đây để thường xuyên rèn luyện nâng cao đạo đức nghề nghiệp?','Để rèn luyện nâng cao đạo đức nghề nghiệp, người lái xe tải phải thực hiện tất cả các công việc nêu trên. Đáp án 1 và 2 đều đúng.',0,1,1,1),
(184,5,'Ngoài các phẩm chất của con người mới xã hổi chủ nghĩa, người lái xe cần phải có phẩm chất đạo đức nghề nghiệp gì ghi dưới đây?','Người lái xe cần có tất cả các phẩm chất đạo đức nghề nghiệp ghi trên đây. Do đó, đáp án đúng là cả 1 và 2.',0,1,1,1),
(185,5,'Người lái xe và nhân viên phục vụ trên xe ô tô vận tải hành khách phải có những trách nhiệm gì theo qui định được ghi ở dưới đây?','Tài xế và phụ xe trên xe khách cần có tất cả những trách nhiệm ghi ở trên. Đáp án đúng là cả 1 và 2.',0,1,1,1),
(186,5,'Khái niệm về văn hóa giao thông được hiểu thế nào là đúng?','Văn hóa giao thông được hiểu là tất cả những nội dung trong các đáp án. Do đó, đáp án đúng là cả 1 và 2.',1,1,1,1),
(187,5,'Trên làn đường dành cho ô tô có vũng nước lớn, có nhiều người đi xe mô tô trên làn đường bên cạnh, người lái xe ô tô xử lý như thế nào là có văn hóa giao thông?','Người lái xe ô tô cần giảm tốc độ cho xe chạy từ từ qua vũng nước, tránh làm bắn nước vào người đi xung quanh. Đáp án 2 đúng.Đáp án 1 sai, vì cho xe chạy nhanh qua sẽ làm cho nước bắn tóe vào người đi mô tô bên canh, như vậy là hành vi có văn hóa.Đáp án 3 sai, vì cho xe chạy lấn làn sang làn đường mô tô là sai luật.',0,1,1,1),
(188,5,'Người lái xe cố tính vi phạm Luật giao thông đường bộ, không phân biệt làn đường, vạch phân làn, phóng nhanh vượt ẩu, vượt đèn đỏ, đi vào đường cấm, đường một chiều được coi là hành vi nào trong các hành vi sau đây?','Người cố tình phạm luật giao thông thì đương nhiên là “thiếu văn hóa giao thông”. Đáp án 2 đúng, đáp án 1 và 3 đều sai.',1,1,1,1),
(189,5,'Khi sơ cứu người bị tai nạn giao thông đường bộ, có vết thương chảy máu ngoài đỏ sẫm trào đều trên mặt vết thương, máu chảy ra số lượng ít bạn phải làm gì?','Nếu vết thương chảy máu số lượng ít, thì phải cầm máu trực tiếp. Còn nếu máu phun thành tia và phun mạnh khi mạch đập, thì phải chặn động mạch và cầm máu bằng garo.Câu hỏi này có đáp án 1 đúng. Đáp án 2 và 3 sai.',0,1,1,1),
(190,5,'Khi sơ cứu người bị tai nạn giao thông đường bộ, có vết thương chảy máu ngoài đỏ tươi phun thành tia và phun mạnh khi mạch đập bạn phải làm gì?','Khi vết thương có máu “phun thành tia và phun mạnh” thì cần phải “chặn động mạch” và “cầm máu bằng garo”. Đáp án đúng là 2 và 3.Đáp án 1 sai, vì trường hợp này vết thương nặng, cầm máu trực tiếp không hiệu quả.',0,1,1,1),
(191,5,'Trong các hành vi dưới đây, người lái xe có văn hóa giao thông cần phải đáp ứng các điều kiện nào?','Người lái xe có văn hóa giao thông phải đáp ứng tất cả các điều kiện nêu trên. Đáp án đúng là cả 1 và 2.',0,1,1,1),
(192,5,'Trong các hành vi dưới đây, người lái xe có văn hóa khi tham gia giao thông phải đáp ứng các điều kiện nào?','Người lái xe có văn hóa phải đáp ứng các điều kiện trong cả 2 đáp án đầu tiên. Do đó, đáp án 1 và 2 đúng. Đáp án 3 sai.',0,1,1,1),
(193,5,'Trong các hành vi dưới đây, người lái xe mô tô có văn hóa giao thông phải ứng xử như thế nào?','Theo luật, người lái xe mô tô:+Phải đi đúng làn đường, chứ không được tùy ý chọn làn đường có ít phương tiện tham gia giao thông;+Phải đội mũ bảo hiểm bất cứ khi nào đi xe, chứ không phải chỉ đội “ở nơi có biển báo bắt buộc đội mũ bảo hiểm”Như vậy đáp án 1 sai. Đáp án 4 sai.Đáp án 2 và 3 đúng, vì đó là những hành vi tuân thủ luật và thể hiện có văn hóa giao thông.',1,1,1,1),
(194,5,'Trong các hành vi dưới đây, người lái xe mô tô có văn hóa giao thông phải ứng xử như thế nào?','Theo luật, người lái xe mô tô:+Phải đi đúng làn đường, chứ không được tùy ý chọn làn đường có ít phương tiện tham gia giao thông. Đáp án 2 sai.+Phải đội mũ bảo hiểm bất cứ khi nào đi xe, chứ không phải chỉ đội “ở nơi có biển báo bắt buộc đội mũ bảo hiểm”. Đáp án 4 sai.Đáp án 1 và 3 đúng, vì đó là những hành vi tuân thủ luật và thể hiện có văn hóa giao thông.',1,1,1,1),
(195,5,'Trong các hành vi dưới đây, người lái xe ô tô có văn hóa giao thông phải ứng xử như thế nào?','Theo quy định của luật giao thông, người lái xe mô tô phải tuân thủ:+Phải đi đúng làn đường, chứ không được tùy ý chọn làn đường có ít phương tiện tham gia giao thông. Đáp án 2 sai.+Chỉ được dừng đỗ xe ở nơi quy định, chứ không phải là “nơi thuận tiện”. Đáp án 3 sai.Đáp án 1 và 4 đúng, vì đó là những hành vi tuân thủ luật và thể hiện có văn hóa giao thông.',0,1,1,1),
(196,5,'Trong các hành vi dưới đây, người lái xe ô tô có văn hóa giao thông phải ứng xử như thế nào?','Theo quy định của luật giao thông, người lái xe phải tuân thủ:+Phải đi đúng làn đường, chứ không được tùy ý chọn làn đường có ít phương tiện tham gia giao thông. +Chỉ được dừng đỗ xe ở nơi quy định, chứ không phải là “nơi thuận tiện”.Như vậy thì đáp án 1 sai. Đáp án 4 sai.Đáp án 2 và 3 đúng, vì đó là những hành vi tuân thủ luật và thể hiện có văn hóa giao thông.',0,1,1,1),
(197,5,'Người có văn hóa giao thông khi điều khiển xe cơ giới tham gia giao thông đường bộ phải đảm bảo các điều kiện gì?','Người lái xe phải đảm bảo tất cả các điều kiện nêu trong các đáp án, đó là quy định của luật. Tất nhiên người có văn hóa giao thông đương nhiên phải tuân theo luật. Thế nên, cả 2 đáp án đều đúng.',1,1,1,1),
(198,5,'Khi xảy ra tai nạn giao thông, người lái xe và người có mặt tại hiện trường vụ tai nạn phải thực hiện các công việc gì dưới đây?','Người lái xe và người có mặt tại hiện trường phải thực hiện tất cả các công việc nêu trên. Đáp án đúng là cả 1 và 2.',0,1,1,1),
(199,5,'Khi xảy ra tai nạn giao thông, có người bị thương nghiêm trọng, người lái xe và người có mặt tại hiện trường vụ tai nạn phải thực hiện các công việc gì?','Với câu hỏi này, cả đáp án 1 và 3 đúng vì đó là những việc phải làm và cần làm.Đáp án 2 sai vì đó là hành vi trốn tránh trách nhiệm, hoặc không giữ nguyên hiện trường.',0,1,1,1),
(200,5,'Khi sơ cứu ban đầu cho người bị tai nạn giao thông đường bộ không còn hô hấp, người lái xe và người có mặt tại hiện trường vụ tai nạn phải thực hiện các công việc gì?','Khi sơ cứu cho người bị nạn, cần phải thực hiện tất cả các công việc nêu trong các đáp án. Vậy đáp án đúng là cả 1 và 2.',0,1,1,1),
(201,6,'Khi vào số để tiến hoặc lùi xe ô tô có số tự động, người lái xe phải thực hiện các thao tác nào để đảm bảo an toàn?','Với xe số tự động, khi vào số tiến hoặc lùi, cần phải đạp phanh + kiểm tra số. Do đó phương án 1 đúng.Phương án 2 sai, vì “tăng ga” khi vào số tiến lùi là không đảm bảo an toàn.',0,0,0,1),
(202,6,'Khi nhả phanh tay, người lái xe cần phải thực hiện các thao tác nào?','Khi nhả phanh tay, việc đầu tiên là phải “bóp khóa hãm”. Phương án 1 và 3 sai do không có thao tác này trước khi hạ phanh. Phương án 2 đúng.',0,0,0,1),
(203,6,'Khi khởi hành ô tô trên đường bằng, người lái xe cần thực hiện các thao tác nào theo trình tự dưới đây?','Cần phải đạp ly hợp (côn) hết hành trình mới vào được số 1. Đáp án 1 sai vì có nội dung sai và các ý sắp xếp lộn xộn. Đáp án 2 đúng.',0,0,0,1),
(204,6,'Khi quay đầu xe người lái xe cần phải quan sát và thực hiện các thao tác nào?','Khi quay đầu xe, cần phải đưa đầu xe về phía nguy hiểm để tài xế có thể quan sát, và xử lý tình huống tốt hơn. Đáp án 2 sai vì đưa đuôi xe về phía nguy hiểm. Đáp án 1 đúng.',0,0,0,1),
(205,6,'Khi tránh nhau trên đường hẹp, người lái xe cần phải chú ý đến những điểm nào?','Khi tránh nhau, không được bật đèn pha vì gây chói mắt cho xe đối diện, dễ mất an toàn. Đáp án 3 sai. Đáp án 1 và 2 đúng, vì gồm những ý mà lái xe cần chú ý khi tránh xe trên đường hẹp.',0,0,0,1),
(206,6,'Khi điều khiển ô tô lên dốc cao, người lái xe cần thực hiện các thao tác nào?','Kỹ thuật lên dốc cần đảm bảo: về số thấp từ chân dốc, và đến gần đỉnh dốc phải đi chậm. Đáp án 2 đúng.Đáp án 1 sai vì không về số thấp ngay từ chân dốc, và tăng tốc khi đến gần đỉnh dốc (không an toàn). Đáp án 3 sai.',0,0,0,1),
(207,6,'Khi điều khiển ô tô xuống dốc cao, người lái xe cần thực hiện các thao tác nào dưới đây?','Khi xuống dốc cần về số thấp để tăng lực hãm (phanh bằng động cơ). Có thể áp dụng nguyên tắc lên dốc bằng số nào thì xuống dốc bằng số đó, tất nhiên với giả định độ dốc lên ngang bằng với độ dốc xuống. Đáp án 2 đúng.Đáp án 1 sai vì “tăng số lên cao”, sẽ giảm lực phanh của động cơ, và do đó phải sử dụng phanh nhiều, dễ hỏng phanh và mất an toàn.Đáp án 3 sai vì “về số 0” cũng làm việc điều khiển xe mất chủ động, không an toàn.',0,0,0,1),
(208,6,'Khi xuống dốc, muốn dừng xe, người lái xe cân thực hiện các thao tác nào?','Muốn dừng xe khi đang xuống dốc, trước hết cần phải “đạp phanh” để giảm tốc. Sau đó “về số 1” vừa để hỗ trợ lực phanh bằng động cơ, vừa để giữ chủ động cho xe duy trì động lực tiến. Do đó, đáp án 1 đúng.Đáp án 2 sai vì thiếu thao tác “đạp phanh” cho xe đi chậm lại, và thiếu “về số 1” trước khi dừng.Đáp án 3 sai vì thiếu “về số 1” trước khi dừng.',0,0,0,1),
(209,6,'Khi điều khiển xe trên đường vòng người lái xe cần phải làm gì?','Khi lái xe chỗ đường vòng, ngoài việc quan sát và bật tín hiệu, thao tác quan trọng là “giảm tốc độ” và “về số thấp”. Đáp án 1 đúng.Đáp án 2 sai vì “tăng tốc” và không về số thấp. Đáp án 3 sai.',0,0,0,1),
(210,6,'Khi điều khiển xe ô tô rẽ phải ở chỗ đường giao nhau, người lái xe cần thực hiện các thao tác nào?','Khi cho xe rẽ phải, cần thực hiện các thao tác:Xi nhan phải trước khoảng 30 mét;Bám sát bên phải đường;Giảm tốc độ;Quan sát bên phải.Như vậy đáp án 2 đúng.Đáp án 1 sai vì cho xe sang làn đường “bên trái”, và không bật xi nhan trước 30 mét.Đáp án 3 sai vì cho xe “tăng tốc” và chỉ quan sát “bên trái”.',0,0,0,1),
(211,6,'Khi điều khiển xe ô tô rẽ trái ở chỗ đường giao nhau, người lái xe cần thực hiện các thao tác nào?','Khi cho xe rẽ trái, cần thực hiện các thao tác:Xi nhan trái trước 30 mét;Giảm tốc độ;Quan sát bên trái;Đổi sang làn bên trái.Như vậy đáp án 1 đúng.Đáp án 2 sai cho xe “tăng tốc”. Đáp án 3 sai vì đổi làn đường “sang phải”. Đáp án 4 sai vì liệt kê cả các phương án sai.',0,0,0,1),
(212,6,'Khi điều khiển xe vượt qua rãnh lớn cắt ngang mặt đường, người lái xe cần thực hiện các thao tác nào?','Khi xe qua rãnh lớn, cần phải gài số 1 để máy khỏe, không tăng số mà tăng ga cho bánh xe vượt lên. Như vậy đáp án 3 đúng.Đáp án 1 sai vì “tăng số”, đáp án 2 sai vì không “gài số 1” mà “tăng số”.',0,0,0,1),
(213,6,'Khi điều khiển xe qua đường sắt, người lái xe cần thực hiện các thao tác nào dưới đây?','Khi xe qua đường sắt, cần thực hiện tất cả các thao tác nêu trong các đáp án để đảm bảo an toàn. Đáp án đúng là cả 1 và 2.',0,0,0,1),
(214,6,'Khi điều khiển xe ô tô tự đổ, người lái xe cần chú ý những điểm gì?','Khi xe tự đổ chạy trên đường quốc lộ, cần phải hạ hết thùng xe xuống. Do đó, đáp án 2 sai.Đáp án 1 và 3 đều đúng, vì đó là những thao tác cần thực hiện để đảm bảo an toàn.',0,0,0,1),
(215,6,'Khi điều khiển tăng số, người lái xe ô tô cần chú ý những điểm gì?','Có câu "Tăng số thì phải lấy đà, giảm số thì phải vù ga mới vào". Nghĩa là, khi tăng số không được vù ga. Do đó, đáp án 1 đúng, đáp án 2 sai.',0,0,0,1),
(216,6,'Khi điều khiển xe giảm số, người lái xe cần chú ý đến những điểm gì?','Có câu "Tăng số thì phải lấy đà, giảm số thì phải vù ga mới vào". Việc vù ga (tăng ga) khi giảm số là để đảm bảo tính đồng tốc và ăn khớp tốt giữa các bánh răng chủ động và bị động.Đáp án 2 đúng, đáp án 1 sai.',0,0,0,1),
(217,6,'Để giảm tốc độ khi ô tô đi xuống đường dốc dài, người lái xe phải thực hiện các thao tác nào?','Khi xe xuống dốc, thao tác rất quan trọng là phải “về số thấp” kết hợp với đạp phanh.Phương án 1 sai vì không về số thấp, mà lại còn cắt côn (ly hợp) rất nguy hiểm. Phương án 3 sai vì “tăng lên số cao” thay vì về số thấp.Phương án 2 đúng.',0,0,0,1),
(218,6,'Điều khiển xe ô tô qua đoạn ngập nước, người lái xe cần thực hiện các thao tác nào?','Khi xe qua đường ngập nước, cần phải giữ tốc độ thấp, máy khỏe để tránh bị dừng chết máy, và hạn chế gây sóng xung quanh. Do đó cần các thao tác về số 1, giữ đều ga và giữ vững tay lái.Đáp án 3 đúng. Đáp án 1 và 2 đều sai vì “tăng ga” và không “về số 1”.Tham khảo thêm trong bài Cách lái xe qua đường ngập lụt',0,0,0,1),
(219,6,'Khi điều khiển xe ô tô tới gần xe chạy ngược chiều vào ban đêm, người lái xe cần thực hiện các thao tác nào?','Khi có xe chạy ngược chiều ban đêm, cần phải chuyển “đèn chiếu xa sang đèn chiếu gần” và “không nhìn thẳng vào đèn của xe chạy ngược chiều”. Đáp án 1 đúng.Đáp án 2 sai vì chuyển “đèn chiếu gần sang đèn chiếu xa” gây chói mắt tài xế đi đối diện. Đáp án 3 sai vì “nhìn thẳng vào đèn của xe chạy ngược chiều” sẽ bị chói mắt, không an toàn.',0,0,0,1),
(220,6,'Khi điều khiển xe ô tô trên đường trơn cần chú ý những điểm gì?','Khi đi đường trơn, ngoài việc dùng số thấp, đi chậm, đều ga, thì cần nhớ không “đánh lái ngoặt và phanh gấp”. Do đó đáp án 1 sai vì cho “đánh lái ngoặt và phanh gấp khi cần thiết”. Đáp án 2 đúng.',0,0,0,1),
(221,6,'Khi động cơ ô tô đã khởi động, bảng đồng hồ xuất hiện ký hiệu như hình vẽ dưới đây, báo hiệu tình trạng như thế nào của xe ô tô?','Trên bảng đồng hồ, hình chữ “Brake” báo hiệu phanh tay đang hãm, hình dấu chấm than trong vòng tròn cảnh báo thiếu dầu phanh.Như vậy đáp án đúng là cả 1 và 2. Đáp án 3 và 4 sai.',0,0,0,1),
(222,6,'Khi động cơ ô tô đã khởi động, bảng đồng hồ xuất hiện ký hiệu như hình vẽ dưới đây, báo hiệu tình trạng như thế nào của xe ô tô?','Hình bình đựng  + giọt chất lỏng này cảnh báo thiếu dầu bôi trơn. Bạn lưu ý tránh hiểu nhầm chất lỏng sang nước làm mát, hoặc nhiên liệu.Đáp án 4 đúng. Các đáp án còn lại đều sai.',0,0,0,1),
(223,6,'Khi động cơ ô tô đã khởi động, bảng đồng hồ xuất hiện ký hiệu như hình vẽ dưới đây, báo hiệu tình trạng như thế nào của xe ô tô?','Nhìn hình vẽ chiếc xe có 4 cửa đang mở, ta có thể thấy ý nghĩa: có cửa xe chưa đóng, hoặc đóng chưa chặt. Đáp án đúng là cả 1 và 2. Đáp án 3 và 4 sai.',0,0,0,1),
(224,6,'Khi động cơ ô tô đã khởi động, bảng đồng hồ xuất hiện ký hiệu như hình vẽ dưới đây, báo hiệu tình trạng như thế nào của xe ô tô?','Mặc dù không được giống lắm, nhưng có thể tưởng tượng hình vẽ phác họa người ngồi trên ghế với dây an toàn cài chéo qua. Đây là cảnh báo người ngồi hàng ghế trước “chưa thắt dây an toàn”.Đáp án 3 đúng. Các đáp án còn lại đều sai.',0,0,0,1),
(225,6,'Khi động cơ ô tô đã khởi động, bảng đồng hồ xuất hiện ký hiệu như hình vẽ dưới đây, báo hiệu tình trạng như thế nào của xe ô tô?','Hình cột bơm xăng này cảnh báo “sắp hết nhiên liệu”. Đáp án 4 đúng. Các đáp án còn lại đều sai.',0,0,0,1),
(226,6,'Khi đã đỗ xe ô tô sát lề đường bên phải, người lái xe phải thực hiện các thao tác nào dưới đây khi mở cửa xuống xe để đảm bảo an toàn?','Trước khi mở cửa xe, cần phải quan sát cẩn trọng, nhất là phía sau, để tránh gây tai nạn cho xe đang đi vượt lên. Đáp án 1 đúng.Đáp án 2 và 3 đều sai, do thiếu quan sát “phía sau”. Đáp án 3 còn thiếu “mở hé cửa”, mà mở hết cửa ngay thì rất nguy hiểm cho xe đi cùng chiều đang vượt lên.',0,0,0,1),
(227,6,'Khi lái xe ô tô qua đường sắt không có rào chắn, không có người điều khiển giao thông, người lái xe phải xử lý như thế nào?','Khi lái xe qua đường sắt không có rào chắn hay người điều khiển giao thông, cần những thao tác quan trọng như: “hạ cửa kính” để quan sát, “tắt thiết bị âm thanh” để lắng nghe xem có tiếng còi tàu đang tới hay không. Nếu không có tàu, thì về số thấp cho xe từ từ chạy qua đường sắt.Như vậy đáp án 1 đúng.Đáp án 2 sai vì không có “hạ cửa kính”, tắt âm thanh, mà còn “tăng tốc”, không đảm bảo an toàn. Đáp án 3 cũng sai vì liệt kê cả đáp án 2.',0,0,0,1),
(228,6,'Khi ô tô có hộp số tự động đi trên đường trơn trượt, lầy lội hoặc dốc cao và dài, người lái xe để cần số ở vị trí nào?','Khi đi đường trơn, xuống dốc dài, cần đảm bảo cho xe ở số thấp với lực máy khỏe. Với xe số tự động, thì số L hoặc số 2 là phù hợp. Xem thêm ý nghĩa các số tự động tại đây.Đáp án 2 đúng, đáp án 1 và 3 đều sai (với D: số tiến tự động; và N: số mo).',0,0,0,1),
(229,6,'Khi động cơ ô tô đã khởi động, muốn điều chỉnh ghế của người lái, người lái xe phải điều chỉnh cần số ở vị trí nào?','Khi điều chỉnh ghế lái, rất dễ vô ý đạp vào chân ga. Do đó, cần về số P (đỗ) hoặc N (mo) để đảm bảo an toàn.Đáp án 1 đúng. Đáp án 2 và 3 sai.',0,0,0,1),
(230,6,'Khi điều khiển ô tô có sô tự động, người lái xe sử dụng chân như thế nào là đúng?','Khi lái xe số tự động, tuyệt đối không dùng chân trái vào bất cứ việc gì, để tránh cho chân phải đạp nhầm chân ga rất nguy hiểm. Đáp án 1 đúng.Đáp án 2 và 3 đều sai vì cho chân trái “đạp phanh”.',0,0,0,1),
(231,6,'Trong các trường hợp dưới đây, để đảm bảo an toàn giao thông người lái xe điều khiển xe như thế nào khi tầm nhìn bị hạn chế bởi sương mù?','Để giữ an toàn khi lái xe trong sương mù, cần phải “giảm tốc độ” và chạy “cách xa xe trước”. Đáp án 2 đúng.Đáp án 1 và 3 sai, do cho xe “tăng tốc độ”, “chạy gần xe trước”.',0,0,0,1),
(232,6,'Khi đèn pha của xe đi ngược chiều gây chói mắt, làm giảm khả năng quan sát trên đường người lái xe xử lý như thế nào?','Khi bị đèn xe ngược chiều gây chói mắt, để giữ an toàn thì bạn cần giảm tốc độ, và dừng lại nếu cần thiết. Đáp án 1 đúng.Đáp án 2 sai do “bật đèn pha xa” thì gây chói mắt cho xe đối diện, mất an toàn. (Ghi chú: rất tiếc là nhiều người bất đắc dĩ áp dụng cách này trên thực tế, vì xe đối diện cứ rọi thẳng pha làm khó chịu người đi ngược đường)',0,0,0,1),
(233,6,'Để đạt được hiệu quả phanh cao nhất, người lái xe mô tô phải sử dụng các kỹ năng như thế nào?','Để phanh hiệu quả nhất, cần kết hợp “giảm hết ga” và sử dụng “cả phanh sau và phanh trước”. Đáp án đúng là cả 3 và 4. Đáp án 1 và 2 sai do chỉ dùng 1 phanh thì chưa hiệu quả tối đa.',0,0,0,1),
(234,6,'Khi lái xe ô tô trên đường có nhiều “ổ gà”, người lái xe phải thực hiện thao tác như thế nào?','Đường nhiều ổ gà sẽ gây sóc, dễ mất lái, do đó cần “giảm tốc độ, về số thấp và giữ đều ga” để đảm bảo an toàn.Đáp án 1 đúng. Đáp án 2 sai do “cho xe tăng tốc”.',0,0,0,1),
(235,6,'Khi điều khiển ô tô gặp mưa to hoặc sương mù, người lái xe phải làm gì?','Khi lái xe trong trời mưa to hoặc sương mù, lái xe thường bị lóa mắt và quan sát hạn chế. Do đó, cần đi chậm, bật đèn chiếu gần (đèn cốt) và đèn vàng, bật cần gạt nước… để cải thiện tầm quan sát. Đáp án 1 đúng.Đáp án 2 cũng đúng, nhưng có lẽ nên thêm ý “nếu cần thiết”. Nghĩa là chỉ khi mưa quá to, hoặc sương mù quá dày đặc thì mới cần “dừng xe, bật đèn khẩn cấp”.Đáp án 3 sai, vì “bật đèn chiếu xa “ (đèn pha) sẽ gây lóa cho cả bạn và xe ngược chiều.',0,0,0,1),
(236,6,'Xe ô tô tham gia giao thông đường bộ phải bảo đảm các quy định về chất lượng, an toàn kỹ thuật và bảo vệ môi trường nào ghi dưới đây?','Xe ô tô phải đảm bảo tất cả các quy định trong cả 2 đáp án. Đó là theo Điều 53.1 Luật giao thông đường bộ 2008.Đáp án đúng là cả 1 và 2.',0,0,0,1),
(237,6,'Xe ô tô tham gia giao thông trên đường bộ phải bắt buộc có đủ bộ giảm thanh, giảm khói không?','Xe ô tô phải có đủ bộ phận giảm thanh, giảm khói, theo quy định tại Điều 53.1i - Luật giao thông đường bộ 2008.Đáp án 2 đúng, đáp án 1 sai.',0,0,0,1),
(238,6,'Ô tô tham gia giao thông trên đường bộ phải có đủ bộ các loại đèn gì?','Điều 53d Luật giao thông đường bộ 2008 quy định: xe ô tô phải có đủ các loại đèn chiếu gần, chiếu xa, soi biển số, báo hãm, tín hiệu; nhưng không cần phải có “dàn đèn pha trên nóc xe”.Do đó, đáp án 1 và 2 đúng. Đáp án 3 sai.',0,0,0,1),
(239,6,'Hãy nêu yêu cầu của kính chắn gió ô tô?','Kính chắn gió ô tô phải đảm bảo là loại “kính an toàn”, “kính nhiều lớp”... Đáp án 1 đúng, đáp án 2 và 3 sai.',0,0,0,1),
(240,6,'Hãy nêu yêu cầu về an toàn kỹ thuật đối với bánh xe lắp cho ô tô?','Tất cả các ý nêu trong 2 đáp án đều là yêu cầu về an toàn kỹ thuật với bánh xe ô tô. Vì vậy, đáp án đúng là cả 1 và 2',0,0,0,1),
(241,6,'Âm lượng của còi điện lắp trên ô tô (đo ở độ cao 1,2m với khoảng cách 2m tính từ đầu xe) là bao nhiêu?','Theo quy định tại Phụ lục 1 của Thông tư 10/2009/TT-BGTVT, âm lượng của còi điện trên xe ô tô phải từ 90 dB(A) đến 115 dB(A).Do đó, đáp án 1 đúng, đáp án 2 và 3 sai.',0,0,0,1),
(242,6,'Mục đích của bảo dưỡng thường xuyên đối với xe ô tô có tác dụng gì?','Rõ ràng, việc bảo dưỡng xe thường xuyên sẽ giúp: tính năng kỹ thuật tốt, giảm hao mòn, ngăn ngừa hư hỏng, và giữ hình thức bên ngoài.Đáp án đúng là cả 1 và 2.THOÁT CÂU TIẾP ',0,0,0,1),
(243,6,'Trong các nguyên nhân nêu dưới đây, nguyên nhân nào làm động cơ điezen không nổ?','Đáp án 1 đúng do liệt kê những nguyên nhân làm cho động cơ diesel không nổ.Đáp án 2 và 3 sai, do liệt kê cả “tia lửa điện”, “tia điện”. Thực tế, động cơ diesel nổ bằng cách nén hòa khí ở áp suất cao, chứ không dùng bugi đánh tia lửa điện như động cơ xăng.',0,0,0,1),
(244,6,'Hãy nêu yêu cầu về an toàn kỹ thuật đối với gạt nước lắp trên ô tô?','Gạt nước phải đảm bảo: đủ số lượng, hoạt động bình thường, lưỡi gạt còn tốt, diện tích quét đảm bảo… Đáp án đúng là cả 1 và 2.',0,0,0,1),
(245,6,'Hãy nêu yêu cầu về an toàn kỹ thuật đối với dây đai an toàn lắp trên ô tô?','Dây an toàn phải có cơ cấu hãm “giữ chặt dây khi giật dây đột ngột”. Như vậy khi chẳng may xe va chạm, dây mới giữ được an toàn. Đáp án 1 đúng.Đáp án 2 sai do “cơ cấu hãm mở ra khi giật dây đột ngột”. Nếu cơ cấu hãm mở như vậy thì người thắt dây sẽ gặp nguy hiểm.',0,0,0,1),
(246,6,'Thế nào là động cơ 4 kỳ?','Động cơ 4 kỳ thì mỗi lần sinh công cần thực hiện 4 hành trình. Vậy đáp án 2 đúng, đáp án 1 sai.',0,0,0,1),
(247,6,'Thế nào là động cơ 2 kỳ?','Động cơ 2 kỳ thì mỗi lần sinh công cần thực hiện 2 hành trình. Đáp án 1 đúng, đáp án 2 sai.',0,0,0,1),
(248,6,'Niên hạn sử dụng của ô tô tải (tính bắt đầu từ năm sản xuất) là bao nhiêu năm?','Theo quy định tại Điều 4 - Nghị định số 95/2009/NĐ-CP, niên hạn sử dụng xe tải là 25 năm. Đáp án 3 đúng, đáp án 1 và 2 đều sai.',0,0,0,1),
(249,6,'Niên hạn sử dụng của ô tô chở người trên 9 chỗ ngồi (tính bắt đầu từ năm sản xuất) là bao nhiêu năm?','Theo quy định tại Điều 4 - Nghị định số 95/2009/NĐ-CP, niên hạn sử dụng của ô tô chở người nói chung là 20 năm. Đáp án 2 đúng, đáp án 1 và 3 sai.',0,0,0,1),
(250,6,'Hãy nêu công dụng của động cơ ô tô?','Động cơ ô tô chuyển đổi nhiệt năng (của nhiên liệu cháy) thành cơ năng thì xe mới chuyển động được. Đáp án 1 đúng.Đáp án 2 sai vì nói ngược: cơ năng thành nhiệt năng.',0,0,0,1),
(251,6,'Hãy nêu công dụng hệ thống truyền lực của ô tô?','Hệ thống truyền lực truyền mô men quay từ động cơ tới bánh xe. Đáp án 1 đúng.Đáp án 2 là công dụng của hệ thống lái. Đáp án 3 là công dụng của hệ thống phanh. Cả đáp án 2 và 3 đều sai.',0,0,0,1),
(252,6,'Hãy nêu công dụng ly hợp của ô tô?','Ly hợp ô tô (còn gọi là côn) có tác dụng truyền hoặc ngắt truyền động. Đáp án 2 đúng.Đáp án 1 sai vì đó là công dụng của hộp số. Đáp án 3 sai vì đó la công dụng của hệ thống truyền lực.',0,0,0,1),
(253,6,'Hãy nêu công dụng hộp số của ô tô?','Hộp số có tác dụng "truyền, thay đổi mô men và cắt truyền động từ động cơ đến bánh xe chủ động", và "đảm bảo cho ô tô chuyển động lùi". Đáp án 3 đúng. Đáp án 1 và 2 sai, vì thiếu “cắt truyền động” và “chuyển động lùi”.',0,0,0,1),
(254,6,'Hãy nêu công dụng hệ thống lái của ô tô?','Hệ thống lái giúp xe “đổi hướng” hoặc giữ “hướng xác định”. Đáp án 3 đúng. Đáp án 1 sai vì đó là công dụng của hệ thống truyền lực. Đáp án 2 sai, vì đó là công dụng của hộp số.',0,0,0,1),
(255,6,'Hãy nêu công dụng hệ thống phanh của ô tô?','Hệ thống phanh giúp xe “giảm tốc” hoặc “dừng chuyển động”, vàgiữ xe đứng yên trên dốc. Đáp án 1 đúng. Đáp án 2 sai vì liên quan đến hệ thống lái. Đáp án 3 sai vì đó liên quan đến công dụng của hộp số.',0,0,0,1),
(256,7,'Biển nào cấm người đi bộ?','Biển báo cấm thường hình tròn, viền đỏ, nền trắng, hình vẽ màu đen. Do đó biển báo cấm người đi bộ là Biển 2, do đó đáp án 3 đúng. Biển 1 là biển báo đường người đi bộ cắt ngang. Biển 3 là biển báo đường dành cho người đi bộ. Do đó các đáp án 1, 2 và 4 đều sai.',1,1,1,1),
(257,7,'Gặp biển nào người lái xe phải nhường đường cho người đi bộ?','Biển 1 cảnh báo có đường người đi bộ cắt ngang: gặp biển này các loại xe cộ phải nhường đường cho người đi bộ. Đây là loại biển báo nguy hiểm có dạng hình tam giác đều - với 1 đỉnh hướng lên trên, viền ngoài màu đỏ, nền màu vàng nhạt, hình màu đen (trừ biển giao nhau với đường ưu tiên). Biển 2 cấm người đi bộ. Biển 3 báo hiệu đường dành cho người đi bộ, các phương tiện không được vào. Như vậy đáp án 1 đúng (Biển 1). Các đáp án còn lại đều sai.',1,1,1,1),
(258,7,'Biển nào chỉ đường dành cho người đi bộ, các loại xe không được đi vào khi gặp biển này?','Biển 1 cảnh báo có đường người đi bộ cắt ngang, các loại xe cộ phải nhường đường cho người đi bộ. Biển 2 cấm người đi bộ. Biển 3 báo hiệu đường dành cho người đi bộ, các phương tiện không được vào. Đây là loại Biển hiệu lệnh, có dạng hình tròn, nền màu xanh lam, hình và chữ số màu trắng (trừ biển hết hạn chế tốc độ tối thiểu và hướng đi phải theo cho các xe chở hàng nguy hiểm). Như vậy biển 3 là biển đúng với câu hỏi. Đáp án 3 đúng. Các đáp án còn lại đều sai.',1,1,1,1),
(259,7,'Biển nào cấm các loại xe cơ giới đi vào, trừ xe gắn máy, mô tô hai bánh và các loại xe ưu tiên theo luật định?','Để trả lời câu hỏi này, bạn cần hiểu được ý nghĩa của 3 biển báo cấm trong hình vẽ.Biển 1 - "Cấm ô tô": cấm tất cả các loại xe cơ giới, trừ môtô hai bánh, xe gắn máy và các xe được ưu tiên theo quy định. Do trên hình vẽ ô tô con, bạn lưu ý tránh hiểu nhầm là biển này chỉ cấm ô tô con (4-5 chỗ).Biển 2 - "Cấm ô tô khách và ô tô tải":  cấm ôtô chở khách và các loại ôtô tải.Biển 3 - "Cấm ô tô, máy kéo kéo moóc hoặc sơ mi rơ moóc": cấm tất cả các loại xe cơ giới kéo theo rơ-moóc.Như vậy, Biển 1 là biển cần tìm. Đáp án 1 đúng.Tham khảo thêm nội dung Các loại biển báo cấm.',0,1,1,1),
(260,7,'Biển nào cấm ô tô tải?','Để trả lời câu hỏi này, bạn cần hiểu được ý nghĩa của 3 biển báo cấm trong hình vẽ.Biển 1 - "Cấm ô tô": cấm tất cả các loại xe cơ giới, đương nhiên cấm cả ô tô tải.Biển 2 - "Cấm ô tô tải": rõ ràng là biển này cấm xe tải.Biển 3 - "Cấm máy kéo": cấm tất cả các loại máy kéo, nhưng không cấm xe tải.Như vậy, biển 1 và 2 đều đúng câu hỏi. Đáp án 4 đúng, các đáp án còn lại sai.Tham khảo thêm Nội dung các biển báo cấm.',0,1,1,1),
(261,7,'Biển nào cấm máy kéo?','Để trả lời câu hỏi này, bạn cần hiểu được ý nghĩa của 3 biển báo cấm trong hình vẽ.Biển 1 - “Cấm mô tô”: cấm tất cả các loại môtô.Biển 2 - "Cấm ô tô tải": cấm tất cả các loại ôtô tải (từ 1,5 tấn trở lên). Lưu ý, biển này cấm cả máy kéo và các xe máy chuyên dùng (nội dung này rất dễ bị bỏ qua!).Biển 3 - "Cấm máy kéo": cấm tất cả các loại máy kéo, nhưng không cấm xe tải.Như vậy, biển 2 và 3 đều đúng câu hỏi. Đáp án 1 đúng, các đáp án còn lại sai.Tham khảo thêm Ý nghía của các biển báo cấm.',0,1,1,1),
(262,7,'Biển nào báo hiệu sắp đến chỗ giao nhau nguy hiểm?','Đây là nhóm các biển báo nguy hiểm, bạn cần hiểu ý nghĩa:Biển 1 - "Đường hai chiều": sắp đến đoạn đường hai chiều, đường đôi tạm thời.Biển 2 - "Giao nhau với đường ưu tiên": sắp đến nơi giao nhau với đường ưu tiên.Biển 3 - “Giao nhau có tín hiệu đèn": sắp đến nơi giao nhau có đèn giao thông.Như vậy, biển 2 và 3 báo hiệu sắp đến chỗ giao nhau nguy hiểm. Đáp án 2 đúng. Đáp án còn lại sai.Tham khảo thêm Nội dung các biển báo nguy hiểm.',1,1,1,1),
(263,7,'Biển nào báo hiệu sắp đến chỗ giao nhau với đường sắt có rào chắn?','Câu này khá dễ. Bạn nhìn biển báo có hàng rào là có thể đoán được ý nghĩa rồi. Tuy vậy, cũng nên hiểu nội dung từng biển cho chắc chắn:Biển 1 - “Giao nhau với đường sắt có rào chắn”: báo trước sắp đến chỗ giao nhau giữa đường bộ và đường sắt có rào chắnBiển 2 - "Giao nhau với đường ưu tiên": sắp đến nơi giao nhau với đường ưu tiên.Biển 3 - “Giao nhau có tín hiệu đèn": sắp đến nơi giao nhau có đèn giao thông.Như vậy đáp án 1 đúng, đáp án 2 và 3 sai.Tham khảo thêm Ý nghĩa các biển báo nguy hiểm.',1,1,1,1),
(264,7,'Biển nào báo hiệu giao nhau có tín hiệu đèn?','Chỉ cần nhìn Biển 3 có hình đèn xanh đỏ, là biết biển này báo hiệu đèn giao thông. Nhưng cũng nên xem ý nghĩa của từng biển:Biển 1 - “Giao nhau với đường sắt có rào chắn”: báo trước sắp đến chỗ giao nhau giữa đường bộ và đường sắt có rào chắnBiển 2 - "Giao nhau với đường ưu tiên": sắp đến nơi giao nhau với đường ưu tiên.Biển 3 - “Giao nhau có tín hiệu đèn": sắp đến nơi giao nhau có đèn giao thông.Vậy Đáp án 3 đúng, đáp án 1 và 2 sai.Tham khảo thêm Nội dung các biển báo nguy hiểm.',1,1,1,1),
(265,7,'Biển nào báo hiệu nguy hiểm giao nhau với đường sắt?','Hãy cùng xem ý nghĩa từng biển:Biển 1 - “Giao nhau với đường sắt có rào chắn”: báo trước sắp đến chỗ giao nhau giữa đường bộ và đường sắt có rào chắnBiển 2 - “Giao nhau với đường hai chiều": báo trước sắp đến vị trí giao nhau với đường hai chiềuBiển 3 - “Nơi đường sắt giao vuông góc với đường bộ": chỉ chỗ giao nhau với đường sắt không có rào chắn với 1 đường sắt.Đáp án 1 đúng (Biển 1 và 3). Đáp án còn lại đều sai.Tham khảo thêm Nội dung các biển báo nguy hiểm.',1,1,1,1),
(266,7,'Biển báo nào báo hiệu đường sắt giao nhau với đường bộ không có rào chắn?','Ý nghĩa từng biển như sau:Biển 1 - “Giao nhau với đường sắt có rào chắn”: báo trước sắp đến chỗ giao nhau với đường sắt có rào chắnBiển 2 - “Giao nhau với đường sắt không có rào chắn": báo trước sắp đến chỗ giao nhau với đường sắt không có rào chắnBiển 3 - “Nơi đường sắt giao vuông góc với đường bộ": chỉ chỗ giao nhau với đường sắt không có rào chắn với 1 đường sắt.Như vậy Biển 2 và 3 có nội dung như câu hỏi. Vậy đáp án 2 đúng. Các đáp án còn lại sai.Tham khảo thêm Nội dung các biển báo nguy hiểm.',1,1,1,1),
(267,7,'Biển nào báo hiệu cửa chui?','Những biển này có hình dáng hơi na ná giống nhau, nhưng ý nghĩa khác nhau:Biển 1 - “Cầu vồng”: sắp đến công trình có độ vồng lớn ảnh hưởng tới tầm nhìn.Biển 2 - “Cửa chui”: sắp đến đường có cổng chắn ngang (kiểu cổng giống như đường hầm, cổng thành, cầu vượt đường bộ dạng cầu vòm v.v…).Biển 3 - “Đường hầm”: chuẩn bị đi vào hầm đường bộ.Như vậy, Đáp án 2 đúng. Đáp án 1 và 3 sai.Tham khảo thêm Nội dung ý nghĩa các biển báo nguy hiểm.',0,1,1,1),
(268,7,'Biển nào báo hiệu "Hết đoạn đường ưu tiên"?','Ý nghĩa từng biển như sau:Biển 1 - “Giao nhau với đường không ưu tiên": báo trước sắp đến nơi giao nhau với đường không ưu tiênBiển 2 - "Giao nhau với đường ưu tiên": sắp đến nơi giao nhau với đường ưu tiên.Biển 3 “Hết đoạn đường ưu tiên”: Báo hiệu hết đoạn đường được ưu tiên.Như vậy biển 3 trả lời đúng câu hỏi. Đáp án 1 đúng. Đáp án 2 và 3 sai.Lưu ý: Trong câu này, Biển 1 và 2 thuộc nhóm Biển báo nguy hiểm, còn Biển 3 thuộc nhóm Biển chỉ dẫn.',1,1,1,1),
(269,7,'Biển nào báo hiệu, chỉ dẫn xe đi trên đường này được quyền ưu tiên qua nơi giao nhau?','Xe chạy trên đường ưu tiên sẽ được quyền ưu tiên ki qua nơi giao nhau. Trong khi đó, ý nghĩa từng biển như sau:Biển 1 - “Giao nhau với đường không ưu tiên", nghĩa là bạn đang trên đường ưu tiênBiển 2 - "Giao nhau với đường ưu tiên", nghĩa là bạn đang trên đường không ưu tiên.Biển 3 “Bắt đầu đường ưu tiên”: xe đi trên đường có đặt biển này được ưu tiên đi trước.Rõ ràng biển 1 và 3 báo hiệu xe được ưu tiên qua nơi giao nhau. Đáp án 2 đúng. Các đáp án kia sai.',1,1,1,1),
(270,7,'Biển nào báo hiệu giao nhau với đường không ưu tiên?','Trước hết cần hiểu ý nghĩa từng biển:Biển 1 - “Giao nhau với đường không ưu tiên": báo trước sắp đến nơi giao nhau với đường không ưu tiên.Biển 2 - "Giao nhau với đường ưu tiên": sắp đến nơi giao nhau với đường ưu tiên.Biển 3 “Bắt đầu đường ưu tiên”.Như vậy Biển 1 trả lời cho câu hỏi, và đáp án 1 đúng. Các đáp án còn lại sai.',1,1,1,1),
(271,7,'Biển nào báo hiệu giao nhau với đường ưu tiên?','Trước hết cần hiểu ý nghĩa từng biển:Biển 1 - “Giao nhau với đường không ưu tiên": báo trước sắp đến nơi giao nhau với đường không ưu tiên.Biển 2 - "Giao nhau với đường ưu tiên": sắp đến nơi giao nhau với đường ưu tiên.Biển 3 “Bắt đầu đường ưu tiên”.Như vậy Biển 2 trả lời cho câu hỏi, và do đó đáp án 2 đúng. Các đáp án còn lại sai.',1,1,1,1),
(272,7,'Biển nào báo hiệu nguy hiểm đường bị hẹp?','Biển 1 báo hiệu đường hẹp cả 2 bên. Biển 2 báo hiệu đường hẹp phía trái. Biển 3 báo hiệu đường ngoặt nguy hiểm, hướng ngoặt phải. Vậy biển 1 và 2 trả lời cho câu hỏi này, và đáp án 4 đúng. Các đáp án còn lại sai. Tìm hiểu chi tiết về các Biển báo nguy hiểm.',0,1,1,1),
(273,7,'Khi gặp biển nào, người lái xe phải giảm tốc độ, chú ý xe đi ngược chiều, xe đi ở phía đường bị hẹp phải nhường đường cho xe đi ngược chiều?','Ý nghĩa từng biển như sau: Biển 1 báo hiệu hết đường đôi. Biển 2 báo hiệu đường hẹp phía bên trái. Biển 3 báo hiệu đường hẹp phía bên phải. Biển 2 và 3 trả lời cho câu hỏi, và đáp án đúng là 2. Các đáp án còn lại sai. Tìm hiểu chi tiết về Biển báo nguy hiểm.',0,1,1,1),
(274,7,'Biển nào báo hiệu giao nhau với đường ưu tiên?','Biển 1 báo hiệu giao nhau giữa các tuyến đường cùng cấp. Biển 2 báo hiệu giao nhau với đường không ưu tiên. Còn biển 3 báo hiệu giao nhau với đường ưu tiên (bạn đang trên đường không ưu tiên).  Vậy đáp án 2 đúng (Biển 3). Các đáp án còn lại đều sai. Tìm hiểu chi tiết về Biển báo nguy hiểm.',1,1,1,1),
(275,7,'Biển nào báo hiệu "Đường giao nhau" của các tuyến đường cùng cấp?','Biển 1 báo hiệu giao nhau giữa các tuyến đường cùng cấp. Biển 2 báo hiệu giao nhau với đường không ưu tiên. Còn biển 3 báo hiệu giao nhau với đường ưu tiên.  Vậy đáp án 1 đúng (Biển 2). Các đáp án 2 và 3 đều sai. Tìm hiểu chi tiết về Biển báo nguy hiểm.',1,1,1,1),
(276,7,'Biển nào báo hiệu giao nhau với đường không ưu tiên?','Biển 1 báo hiệu giao nhau giữa các tuyến đường cùng cấp. Biển 2 báo hiệu giao nhau với đường không ưu tiên (bạn đang ở trên đường ưu tiên). Còn biển 3 báo hiệu giao nhau với đường ưu tiên.  Vậy đáp án 2 đúng (Biển 2). Các đáp án 1 và 3 đều sai. Tìm hiểu chi tiết về Biển báo nguy hiểm.',1,1,1,1),
(277,7,'Biển nào báo hiệu đường ưu tiên?','Biển 1 báo hiệu sắp đến đoạn đường 2 chiều. Biển 2 báo hiệu phải nhường đường cho xe cơ giới đi ngược chiều qua đường hẹp. Biển 3 là biển báo được ưu tiên đi trước qua đường hẹp. Vậy biển 1 trả lời cho câu hỏi này, và đáp án 1 đúng. Đáp án 2 và 3 sai. Ghi chú: biển 2 và 3 dễ nhầm với nhau, bạn có thể nhớ thế này: chiều mũi tên đỏ sẽ phải nhường đường cho chiều kia.',1,1,1,1),
(278,7,'Biển nào báo hiệu phải giảm tốc độ, nhường đường cho xe cơ giới đi ngược chiều qua đường hẹp?','Biển 1 báo hiệu sắp đến đoạn đường 2 chiều. Biển 2 báo hiệu phải nhường đường cho xe cơ giới đi ngược chiều qua đường hẹp. Biển 3 là biển báo được ưu tiên đi trước qua đường hẹp. Vậy biển 2 trả lời cho câu hỏi này, và đáp án 2 đúng. Đáp án 1 và 3 sai. Ghi chú: biển 2 và 3 dễ nhầm với nhau, bạn có thể nhớ thế này: chiều mũi tên đỏ sẽ phải nhường đường cho chiều kia.',1,1,1,1),
(279,7,'Biển nào chỉ dẫn được ưu tiên qua đường hẹp?','Biển 1 báo hiệu sắp đến đoạn đường 2 chiều. Biển 2 báo hiệu phải nhường đường cho xe cơ giới đi ngược chiều qua đường hẹp. Biển 3 là biển báo được ưu tiên đi trước qua đường hẹp. Vậy biển 3 trả lời cho câu hỏi này, và đáp án 3 đúng. Đáp án 1 và 2 sai. Ghi chú: biển 2 và 3 dễ nhầm với nhau, bạn có thể nhớ mẹo một chút: chiều mũi tên đỏ sẽ phải nhường đường cho chiều kia.',1,1,1,1),
(280,7,'Biển nào báo hiệu cấm xe mô tô ba bánh đi vào?','Hãy xem ý nghĩa từng biển:Biển 1 cấm mô tô, gồm cả loại 2 hoặc 3 bánhBiển 2 cấm ô tô, nhưng cũng cấm cả môtô 3 bánh có thùng đi qua, trừ môtô hai bánhBiển 3 cấm ô tô tảiNhư vậy cả biển 1 và 2 đều cấm mô tô 3 bánh. Vậy đáp án 2 đúng, đáp án 1 và 3 sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',1,1,1,1),
(281,7,'Biển nào báo hiệu cấm xe mô tô hai bánh đi vào?','Hãy xem ý nghĩa từng biển:Biển 1 cấm mô tô, gồm cả loại 2 hoặc 3 bánhBiển 2 cấm ô tô, nhưng cũng cấm cả môtô 3 bánh có thùng đi qua, trừ môtô hai bánhBiển 3 cấm máy kéoNhư vậy, chỉ có biển 1 cấm mô tô 2 bánh. Đáp án 1 đúng. Đáp án 2 và 3 sai.',1,1,1,1),
(282,7,'Khi gặp biển nào thì xe mô tô hai bánh được đi vào?','Hãy cùng xem ý nghĩa từng biển:Biển 1 cấm mô tô, gồm cả loại 2 hoặc 3 bánhBiển 2 cấm ô tô, nhưng cũng cấm cả môtô 3 bánh có thùng đi qua, trừ môtô hai bánhBiển 3 cấm ô tô tảiDo biển 2 và 3 không cấm mô tô 2 bánh, đáp án 3 đúng. Các đáp án còn lại đều sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',1,1,1,1),
(283,7,'Biển nào xe mô tô hai bánh được đi vào?','Cả 3 biển này đều là biển “Đường cấm”, nhưng khác nhau ở biển phụ hình chữ nhật nằm phía dưới. Hãy xem ý nghĩa từng biển nhé:Biển 1: Cấm ô tô và mô tô 3 bánh, nhưng không cấm mô tô 2 bánh.Biển 2: Cấm mô tô 2 bánh, 3 bánhBiển 3: Cấm ô tô tảiNhư vậy, chỉ có biển 2 cấm mô tô 2 bánh, nghĩa là với biển 1 và 3 thì mô tô 2 bánh được đi vào. Đáp án 2 đúng, đáp án 1 và 3 sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',1,1,1,1),
(284,7,'Biển nào xe mô tô hai bánh không được đi vào?','Cả 3 biển này đều là biển “Đường cấm”, nhưng khác nhau ở biển phụ hình chữ nhật nằm phía dưới. Hãy xem ý nghĩa từng biển nhé:Biển 1: Cấm ô tô và mô tô 3 bánh, nhưng không cấm mô tô 2 bánh.Biển 2: Cấm mô tô 2 bánh, 3 bánhBiển 3: Cấm ô tô tảiNhư vậy, chỉ có biển 2 cấm mô tô 2 bánh, nghĩa là với biển 1 và 3 thì mô tô 2 bánh được đi vào. Đáp án 2 đúng, đáp án 1 và 3 sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',1,1,1,1),
(285,7,'Biển này có ý nghĩa hiệu lực như thế nào?','Cả 3 biển này đều kèm biển phụ. Biển phụ thể hiện loại xe bị cấm. Nghĩa là, 3 biển này cấm các loại xe ở biển phụ đi vào.Đáp án 1 đúng. Đáp án 2 sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm và Các loại biển báo phụ.',0,1,1,1),
(286,7,'Biển nào cho phép ô tô con được vượt?','Trước hết cần hiểu ý nghĩa từng biển:Biển 1: Hết cấm vượt (nghĩa là được vượt).Biển 2: Cấm xe cơ giới vượt nhau (đương nhiên cấm ô tô con vượt)Biển 3: Cấm ô tô tải vượt, nhưng không cấm ô tô con vượtVậy thì biển 1 và 3 cho phép ô tô con đươc vượt. Đáp án 4 đúng. Các đáp án còn lại sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',1,1,1,1),
(287,7,'Biển nào không cho phép ô tô con vượt?','Trước hết cần hiểu ý nghĩa từng biển:Biển 1: Hết cấm vượt (cho phép ô tô con vượt).Biển 2: Cấm xe cơ giới vượt nhau (đương nhiên không cho phép ô tô con vượt)Biển 3: Cấm ô tô tải vượt, nhưng cho phép ô tô con vượtNhư vậy, chỉ có biển 2 không cho phép ô tô con vượt. Đáp án 1 đúng. Đáp án 2 và 3 sai. Lưu ý: câu này hơi “bẫy” về việc để Biển 2 ở Đáp án 1, dễ chọn nhầm.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',0,1,1,1),
(288,7,'Biển nào cấm ô tô tải vượt?','Trước hết cần hiểu ý nghĩa từng biển:Biển 1: Hết cấm vượt (nghĩa là không cấm ô tô tải vượt).Biển 2: Cấm xe cơ giới vượt nhau (đương nhiên cấm ô tô tải vượt)Biển 3: Cấm ô tô tải vượtNhư vậy, Biển 2 và 3 đều cấm ô tô tải vượt. Đáp án 4 đúng. Các đáp án còn lại đều sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',0,1,1,1),
(289,7,'Biển nào cấm quay xe?','Biển 1 cấm xe rẽ trái, và cũng cấm luôn quay đầu xe (vì muốn quay đầu thì kiểu gì cũng phải đánh lái rẽ trái trước rồi sau đó mới tiếp tục quay đầu xe).Biển 2 cấm quay đầu.Như vậy, cả 2 biển đều cấm quay đầu. Đáp án 1 và 2 đều đúng.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',1,1,1,1),
(290,7,'Biển nào cấm xe rẽ trái?','Biển 1 cấm rẽ trái. Biển 2 cầm quay đầu, nhưng không cấm rẽ trái.Như vậy đáp án 1 đúng. Đáp án 2 và 3 đều sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',1,1,1,1),
(291,7,'Khi gặp biển nào xe được rẽ trái?','Biển 1 cấm rẽ trái. Biển 2 cầm quay đầu, nhưng không cấm rẽ trái. Nói cách khác gặp biển này, xe được rẽ trái.Vậy đáp án 2 đúng. Đáp án 1 và 3 sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',1,1,1,1),
(292,7,'Biển nào cho phép xe rẽ trái?','Biển 1 cấm rẽ trái.Biển 2 chỉ dẫn khu vực được quay đầu xe, nhưng cũng không cho phép rẽ trái (ý này oái oăm, khó suy luận, phải đọc luật và ghi nhớ thôi!).Như vậy chẳng biển nào cho phép rẽ trái, nên đáp án 3 đúng. Đáp án 1 và 2 sai.',0,1,1,1),
(293,7,'Biển nào xe quay đầu không bị cấm?','Biển 1 cấm rẽ trái, cũng đồng thời cấm quay đầu (vì muốn quay đầu thì kiểu gì cũng phải đánh lái rẽ trái trước rồi sau đó mới tiếp tục quay đầu xe).Biển 2 chỉ dẫn khu vực được quay đầu xe.Như vậy chỉ có biển 2 không cấm quay đầu xe. Đáp án 2 đúng. Đáp án 1 và 3 sai.',0,1,1,1),
(294,7,'Biển nào xe được phép quay đầu nhưng không được phép rẽ trái?','Biển 1 cấm rẽ trái, cũng đồng thời cấm quay đầu (vì muốn quay đầu thì kiểu gì cũng phải đánh lái rẽ trái trước rồi sau đó mới tiếp tục quay đầu xe).Biển 2 chỉ dẫn khu vực được quay đầu xe (được phép quay đầu), nhưng không cho phép rẽ trái (ý này oái oăm, khó suy luận, phải đọc luật và ghi nhớ thôi!).Vậy đáp án 2 đúng. Đáp án 1 và 3 sai.',0,1,1,1),
(295,7,'Biển nào là biển báo hiệu cấm đi ngược chiều?','Hãy cùng xem ý nghĩa từng biển:Biển 1 - Đường cấm: cấm xe cộ đi vào theo cả 2 hướng (trừ xe ưu tiên)Biển 2 - Cấm đi ngược chiều: cấm các loại xe đi vào chiều đặt biển (nhìn thấy biển)Biển 3 - Cấm đỗ xeNhư vậy chỉ có biển 2 cấm đi ngược chiều. Đáp án 2 đúng. Đáp án 1 và 3 sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',0,1,1,1),
(296,7,'Biển nào báo hiệu chiều dài đoạn đường phải giữ cự ly tối thiểu giữa hai xe?','Cả 2 biển đều gồm biển chính (giữ cự ly tối thiểu giữa 2 xe), kèm theo biển phụ phía dưới. Ý nghĩa khác nhau do biển phụ:Biển phụ 1 - Phạm vi tác dụng của biển, nghĩa là từ vị trí đặt biển đến sau đó 800m, phải giữ cự ly tối thiểu giữa 2 xe (theo biển chính). Chiều dài đoạn đường là 800m.Biển phụ 2 - Khoảng cách đến đối tượng báo hiệu, nghĩa là sau 200m nữa (biển phụ) mới bắt đầu phải giữ khoảng cách tối thiểu giữa 2 xe (biển chính). Biển này không nêu chiều dài đoạn đường cần giữ khoảng cách là bao nhiêu.Như vậy, biển 1 trả lời cho câu hỏi. Đáp án 1 đúng. Đáp án 2 và 3 đều sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm và Biển báo phụ.',0,1,1,1),
(297,7,'Biển nào báo hiệu khoảng cách thực tế từ nơi đặt biển đến nơi cần cự ly tối thiểu giữa hai xe?','Cả 2 biển đều gồm biển chính (giữ cự ly tối thiểu giữa 2 xe), kèm theo biển phụ phía dưới. Ý nghĩa khác nhau do biển phụ:Biển phụ 1 - Phạm vi tác dụng của biển: nghĩa là ngay từ vị trí đặt biển đến sau đó 800m, phải giữ cự ly tối thiểu giữa 2 xe.Biển phụ 2 - Khoảng cách đến đối tượng báo hiệu: khoảng cách từ nơi đặt biển đến nơi cần cự ly tối thiểu giữa 2 xe là 200m. Nói cách khác, sau 200m nữa mới phải giữ khoảng cách tối thiểu.Như vậy, biển 2 trả lời cho câu hỏi. Đáp án 2 đúng. Đáp án 1 và 3 đều sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm và Biển báo phụ.',0,1,1,1),
(298,7,'Khi gặp biển nào xe ưu tiên theo luật định vẫn phải dừng?','Hãy xem ý nghĩa từng biển thế nào:Biển 1 - Đường cấm: cấm tất cả các phương tiện qua lại, trừ xe ưu tiên.Biển 2 - Dừng lại: buộc tất cả các loại xe cộ, kể cả xe ưu tiên, phải dừng lạiBiển 3 - Cấm đi ngược chiều: cấm xe cộ đi vào theo chiều đặt biển, trừ xe ưu tiênNhư vậy, chỉ khi gặp biển 2, xe ưu tiên vẫn phải dừng lại. Đáp án 2 đúng. Đáp án 1 và 3 sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',1,1,1,1),
(299,7,'Trong các trường hợp dưới đây, người lái xe phải xử lý như thế nào khi gặp biển 2 "Stop"?','Câu hỏi này hơi “chuối”, vì chỉ hỏi biển 2, nhưng đưa cả biển 1 và 3 vào gây rối mắt, và khó hiểu. Câu này có thể hỏi ngắn gọn: gặp biển 2 - Stop thì đáp án nào đúng?Đáp án 1 đúng, vì hiệu lệnh của người điều khiển giao thông còn có hiệu lực cao hơn cả biển báo. Người đó ra hiệu dừng, thì xe phải dừng lại (dù có biển báo hay không).Đáp án 2 đúng, vì gặp biển Stop cắm ở những vị trí đường không an toàn, cầu hẹp, người lái xe phải cho xe dừng lại. Đáp án 3 sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',0,1,1,1),
(300,7,'Biển nào cấm tất cả các loại xe cơ giới và thô sơ đi lại trên đường, trừ xe ưu tiên theo luật định (nếu đường vẫn cho xe chạy được)?','Biển 1 báo đường cấm, nghĩa là cấm tất cả các loại xe cơ giới và thô sơ đi lại trên đường, trừ xe ưu tiên. Còn Biển 2 buộc tất cả các phương tiện, kể cả xe ưu tiên, phải dừng lại.Như vậy đáp án 1 đúng. Đáp án 2 và 3 sai.Tìm hiểu thêm về Ý nghĩa của các biển báo cấm.',0,1,1,1),
(301,7,'Biển nào (đặt trước ngã ba, ngã tư) cho phép xe được rẽ trái sang hướng khác?','Với câu hỏi này, bạn cần nhớ ý nghĩa và vị trí đặt của từng biển:Biển 1: các xe chỉ được đi thẳng. Biển này có thể đặt trước nơi giao nhau (hiệu lực của biển là khu vực giao nhau phía sau), hoặc sau nơi đường giao nhau (hiệu lực của biển từ vị trí đặt đến nơi giao nhau kế tiếp).Biển 2: các xe chỉ được rẽ trái hoặc phải. Biển này được đặt ở sau nơi giao nhau, hiệu lực ở nơi đường giao nhau trước mặt biển, bắt buộc xe phải rẽ trái, quay đầu hoặc rẽ phải.Như vậy, đáp án 1 sai do Biển 1 chỉ cho xe đi thẳng. Đáp án 2 sai do biển Biển 2 phải đặt sau chỗ giao cắt (ngã 3, ngã tư). Đáp án 3 là chính xác.Ghi chú: những biển loại này, người học hay lúng túng về vị trí cắm biển, và phạm vi tác dụng của biển. Và chỉ còn cách học kỹ và ghi nhớ thôi.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',0,1,1,1),
(302,7,'Biển nào báo hiệu hướng đi thẳng phải theo?','Biển 1 báo hiệu các xe chỉ được đi thẳng, nghĩa là hướng đi thẳng phải theo. Biển 2 báo hiệu bạn đang đi trên đường 1 chiều, cấm quay đầu lại, chỉ được rẽ phải hoặc trái.Đáp án 1 đúng. Đáp án 2 sai.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',0,1,1,1),
(303,7,'Biển nào báo hiệu đường một chiều?','Biển 1 báo hiệu các xe chỉ được đi thẳng, nghĩa là hướng đi thẳng phải theo. Biển 2 báo hiệu bạn đang đi trên đường 1 chiều, cấm quay đầu lại, chỉ được rẽ phải hoặc trái.Như vậy đáp án 2 đúng. Đáp án 1 và 3 sai.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',0,1,1,1),
(304,7,'Trong các biển nào dưới đây biển nào là biển hết hạn chế tốc độ tối đa?','Biển 1 báo hiệu hết hạn chế tốc độ tối đa - 40km/h. Biển 2 báo hiệu hết tất cả các lệnh cấm. Biển 3 báo hiệu hết hạn chế tốc độ tối thiểu 30km/h.Như vậy biển 1 trả lời câu hỏi, và đáp án 1 đúng. Các đáp án còn lại đều sai.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',0,1,1,1),
(305,7,'Trong các biển nào dưới đây biển nào là biển hết hạn chế tốc độ tối thiểu?','Biển 1 báo hiệu hết hạn chế tốc độ tối đa - 40km/h. Biển 2 báo hiệu hết tất cả các lệnh cấm. Biển 3 báo hiệu hết hạn chế tốc độ tối thiểu 30km/h.Như vậy biển 3 trả lời câu hỏi, và đáp án 3 đúng. Các đáp án còn lại đều sai.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',0,1,1,1),
(306,7,'Trong các biển dưới đây biển nào là biển hết mọi lệnh cấm?','Biển 1 báo hiệu hết hạn chế tốc độ tối đa - 40km/h.Biển 2 báo hiệu hết tất cả các lệnh cấm.Biển 3 báo hiệu hết hạn chế tốc độ tối thiểu 30km/h.Như vậy biển 2 trả lời câu hỏi, và đáp án 2 đúng. Các đáp án còn lại đều sai.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',0,1,1,1),
(307,7,'Biển nào báo hiệu đường dành cho xe ô tô?','Biển 1 báo hiệu đường dành cho ô tô. Biển 2 có ý nghĩa ngược lại, báo hiệu hết đường dành cho ô tô.Như vậy đáp án 1 đúng, đáp án 2 sai.Tham khảo thêm về Ý nghĩa các biển chỉ dẫn giao thông.',0,1,1,1),
(308,7,'Biển nào báo hiệu hết đường dành cho xe ô tô?','Biển 1 báo hiệu đường dành cho ô tô. Biển 2 có ý nghĩa ngược lại, báo hiệu hết đường dành cho ô tô.Như vậy đáp án 1 sai, đáp án 2 đúng. Ghi chú: lưu ý câu này đảo số đáp án và số biển, người thi rất dễ bị nhầm. Tham khảo thêm về Ý nghĩa các biển chỉ dẫn giao thông.',0,1,1,1),
(309,7,'Khi gặp biển nào thì các phương tiện không được phép đi vào, trừ ô tô và mô tô?','Biển 1 báo hiệu đường dành cho ô tô, xe máy, các phương tiện khác không được đi vào.Biển 2 báo hiệu hết đường dành cho ô tô, xe máy.Như vậy đáp án 1 đúng, đáp án 2 sai. Tham khảo thêm về Ý nghĩa các biển chỉ dẫn giao thông.',0,1,1,1),
(310,7,'Biển nào cho phép được quay đầu xe đi theo hướng ngược lại khi đặt biển trước ngã ba, ngã tư?','Với câu hỏi này, bạn cần nhớ ý nghĩa và vị trí đặt của từng biển:Biển 1: các xe chỉ được rẽ trái. Biển này đặt ở trước nơi giao nhau, báo hiệu xe phải rẽ trái ở nơi giao nhau đằng sau mặt biển.Biển 2: các xe chỉ được rẽ trái hoặc phải. Biển này được đặt ở sau nơi giao nhau, hiệu lực ở nơi đường giao nhau trước mặt biển, bắt buộc xe phải rẽ trái, quay đầu hoặc rẽ phải.Biển 3: các xe chỉ được rẽ trái, đi thẳng, hoặc quay đầu. Biển này được đặt trước chỗ giao nhau, có tác dụng phía trước chỗ giao nhau.Như vậy, chỉ có Biển 3 cho phép quay đầu và đặt trước ngã ba, ngã tư (chỗ đường giao nhau). Đáp án 3 đúng.Đáp án 1 sai, do Biển 1 chỉ cho xe rẽ trái, không được quay đầu. Đáp án 2 sai, do Biển 2 đặt ở sau ngã ba, ngã tư.Ghi chú: những biển loại này, người học hay lúng túng về vị trí cắm biển, và phạm vi tác dụng của biển. Và chỉ còn cách học kỹ và ghi nhớ thôi.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',0,1,1,1),
(311,7,'Biển nào không cho phép rẽ phải?','Với câu hỏi này, bạn cần nhớ ý nghĩa của từng biển:Biển 1: các xe chỉ được rẽ trái. Biển 2: các xe chỉ được rẽ trái, quay đầu, hoặc rẽ phải.Biển 3: các xe chỉ được đi thẳng, hoặc rẽ phải.Như vậy chỉ có Biển 1 không cho phép rẽ phải. Đáp án 1 đúng. Các đáp án còn lại đều sai.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',0,1,1,1),
(312,7,'Khi đến chỗ giao nhau, gặp biển nào thì người lái xe không được cho xe đi thẳng, phải rẽ sang hướng khác?','Với câu hỏi này, bạn cần nhớ ý nghĩa của từng biển:Biển 1: các xe chỉ được rẽ trái. Biển 2: các xe chỉ được rẽ trái, quay đầu, hoặc rẽ phải.Biển 3: các xe chỉ được đi thẳng, hoặc rẽ phải. Chỉ có Biển 3 cho phép xe đi thẳng. Biển 1 và 2 không cho đi thẳng, phải rẽ sang hướng khác. Vậy đáp án 3 đúng. Các đáp án còn lại sai.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',1,1,1,1),
(313,7,'Biển nào được đặt trước ngã ba, ngã tư và phạm vi tác dụng của biển ở ngã ba, ngã tư đằng sau mặt biển?','Với câu hỏi này, bạn cần nhớ vị trí đặt và phạm vi tác dụng của từng biển:Biển 1: các xe chỉ được rẽ trái. Biển này đặt ở trước nơi giao nhau, báo hiệu xe phải rẽ trái ở nơi giao nhau đằng sau mặt biển.Biển 2: các xe chỉ được rẽ trái hoặc phải. Biển này được đặt ở sau nơi giao nhau, hiệu lực ở nơi đường giao nhau đằng trước mặt biển, bắt buộc xe phải rẽ trái, quay đầu hoặc rẽ phải.Biển 3: các xe chỉ được rẽ trái, đi thẳng, hoặc quay đầu. Biển này được đặt trước chỗ giao nhau, có tác dụng tại chỗ giao nhau đằng sau mặt biển.Như vậy, Biển 1 và 3 đặt trước chỗ giao nhau, và tác dụng của biển ở sau mặt biển. Đáp án 3 đúng.Biển 2 đặt sau nơi giao nhau, nên đáp án 1 và 2 đều sai.Ghi chú: những biển loại này, người học hay lúng túng về vị trí cắm biển, và phạm vi tác dụng của biển. Và chỉ còn cách học kỹ và ghi nhớ thôi.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',0,1,1,1),
(314,7,'Biển nào được đặt sau ngã ba, ngã tư và phạm vi tác dụng của biển ở ngã ba, ngã tư trước mặt biển?','Với câu hỏi này, bạn cần nhớ vị trí đặt và phạm vi tác dụng của từng biển:Biển 1: các xe chỉ được rẽ trái. Biển này đặt ở trước nơi giao nhau, báo hiệu xe phải rẽ trái ở nơi giao nhau đằng sau mặt biển.Biển 2: các xe chỉ được rẽ trái hoặc phải. Biển này được đặt ở sau nơi giao nhau, hiệu lực ở nơi đường giao nhau đằng trước mặt biển, bắt buộc xe phải rẽ trái, quay đầu hoặc rẽ phải.Biển 3: các xe chỉ được rẽ phải, hoặc đi thẳng. Biển này được đặt trước chỗ giao nhau, có tác dụng đằng sau mặt biển.Chỉ có Biển 2 đặt sau nơi giao nhau, và phạm vị tác dụng ở chỗ giao nhau đằng trước mặt biển. Đáp án 3 đúng.Biển 1 và 3 đặt trước chỗ giao nhau, và tác dụng của biển ở sau mặt biển. Do đó đáp án 1 và 2 đều sai.Ghi chú: những biển loại này, người học hay lúng túng về vị trí cắm biển, và phạm vi tác dụng của biển. Và chỉ còn cách học kỹ và ghi nhớ thôi.Tham khảo thêm về Ý nghĩa các biển hiệu lệnh.',0,1,1,1),
(315,7,'Khi gặp các biển này xe ưu tiên theo luật định (có tải trọng hay chiều cao toàn bộ vượt quá số ghi trên biển) có được đi qua hay không?','Đây là nhóm biển báo hạn chế tải trọng, chiều cao xe. Biển cấm các loại xe có tải trọng hay chiều cao vượt quá trị số ghi trên biển đi qua.Đáp án 2 đúng. Đáp án 1 sai.Tham khảo thêm Ý nghĩa các biển báo cấm.',0,1,1,1),
(316,7,'Biển nào hạn chế kích thước chiều cao của xe và hàng?','Hãy cùng xem ý nghĩa của từng biển:Biển 1 - Hạn chế chiều cao: cấm xe có chiều cao vượt quá trị số 3,5m trên biển đi quaBiển 2 - Hạn chế trọng lượng trên trục xe: cấm xe có tải trọng trên mỗi trục vượt quá trị số trên biển (7 tấn) đi quaBiển 3 - Hạn chế trọng lượng xe: cấm xe có tải trọng toàn bộ (xe + hàng) vượt quá trị số trên biển (10 tấn) đi quaNhư vậy chỉ có biển 1 trả lời cho câu hỏi. Đáp án 1 đúng. Đáp án còn lại sai.Tham khảo thêm Ý nghĩa các biển báo cấm.',0,1,1,1),
(317,7,'Biển số 2 có ý nghĩa như thế nào?','Câu hỏi này chỉ liên quan tới biển số 2, bạn không cần để ý tới biển số 1 và 3 (sao không bỏ quách biển thừa này đi cho đỡ rối mắt nhỉ?!)Biển 2 vẽ số tấn 7t phía trên trục xe, có thể suy luận được ý nghĩa: cấm xe có tải trọng trên mỗi trục vượt quá trị số trên biển (7 tấn) đi qua. Nghĩa là cho xe có tải trọng trục từ 7 tấn trở xuống lưu thông.Đáp án 2 đúng. Đáp án 1 sai.Tham khảo thêm Ý nghĩa các biển báo cấm.',0,1,1,1),
(318,7,'Biển số 3 có ý nghĩa như thế nào?','Câu hỏi này chỉ liên quan tới biển số 2, bạn không cần để ý tới biển số 1 và 2 (có lẽ nên bỏ hình 2 biển thừa kia đi cho đỡ rối mắt thí sinh!)Biển số 3 có tên là Hạn chế trọng lượng xe: cấm xe có tải trọng toàn bộ (xe + hàng) vượt quá trị số trên biển (trên 10 tấn) đi qua.Đáp án 1 đúng. Đáp án 2 và 3 sai.Tham khảo thêm Ý nghĩa các biển báo cấm.',0,1,1,1),
(319,7,'Biển này có ý nghĩa gì?','Biển này có tên là "Hướng đi trên mỗi làn đường theo vạch kẻ đường" - Biển số 411 trong Quy chuẩn 41 về báo hiệu đường bộ.Biển này chỉ dẫn số lượng làn đường trên mặt đường và hướng đi  trên mỗi làn đường theo vạch kẻ đường.Vậy đáp án 2 đúng.Đáp án 1 và 3 sai, vì có cụm từ “phải theo” thì sẽ sang nhóm biển hiệu lệnh, trong khi biển 411 là biển chỉ dẫn.Tham khảo thêm Nội dung các biển chỉ dẫn.',0,1,1,1),
(320,7,'Biển nào cấm máy kéo kéo theo rơ moóc?','Trước hết hãy xem ý nghĩa của mỗi biển:Biển 1 cấm xe cơ giới kéo theo rơ-moóc kể cả máy kéo kéo theo rơ-moócBiển 2 cấm máy kéo đi qua, dù có kéo rơ moóc hay khôngNhư vậy thì cả 2 biển đều cấm máy kéo kéo theo rơ-moóc. Đáp án 1 và 2 đều đúng. Đáp án 3 sai.Tham khảo thêm Ý nghĩa các biển báo cấm.',0,1,1,1),
(321,7,'Khi gặp biển số 1, xe ô tô tải có được đi vào không?','Câu này chỉ hỏi về biển số 1, bạn quên 2 biển kia đi.Biển số 1 cấm ô tô, máy kéo kéo theo rơ-moóc, chứ không cấm xe tải đi vào. Đáp án 1 đúng, đáp án 2 sai.Ghi chú: đáp án chưa rõ hết ý. Nếu xe tải kéo theo rơ-moóc thì cũng sẽ bị cấm. Vì thế câu hỏi nên sửa thành “Khi gặp biển số 1, xe ôtô tải không kéo rơ moóc có được đi vào không?”Tham khảo thêm Ý nghĩa của các biển báo cấm.',0,1,1,1),
(322,7,'Biển nào không có hiệu lực đối với ô tô tải không kéo moóc?','Trước hết cần phân tích một chút ý nghĩa từng biển:Biển 1 cấm ô tô kéo moóc, nghĩa là không có hiệu lực với ô tô tải không kéo moócBiển 2 cấm máy kéo, không có hiệu lực với xe tải không kéo moócBiển 3 cấm ô tô, nghĩa là cấm cả ô tô tải có kéo moócNhư vậy, chỉ có biển 1 và 2 thỏa mã câu hỏi. Đáp án 1 đúng, các đáp án còn lại sai.Tham khảo thêm Ý nghĩa của các biển báo cấm.',0,1,1,1),
(323,7,'Biển nào cấm máy kéo?','Trước hết cần phân tích một chút ý nghĩa từng biển:Biển 1 cấm ô tô, máy kéo kéo moóc (không cấm máy kéo nói chung)Biển 2 cấm máy kéoBiển 3 cấm ô tô tải, và cấm cả máy kéo, xe máy chuyên dùng (cần nhớ 2 loại sau)Như vậy, chỉ biển 2 và 3 cấm máy kéo. Đáp án 4 đúng. Đáp án 1, 2 và 3 đều sai.Ghi chú: Cách hỏi câu này có vẻ chưa rõ ràng lắm. Biển số 1 cấm máy kéo kéo moóc, nghĩa là có thể thuộc diện cấm hoặc không, tùy vào hành vi có kéo moóc không: có mooc thì cấm, không thì thôi. Như vậy, để rõ ràng, cần sửa câu hỏi thành “Biển nào cấm máy kéo không kéo moóc”.Tham khảo thêm Ý nghĩa của các biển báo cấm.',0,1,1,1),
(324,7,'Khi gặp biển này, xe lam và mô tô ba bánh có được phép rẽ trái hay rẽ phải hay không?','Với câu này, cần hiểu ý nghĩa của cả biển chính và biển phụ:Biển chính: cấm tất cả các loại xe cơ giới và môtô, nghĩa là cấm cả xe lam và mô tô 3 bánhBiển phụ: chỉ dẫn hướng tác dụng của biển chính là cả bên phải và bên tráiNhư vậy, 2 biển này kết hợp thể hiện: cấm xe cơ giới, kể cả xe lam và mô tô 3 bánh, rẽ phải hay trái. Đáp án 2 đúng, đáp án 1 sai.Tham khảo thêm ý nghĩa của các loại Biển báo cấm và Biển báo phụ.',1,1,1,1),
(325,7,'Biển này có hiệu lực đối với xe mô tô hai bánh, ba bánh không?','Với câu này, cần hiểu ý nghĩa của cả biển chính và biển phụ:Biển chính: cấm tất cả các loại xe cơ giới và môtô, nghĩa có hiệu lực với cả mô tô 2 hoặc 3 bánhBiển phụ: chỉ dẫn hướng tác dụng của biển chính là cả bên phải và bên tráiNhư vậy, 2 biển này kết hợp thể hiện: cấm xe cơ giới, kể cả mô tô 2-3 bánh, rẽ phải hay trái. Đáp án 1 đúng, đáp án 2 sai.Tham khảo thêm ý nghĩa của các loại Biển báo cấm và Biển báo phụ.',1,1,1,1),
(326,7,'Biển này có ý nghĩa gì?','Với câu này, cần hiểu ý nghĩa của cả biển chính và biển phụ:Biển chính: cấm tất cả các loại xe cơ giới và môtô, trừ xe ưu tiênBiển phụ: chỉ dẫn hướng tác dụng của biển chính là về phía bên phải và bên tráiNhư vậy, 2 biển này kết hợp thể hiện: cấm xe cơ giới, trừ xe ưu tiên, đi về bên phải hay trái. Đáp án 2 đúng, đáp án 1 và 3 sai.Tham khảo thêm ý nghĩa của các loại Biển báo cấm và Biển báo phụ.',0,1,1,1),
(327,7,'Biển phụ đặt dưới biển bấm còi có ý nghĩa gì?','Biển phụ này thể hiện chiều dài quãng đường cấm bóp còi (biển chính) là 500 mét kể từ nơi đặt biển.Đáp án 2 đúng. Đáp án 1 và 3 sai.Tham khảo thêm Ý nghĩa của các biển phụ.',0,1,1,1),
(328,7,'Chiều dài đoạn đường 500m từ nơi đặt biển này người lái xe có được phép bấm còi không?','Biển báo kết hợp này thể hiện rằng: chiều dài quãng đường cấm bóp còi (biển chính) là 500 mét kể từ nơi đặt biển (biển phụ).Như vậy đáp án đúng là 2. Đáp án 1 sai.Tham khảo thêm ý nghĩa của các loại Biển báo cấm và Biển báo phụ.',0,1,1,1),
(329,7,'Biển này có ý nghĩa gì?','Biển chính cấm dừng, đỗ xe. Biển phụ phía dưới báo hiệu hướng tác dụng của biển chính là bên phải.Kết hợp cả 2 biển chính + phụ thể hiện: cấm dừng, đỗ xe theo hướng phải. Đáp án 2 đúng. Các đáp án còn lại đều sai.Tham khảo thêm ý nghĩa của các loại Biển báo cấm và Biển báo phụ.',0,1,1,1),
(330,7,'Theo hướng bên phải có được phép đỗ xe, dừng xe không?','Biển chính cấm dừng, đỗ xe. Biển phụ phía dưới báo hiệu hướng tác dụng của biển chính là bên phải.Như vậy kết hợp cả 2 biển chính + phụ thể hiện: theo hướng bên phải không được phép dừng, đỗ xe. Đáp án 1 đúng, đáp án 2 sai.Tham khảo thêm ý nghĩa của các loại Biển báo cấm và Biển báo phụ.',0,1,1,1),
(331,7,'Gặp biển này, xe ô tô sơ mi rơ moóc có tổng chiều dài lớn hơn trị số ghi trên biển có được phép đi vào không?','Biển này cấm xe kéo theo moóc kể cả ôtô sơ-mi rơ-moóc có tổng chiều dài (xe + moóc + hàng) lớn hơn trị số ghi trên biển (14 mét).Vậy đáp án 1 đúng, đáp án 2 sai.',0,1,1,1),
(332,7,'Xe ô tô chở hàng vượt quá phía trước và sau thùng xe, mỗi phía quá 10% thân xe, tổng chiều dài xe (cả hàng) từ trước đến sau nhỏ hơn trị số ghi trên biển thì có được phép đi vào không?','Cho dù tổng chiều dài có vượt quá trị số trên biển hay không, nhưng nếu xếp hàng vượt về phía trước, phía sau thùng xe trên 10% chiều dài xe là vi phạm luật.Theo Khoản 4.2b Điều 1 Nghị định 107/2014/NĐ-CP sửa đổi bổ sung Nghị định 171/2013/NĐ-CP, lỗi xếp hàng này có thể bị phạt từ 800.000 đến 1.200.000 đồng.Như vậy đáp án 1 đúng, đáp án 2 sai.',0,1,1,1),
(333,7,'Biển nào cấm xe đạp đi vào?','Để trả lời câu hỏi, ta cần hiểu ý nghĩa từng biển báo:Biển 1 - Đường dành cho xe thô sơ: bắt buộc các loại xe thô sơ và người đi bộ phải đi trên đường này, và cấm các xe cơ giới đi vào.Biển 2 - Cấm đi xe đạp: báo đường cấm xe đạp đi qua.Biển 3 - Đường người đi xe đạp cắt ngang: báo hiệu gần tới vị trí thường có người đi xe đạpNhư vậy chỉ có biển 2 cấm xe đạp. Đáp án 2 đúng. Đáp án 1 và 3 sai.Tìm hiểu ý nghĩa của Các loại biển báo giao thông.',0,1,1,1),
(334,7,'Biển nào báo hiệu đường dành cho xe thô sơ?','Để trả lời câu hỏi, ta cần hiểu ý nghĩa từng biển báo:Biển 1 - Đường dành cho xe thô sơ: bắt buộc các loại xe thô sơ và người đi bộ phải đi trên đường này, và cấm các xe cơ giới đi vào.Biển 2 - Cấm đi xe đạp: báo đường cấm xe đạp đi qua.Biển 3 - Đường người đi xe đạp cắt ngang: báo hiệu gần tới vị trí thường có người đi xe đạpNhư vậy, chỉ có biển 1 trả lời đúng cho câu hỏi. Đáp án 1 đúng. Các đáp án còn lại sai.Tìm hiểu ý nghĩa của Các loại biển báo giao thông.',0,1,1,1),
(335,7,'Biển nào báo hiệu nơi đỗ xe cho người tàn tật?','Chỉ nhìn Biển 2 với hình ảnh người ngồi trên xe lăn cũng biết đáp án. Nhưng cũng cần hiểu rõ từng biển, để đảm bảo trả lời không bị sót đáp án đúng.Biển 1 - Đường dành cho xe thô sơ: bắt buộc các loại xe thô sơ và người đi bộ phải đi trên đường này, và cấm các xe cơ giới đi vào.Biển 2 - Nơi đỗ xe dành cho người tàn tậtBiển 3 - Đường dành cho người đi bộ, các phương tiện không được vàoNhư vậy đáp án đúng là 2. Đáp án 1 và 3 đều sai.Tìm hiểu ý nghĩa của Các loại biển báo giao thông.',0,1,1,1),
(336,7,'Biển nào cấm xe tải vượt?','Biển 1 cấm xe cơ giới, gồm cả xe tải, vượt nhau. Biển 2 cấm xe tải vượt xe cơ giới khác. Vậy cả 2 đáp án 1 và 2 đều đúng.Tham khảo thêm Ý nghĩa của các biển báo cấm.',0,1,1,1),
(337,7,'Biển nào xe ô tô con được phép vượt?','Biển 1 cấm xe cơ giới, gồm cả ô tô con, vượt nhau. Biển 2 chỉ cấm xe tải vượt, không cấm xe con vượt. Vậy đáp án 1 đúng, đáp án 2 sai.Tham khảo thêm Ý nghĩa của các biển báo cấm.',0,1,1,1),
(338,7,'Biển nào báo hiệu đường đôi?','Để trả lời câu hỏi, ta cần hiểu ý nghĩa của từng biển báo:Biển 1: chỉ dẫn cho người lái xe cơ giới biết mình được quyền ưu tiên đi trước trên đoạn đường hẹp.Biển 2: báo hiệu sắp đến đoạn đường đôi (đường có chiều đi và chiều về phân biệt bằng giải phân cách cứng).Biển 3: báo hiệu sắp hết đường đôi.Như vậy chỉ có Biển 2 trả lời đúng cho câu hỏi này. Đáp án 2 đúng. Đáp án 1 và 3 đều sai.Ghi chú: câu hỏi này có phần thiếu rõ ràng. Thực tế cả biển 2 và biển 3 đều báo hiệu liên quan đến đường đôi, dù là bắt đầu hay kết thúc đường đôi. Có lẽ nên sửa câu hỏi cho rõ hơn thành “Biển nào báo hiệu sắp đến đường đôi”.',0,1,1,1),
(339,7,'Biển nào báo hiệu đường đôi?','Cả 3 biển đều có 2 mũi tên ngược chiều nhau, nên có thể gây nhầm lẫn. Do đó, cần ghi nhớ ý nghĩa của từng biển báo:Biển 1: báo trước sắp đến đoạn đường (từ một chiều chuyển thành) hai chiềuBiển 2: đặt trên đường một chiều, để báo trước sắp đến đoạn đường hai chiềuBiển 3: báo hiệu sắp đến đoạn đường đôi (đường có chiều đi và chiều về phân biệt bằng giải phân cách cứng).Như vậy chỉ có biển 3 báo hiệu đường đôi. Đáp án 3 đúng. Đáp án 1 và 2 sai.Tham khảo thêm Ý nghĩa các biển báo nguy hiểm.',0,1,1,1),
(340,7,'Biển nào báo hiệu hết đường đôi?','Cả 3 biển đều có 2 mũi tên ngược chiều nhau, nên dễ gây nhầm lẫn. Do đó, bạn cần ghi nhớ ý nghĩa của từng biển báo:Biển 1: báo trước sắp đến đoạn đường (từ một chiều chuyển thành) hai chiềuBiển 2: báo hiệu sắp đến đoạn đường đôi (đường có chiều đi và chiều về phân biệt bằng giải phân cách cứng).Biển 3: sắp hết đường đôiNhư vậy chỉ có Biển 3 trả lời đúng câu hỏi. Đáp án 3 đúng. Đáp án 1 và 2 sai.Tham khảo thêm Ý nghĩa các biển báo nguy hiểm.',0,1,1,1),
(341,7,'Biển nào báo hiệu hết đường cao tốc?','Đây là nhóm biển chỉ dẫn, ý nghĩa từng biển như sau:Biển 1: Báo hiệu hết đoạn đường được ưu tiên.Biển 2: Báo hiệu hết đường cao tốcBiển 3: Báo hiệu hết đoạn đường dành cho ô tôNhư vậy Biển 2 trả lời đúng cho câu hỏi. Đáp án 2 đúng. Đáp án 1 và 3 sai.Tham khảo thêm Ý nghĩa của các biển chỉ dẫn.',0,1,1,1),
(342,7,'Biển nào báo hiệu giao nhau với đường hai chiều?','Để làm câu hỏi này cần hiểu ý nghĩa từng biển báo:Biển 1: báo trước sắp đến vị trí giao nhau với đường hai chiềuBiển 2: báo hiệu sắp đến đoạn đường đôi (đường có chiều đi và chiều về phân biệt bằng giải phân cách cứng)Biển 3: báo hiệu sắp đến chỗ giao nhau giữa các tuyến đường cùng cấpNhư vậy biển 1 trả lời cho câu hỏi này. Đáp án 1 đúng. Đáp án 2 và 3 sai.Tham khảo thêm Ý nghĩa các biển báo nguy hiểm.',0,1,1,1),
(343,7,'Biển nào báo hiệu đường hai chiều?','Hãy cùng xem ý nghĩa của từng biển:Biển 1: báo hiệu sắp đến chỗ giao nhau giữa các tuyến đường cùng cấpBiển 2: đặt trên đường một chiều, để báo trước sắp đến đoạn đường hai chiềuBiển 3: báo hiệu sắp hết đường đôiBiển 2 trả lời cho câu hỏi. Đáp án 2 đúng. Đáp án 1 và 3 sai.Tham khảo thêm Ý nghĩa các biển báo nguy hiểm.',0,1,1,1),
(344,7,'Biển nào báo hiệu giao nhau với đường hai chiều?','Biển 1 đặt trên đường một chiều, để báo trước sắp đến đoạn đường hai chiều.Biển 2 báo hiệu sắp đến chỗ giao nhau với đường 2 chiều.Biển 3 báo hiệu sắp đến chỗ giao nhau giữa các tuyến đường cùng cấp.Như vậy, biển 2 trả lời cho câu hỏi. Đáp án 2 đúng. Đáp án 1 và 3 sai.Tham khảo thêm Ý nghĩa các biển báo nguy hiểm.',0,1,1,1),
(345,7,'Biển nào báo hiệu cầu vượt liên thông?','Biển 1 và 3 báo hiệu cầu vượt liên thông (biển chỉ dẫn). Biển 2 biểu thị phía trước có cầu vượt, xe có thể đi thẳng hoặc rẽ phải (biển hiệu lệnh). Vậy đáp án 3 đúng. Các đáp án còn lại đều sai.Tham khảo thêm ý nghĩa của các Biển hiệu lênh và Biển chỉ dẫn.',1,1,1,1),
(346,7,'Biển nào báo hiệu chú ý chướng ngại vật?','Biển 1 báo hiệu sắp hết đường đôi. Biển 2 báo phía trước có chướng ngại vật, xe cần vòng tránh ra 2 bên. Biển 3 báo phía trước có chướng ngại vật, xe cần vòng tránh sang trái.Cả biển 2 và 3 đều báo chướng ngại vật. Đáp án 2 đúng. Đáp án 1 và 3 sai.Tham khảo thêm Ý nghĩa các biển báo nguy hiểm.',0,1,1,1),
(347,7,'Biển nào báo hiệu đoạn đường hay xảy ra tai nạn?','Biển 1 cảnh báo sắp giao nhau với đường không ưu tiên. Biển 2 cảnh báo sắp tới đoạn đường hay xảy ra tai nạn. Biển 3 báo hiệu sắp tới đoạn đường trơn trượt.Vậy biển 2 trả lời cho câu hỏi. Đáp án 2 đúng. Đáp án 1 và 3 sai.Tham khảo thêm Ý nghĩa các biển báo nguy hiểm.',1,1,1,1),
(348,7,'Biển số 1 có ý nghĩa gì?','Biển 1 báo hiệu cầu vượt liên thông. Biển 2 và 3 báo hiệu phía trước có cầu vượt, xe có thể đi thẳng hoặc rẽ trái (biển 2), hay rẽ phải (biển 3).Vậy đáp án 3 đúng. Đáp án 1 và 2 sai.',0,1,1,1),
(349,7,'Biển nào báo hiệu tuyến đường cầu vượt cắt qua?','Biển 1 và 2 báo hiệu tuyến đường cầu vượt cắt qua. Biển 3 báo hiệu cầu vồng (công trình có độ vồng lớn ảnh hưởng tầm nhìn).Vậy đáp án 1 đúng. Đáp án 2 và 3 sai.',1,1,1,1),
(350,7,'Biển nào báo hiệu đường hầm?','Những biển này có hình dáng hơi na ná giống nhau, nhưng ý nghĩa khác nhau:Biển 1 - “Cửa chui”: sắp đến đường có cổng chắn ngang (kiểu cổng giống như đường hầm, cổng thành, cầu vượt đường bộ dạng cầu vòm v.v…).Biển 2 - “Đường hầm”: chuẩn bị đi vào hầm đường bộ.Biển 3 - “Cầu vồng”: sắp đến công trình có độ vồng lớn ảnh hưởng tới tầm nhìn.Như vậy, Đáp án 3 đúng. Đáp án 1 và 2 sai.Tham khảo thêm Ý nghĩa các biển báo nguy hiểm.',0,1,1,1),
(351,7,'Biển nào chỉ dẫn tên đường trên các tuyến đường đối ngoại?','Cả 3 biển đều được sử dụng trên các tuyến đường đối ngoại:Biển 1 cấm đỗ xeBiển 2 cấm đỗ xe từ 6h đến 8hBiển 3 chỉ dẫn tên đườngVậy đáp án 3 đúng. Đáp án 1 và 2 sai.',1,1,1,1),
(352,7,'Biển số 1 có ý nghĩa gì?','Biển số 1 dẫn hết hiệu lực cấm đỗ xe theo giờ (6h-8h) trên các tuyến đường đối ngoại.Đáp án 1 đúng. Đáp án 2 và 3 sai.',0,1,1,1),
(353,7,'Biển số 3 có ý nghĩa gì?','Biển số 3 chỉ dẫn khu vực hạn chế tốc độ tối đa 40km/h trên các tuyến đường đối ngoại. Đáp án 3 đúng. Các đáp án còn lại sai.',0,1,1,1),
(354,7,'Biển nào báo hiệu đường có làn đường dành cho ô tô khách?','Biển 1 báo hiệu làn đường dành riêng cho ô tô con. Biển 2 báo hiệu đường có làn đường dành cho ô tô khách. Biển 3 báo hiệu rẽ ra đường có làn đường dành cho ô tô khách.Vậy Biển 2 trả lời cho câu hỏi này. Đáp án 2 đúng. Đáp án 1 và 3 sai.',0,1,1,1),
(355,7,'Biển nào báo hiệu rẽ ra đường có làn đường dành cho ô tô khách?','Biển 1 báo hiệu làn đường dành riêng cho ô tô con. Biển 2 báo hiệu đường có làn đường dành cho ô tô khách. Biển 3 báo hiệu rẽ ra đường có làn đường dành cho ô tô khách.Vậy Biển 3 trả lời cho câu hỏi này. Đáp án 3 đúng. Đáp án 1 và 2 sai.',0,1,1,1),
(356,8,'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?','Để trả lời câu hỏi sa hình này, cần lưu ý thứ tự: xe ưu tiên - đường ưu tiên - quyền ưu tiên - hướng ưu tiên (gọi tắt là tiên - đường - quyền - hướng cho dễ nhớ).Trước hết, do không có xe nào ưu tiên, nên chỉ xét đến đường ưu tiên: có xe tải và mô tô được đi trước. Trong 2 xe này, không có xe nào có quyền ưu tiên, vì phía bên trái cả 2 xe đều có xe khác. Vậy xét đến hướng ưu tiên theo thứ tự: rẽ phải - đi thẳng - rẽ trái - quay đầu, theo đó xe tải đi thẳng nên được quyền đi trước xe mô tô.Tương tự,xe con và xe lam cùng trên đường không ưu tiên, nhưng xe lam rẽ phải nên được quyền đi trước xe con.Tóm lại thứ tự các xe đi như sau: xe tải, xe mô tô, xe lam, xe con.Đáp án 2 đúng. Các đáp án kia sai.',1,1,1,1),
(357,8,'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?','Để trả lời câu hỏi sa hình này, cần lưu ý thứ tự: xe ưu tiên - đường ưu tiên - quyền ưu tiên - hướng ưu tiên (gọi tắt là tiên - đường - quyền - hướng cho dễ nhớ).Trước hết, chỉ có 1 xe ưu tiên nên xe này sẽ được đi đầu tiên: xe công an (1).Sau đó, xét tới đường ưu tiên: còn có xe con. Vậy xe con (2) sẽ đi kế tiếp sau xe công an.Tiếp theo, xét tới hướng ưu tiên. Cả 2 xe còn lại đều không có hướng ưu tiên: bên trái đều không có xe nào khác (vì xe công an & xe con đã đi qua rồi). Vậy, còn xét tới yếu tố hướng ưu tiên: xe đi thẳng sẽ được đi trước xe rẽ trái. Như thế thì xe tải (3) sẽ đi trước xe lam (4).Tóm lại, thứ tự các xe đi như sau: xe công an, xe con, xe tải, xe lam. Đáp án 1 đúng. Các đáp án còn lại đều sai.',0,1,1,1),
(358,8,'Theo hướng mũi tên, thứ tự các xe đi như thế nào là đúng quy tắc giao thông?','Để trả lời câu hỏi sa hình này, cần lưu ý thứ tự: xe ưu tiên - đường ưu tiên - quyền ưu tiên - hướng ưu tiên (gọi tắt là tiên - đường - quyền - hướng cho dễ nhớ).Trong sa hình, có xe ưu tiên nên sẽ được đi trước tiên là xe công an (1).Xét tiếp đến đường ưu tiên, căn cứ theo biển báo chính và biển phụ: thì có xe công an và xe tải đang ở trên đường ưu tiên. Do đó, xe được đi tiếp theo sẽ là xe tải (2).Khi chỉ còn xe khách và xe con tại ngã tư, thì xe con phải nhường cho xe đi từ bên phải tới: là xe khách (3). Cuối cùng mới đến lượt xe con (4).Vậy, thứ tự các xe sẽ là: xe công an - xe tải - xe khách - xe con. Đáp án 4 đúng, các đáp án còn lại đều sai.Ghi chú: sa hình này có biển phụ, nếu bỏ qua là sẽ trả lời sai ngay, vì không biết xe tải cũng đang nằm trên đường ưu tiên.',0,1,1,1),
(359,8,'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?','Ta sẽ dựa vào nguyên tắc “tiên - đường - quyền - hướng” để giải sa hình này.Vì không có xe ưu tiên hay đường ưu tiên, ta xét tới quyền ưu tiên của các xe tại ngã tư: phải nhường cho xe bên phải tới. Rõ ràng, bên phải xe mô tô trống, không có xe nào, nên xe mô tô (1) được đi đầu tiên.Sau khi xe mô tô đi qua, thì bên phải xe lam cũng trống. Như vậy, kế tiếp là đến xe lam (2) và xe tải (3).Thứ tự các xe: xe mô tô - xe lam - xe tải.Vậy đáp án 3 đúng. Các đáp án còn lại đều sai.',1,1,1,1),
(360,8,'Xe nào phải nhường đường trong trường hợp này?','Tại nơi giao nhau có vòng xuyến, các xe phải nhường cho xe khác từ bên trái đi tới. Như vậy xe con phải nhường đường trong trường hợp này.Đáp án 1 chính xác. Đáp án 2 sai.Ghi chú: Ngoài cách trên còn có thể giải thích câu này theo cách khác. Theo quy định, xe nào vào nơi giao cắt trước thì được quyền đi trước. Nhìn hình vẽ có thể thấy xe tải vào vòng xuyến trước. Như vậy, xe con vào vòng xuyến sau thì phải nhường đường.',0,1,1,1),
(361,8,'Trường hợp này xe nào được quyền đi trước?','Trên sa hình này, xe mô tô gặp biển Stop phải dừng lại nhường đường. Do đó, xe con được quyền đi trước. Đáp án 2 đúng, đáp án 1 sai.',1,1,1,1),
(362,8,'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?','Trên sa hình này, xe đã vào ngã tư trước được quyền đi đầu tiên, đó là xe lam (1). Sau đó tới xe ưu tiên: xe cứu thương (2). Và sau cùng mới tới xe con (3).Vậy thứ tự các xe là: xe lam - xe cứu thương - xe con.Đáp án 1 đúng. Đáp án 2 và 3 sai.Ghi chú: câu này cần lưu ý, xe đã vào giao lộ trước được đi trước cả xe ưu tiên chưa vào giao lộ.',1,1,1,1),
(363,8,'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?','Trong sa hình này, có 2 xe ưu tiên. Thứ tự các xe xe ưu tiên quy định trong Điều 22.1 Luật GTĐB 2008, theo đó xe chữa cháy (1) được ưu tiên cao nhất, nên đi trước xe cứu thường (2). Tất nhiên, với giả định rằng cả 2 xe này cùng đang làm nhiệm vụ.Xe còn lại sẽ đi sau cùng: xe con (3).Như vậy, thứ tự các xe sẽ là: xe cứu hỏa - xe cứu thương - xe con.Đáp án 2 đúng. Đáp án còn lại sai.',0,1,1,1),
(364,8,'Xe nào được quyền đi trước trong trường hợp này?','Trong sa hình này, xe cứu thương là xe ưu tiên nên sẽ được quyền đi trước (chưa cần xét tới đường ưu tiên).Đáp án 2 đúng. Đáp án 1 sai.',1,1,1,1),
(365,8,'Xe nào phải nhường đường đi cuối cùng qua nơi giao nhau?','Dùng nguyên tắc tiên - đường - quyền - hướng để giải bài này.Do không có xe ưu tiên, nên sẽ xét đến đường ưu tiên. Xe con (1) đang đi trên đường ưu tiên nên được đi đầu tiên.Sau đó, do cả 2 xe còn lại đều không phải nhường xe nào bên phải (quyền như nhau), nên sẽ xem xe nào có hướng ưu tiên. Xe tải (2) rẽ phải nên được đi trước xe khách (3) rẽ trái.Như vậy thứ tự các xe sẽ là: xe con - xe tải - xe khách. Nói cách khác, xe khách phải đi cuối cùng.Đáp án 1 đúng, đáp án 2 và 3 sai.',0,1,1,1),
(366,8,'Xe nào phải nhường đường là đúng quy tắc giao thông?','Trong sa hình này, không có xe ưu tiên, cả 2 xe đều đang không nằm trên đường ưu tiên (lưu ý biển phụ).Do đó, ta sẽ xét tiếp tới quyền ưu tiên tại ngã tư không có vòng xuyến: phải nhường cho xe bên phải tới. Như vậy thì xe con phải nhường đường cho xe tải là đúng quy tắc giao thông.Đáp án 1 đúng, đáp án 2 sai.',0,1,1,1),
(367,8,'Xe nào được quyền đi trước trong trường hợp này?','Trong sa hình này, cả 2 xe đều thuộc loại ưu tiên, vậy xe nào được đi trước?Theo quy định tại Điều 22.1 Luật GTĐB 2008, xe cứu hỏa được ưu tiên cao nhất, hơn xe công an. Do đó trong sa hình này xe cứu hỏa được đi trước.Đáp án 2 đúng. Đáp án 1 sai.',0,1,1,1),
(368,8,'Theo tín hiệu đèn thì xe nào được phép đi?','Xe nào gặp đèn xanh thì được phép đi, gặp đèn đỏ phải dừng lại. Vậy, xe con và xe khách được phép đi. Đáp án 1 đúng. Đáp án 2 sai.',1,1,1,1),
(369,8,'Theo tín hiệu đèn, xe nào được quyền đi là đúng quy tắc giao thông?','Trong sa hình này, tín hiệu đèn vẽ không giống lắm, nhìn như biển báo vậy. Nhưng cũng không quan trọng, ta cứ hiểu đó là đèn giao thông là được.Trên hình, xe khách và mô tô đang đi trên ở làn đường có đèn đỏ, nên phải dừng lại. Cả 2 xe con và 2 xe tải (trên 2 chiều) đều ở trên làn đường có đèn xanh, nên có quyền đi.Đáp án 3 đúng. Đáp án 1 và 2 sai.',1,1,1,1),
(370,8,'Trong trường hợp này xe nào được quyền đi trước?','Cả xe quân sự và xe công an đều được ưu tiên ngang nhau, theo Điều 22.1 Luật GTĐB 2008. Hai xe này cùng đang ở trên đường ưu tiên.Để biết xe nào đi trước, ta xét tiếp tới quyền ưu tiên trong ngã tư không có vòng xuyến: xe phải nhường đường cho xe từ bên phải tới. Như vậy xe công an phải nhường cho xe quân sự đi trước.Đáp án 2 đúng. Đáp án 1 sai.Ghi chú: có bạn lập luận xe quân sự được đi trước xe công an là chưa được chính xác, vì luật quy định cụ thể thứ tự xe ưu tiên như sau:Xe chữa cháy đi làm nhiệm vụ;Xe quân sự, xe công an đi làm nhiệm vụ khẩn cấp, đoàn xe có xe cảnh sát dẫn đường;Xe cứu thương đang thực hiện nhiệm vụ cấp cứu, v.v..Như vậy, cả xe quân sự và công an cùng ngang cấp ưu tiên thứ 2 (sau xe chữa cháy), nên không thể nói xe quân sự được ưu tiên hơn được. Do đó, muốn giải sa hình câu này, cần xét tiếp đến yếu tố đường ưu tiên, rồi hướng ưu tiên (như trên).',0,1,1,1),
(371,8,'Theo tín hiệu đèn, xe tải đi theo hướng nào là đúng quy tắc giao thông?','Theo tín hiệu đèn, hướng 2, 3, 4 phải đi thẳng qua ngã ba sau cột đèn. Nhưng do có đèn đỏ hướng đi thẳng, nên xe không được đi theo hướng 2, 3, và 4.Hướng 1 rẽ phải đang có đèn xanh, nên xe được đi theo hướng này. Đáp án 2 đúng. Các đáp án còn lại đều sai.Ghi chú: hình tròn màu xanh nhìn hơi giống biển báo, nhưng phải hiểu là đèn giao thông thì mới đúng.',0,1,1,1),
(372,8,'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?','Theo quy định, các xe ở trên làn nào phải đi theo hướng quy định cho làn đó.Xe con rẽ phải theo đúng chỉ dẫn của làn, nên không vi phạm luật.Xe khách đang ở trên làn phải rẽ trái nhưng lại đi thẳng, là vi phạm quy tắc giao thông.Xe tải 1 (góc trái phía dưới sa hình) đang ở trên làn đi thẳng, mà rẽ trái cũng vi phạm. Xe tải 2 (góc phải phía trên sa hình) đang đi trên làn rẽ phải, nhưng lại rẽ trái cũng là sai luật.Xe mô tô đang trên làn rẽ trái, nhưng lại rẽ phải thì cũng vi phạm luật luôn.Tóm lại, 3 xe: khách, tải, mô tô đều vi phạm luật giao thông. Đáp án 1 đúng, các đáp án còn lại sai.',1,1,1,1),
(373,8,'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?','Trong sa hình này, mặc dù có đảo giao thông hình tròn nằm giữa giao lộ, nhưng không có biển báo hiệu vòng xuyến, nên đây không phải là vòng xuyến: các xe nhường đường cho xe từ bên phải tới.Vì không có xe ưu tiên, đường ưu tiên, nên có thể thấy ngay thứ tự các xe đi như sau:mô tô - xe tải - xe khách - xe con.Vậy đáp án 3 đúng, các đáp án còn lại sai.',1,1,1,1),
(374,8,'Trong trường hợp này, xe nào đỗ vi phạm quy tắc giao thông?','Trên đường có biển báo cấm đỗ cấm dừng với xe tải (xe trên 1,5 tấn). Như vậy xe tải 2,5 tấn đỗ là vi phạm luật giao thông. Đáp án 1 đúng, các đáp án còn lại sai.',1,1,1,1),
(375,8,'Xe nào được quyền đi trước?','Trong sa hình không có xe ưu tiên, nên xe nào đang ở trên đường ưu tiên sẽ được đi trước. Do đó, xe con được quyền đi trước.Đáp án 2 đúng. Đáp án 1 và 3 sai.',1,1,1,1),
(376,8,'Theo hướng mũi tên, những hướng nào xe gắn máy được đi?','Hướng 1 không có biển cấm nên xe được phép đi. Hướng 2 có biển cấm mô tô, nhưng không cấm xe gắn máy. Hướng 3 có biển cấm ô tô, nhưng cũng không cấm xe gắn máy.Như vậy theo hướng mũi tên, xe gắn máy được đi cả 3 hướng. Đáp án 1 đúng, đáp án 2 và 3 sai.Ghi chú: biển cấm mô tô (hướng 2) dễ nhầm với biển cấm xe gắn máy. Khác nhau cơ bản là: biển cấm xe gắn máy không có người ngồi trên xe.',1,1,1,1),
(377,8,'Xe nào đỗ vi phạm quy tắc giao thông?','Trên đường có biển cấm dừng đỗ về cả 2 hướng trước và sau biển (lưu ý có biển phụ). Do đó, cả 2 xe đỗ đều vi phạm luật giao thông.Đáp án 1 đúng. Các đáp án còn lại sai.',1,1,1,1),
(378,8,'Xe nào đỗ vi phạm quy tắc giao thông?','Xe con và mô tô đỗ trên phần đường dành cho người đi bộ nên vi phạm. Xe tải đỗ bên trái đường nên cũng vi phạm.Vậy cả 3 xe đỗ vi phạm quy tắc giao thông. Đáp án 3 đúng, các đáp án còn lại đều sai.',1,1,1,1),
(379,8,'Xe tải kéo mô tô ba bánh như hình này có đúng quy tắc giao thông không?','Trên đường có biển báo cấm ô tô kéo theo rơ-moóc hoặc kéo theo mô tô (2-3 bánh). Do đó đáp án 2 đúng, đáp án 1 sai.Xem thêm ý nghĩa biển báo cấm tại đây.',1,1,1,1),
(380,8,'Theo hướng mũi tên, hướng nào xe không được phép đi?','Hướng 5 có biển báo cấm xe ô tô, nên xe con không được đi vào. Những hướng kia không có biển cấm, nên xe con được đi vào bình thường.Đáp án 2 đúng. Đáp án 1 sai.',0,1,1,1),
(381,8,'Theo hướng mũi tên, những hướng nào ô tô không được phép đi?','Trên đường có biển cấm rẽ trái, cũng có nghĩa là cấm quay đầu. Do đó, hướng 2 và 3 ô tô không được phép đi. Hướng 1 và 4 không bị cấm nên xe được phép đi.Vậy đáp án 4 đúng. Các đáp án còn lại đều sai.',0,1,1,1),
(382,8,'Xe nào vượt đúng quy tắc giao thông?','Trong cả 2 hình đều có biển cấm ô tô tải vượt, nhưng không cấm ô tô con hoặc xe khách vượt xe khác.Như vậy thì đáp án 2 và 3 đều đúng. Đáp án 1 sai.Tham khảo thêm về ý nghĩa của các biển báo cấm.',0,1,1,1),
(383,8,'Theo hướng mũi tên, gặp biển hướng đi phải theo đặt trước ngã tư, những hướng nào xe được phép đi?','Biển này đặt trước ngã tư sẽ có hiệu lực tại ngã tư ngay sau biển: các xe chỉ được đi thẳng hoặc rẽ trái - hướng 2 và 3.Vậy đáp án 1 đúng, đáp án 2 và 3 sai.Tham khảo ý nghĩa của các biển hiệu lệnh.',0,1,1,1),
(384,8,'Xe kéo nhau như hình này có vi phạm quy tắc giao thông không?','Luật giao thông đường bộ 2008, Điều 29.3a quy định: cấm xe kéo rơ moóc, xe kéo sơ mi rơ moóc kéo thêm rơ moóc hoặc xe khác.Sa hình này thể hiện xe kéo rơ moóc kéo theo xe tải. Hành vi này là vi phạm điều luật nêu trên.Vậy đáp án 2 đúng, đáp án 1 sai.',0,1,1,1),
(385,8,'Xe nào phải nhường đường trong trường hợp này?','Xe tải đang thấy biển được nhường đường qua cầu hẹp. Còn xe khách đang thấy biển nhường đường cho xe khác đi ngược chiều qua cầu hẹp.Vậy đán án 1 đúng, đáp án 2 sai.',0,1,1,1),
(386,8,'Xe nào được quyền đi trước trong trường hợp này?','Tại ngã tư, cả 2 xe đều không phải là xe ưu tiên, hay trên đường ưu tiên, và phía bên phải không vướng xe khác, đồng thời đều gặp đèn xanh. Thế nên sẽ căn cứ theo hướng xe đi để phân định. Vì xe xích lô rẽ phải nên sẽ được đi trước, xe lam rẽ trái nên được đi sau.Đáp án 2 đúng, đáp án 1 sai.Ghi chú: trên hình vẽ, nên thêm phần hình ảnh bổ sung thể hiện xe xích lô cũng đang gặp đèn xanh. Tất nhiên, để như hiện tại thì chúng ta vẫn nên ngầm hiểu là đèn chiều đó cũng giống chiều của xe làm: đèn xanh.',1,1,1,1),
(387,8,'Xe kéo nhau trong trường hợp này đúng quy định không?','Về hình ảnh cảm quan, thì xe phía trước nhỏ hơn xe phía sau. Suy ra trọng lượng xe phía trước (xe kéo) nhỏ hơn trọng lượng xe phía sau (xe được kéo).Do đó đáp án 1 đúng, đáp án 2 sai.Tuy nhiên, không có gì đảm bảo chắc chắn rằng xe phía trước nhẹ hơn xe phía sau. Chỉ nhìn hình dáng, kích thước thì chưa đủ để kết luận về trọng lượng. Có lẽ với câu này, người làm đề thi nên bằng cách nào đó thể hiện được trọng lượng của từng xe. Như vậy mới đảm bảo tính chính xác, tránh gây hiểu nhầm, hay phải giả định khi làm bài thi.',0,1,1,1),
(388,8,'Theo hướng mũi tên, những hướng nào ô tô con được phép đi?','Hướng 1 có biển bắt buộc xe tải rẽ phải, không bắt buộc với xe con, nhưng dù sao xe con có thể đi theo hướng 1 bình thường. Hướng 2 có biển cấm xe ô tô, nên xe con không được vào. Hướng 3 và 4 không có biển cấm, xe còn được phép đi.Như vậy xe con được đi theo hướng 1, 3, 4. Đáp án 2 đúng. Đáp án còn lại sai.',0,1,1,1),
(389,8,'Thứ tự xe đi như thế nào là đúng quy tắc giao thông?','Câu này sẽ giải theo nguyên tắc: tiên - đường - quyền - hướng.Trên sa hình không có xe đã đi vào giao lộ, không có xe ưu tiên hay đường ưu tiên. Do đó, ta sẽ xét tiếp theo quyền ưu tiên: tại ngã tư không có bùng binh, phải nhường cho xe từ bên phải tới. Xe con nhường xe lam, xe lam nhường mô tô và xe đạp.Vậy thứ tự đi của các xe là: Môtô + xe đạp, xe lam, xe con.Đáp án 4 đúng, các đáp án kia sai.',1,1,1,1),
(390,8,'Theo hướng mũi tên, những hướng nào xe được phép đi?','Trong sa hình, xe tải gặp biển hiệu lệnh buộc phải rẽ phải theo hướng 1. Nói cách khác, xe này chỉ được đi theo hướng 1 mà thôi.Đáp án 1 đúng, các đáp án kia sai.',0,1,1,1),
(391,8,'Những hướng nào xe tải được phép đi?','Biển báo cấm xe tải đi theo hướng bên phải (hướng 1). Hướng 2 và 3 không cấm nên xe tải được phép đi bình thường.Vậy đáp án 2 đúng, đáp án 1 sai.',0,1,1,1),
(392,8,'Những hướng nào ô tô tải được phép đi?','Do có biển báo cấm ô tô, nên xe tải không được đi các hướng 2, 3, 4. Còn hướng 1 và 5 do không bị cấm, nên xe này đi bình thường.Đáp án 3 đúng, các đáp án kia sai.',0,1,1,1),
(393,8,'Những hướng nào ô tô tải được phép đi?','Hướng 2 có biển báo cấm xe tải, do đó xe tải không được phép đi. Các hướng còn lại không bị cấm, nên xe tải được đi bình thường.Đáp án 2 đúng, các đáp án kia sai.',0,1,1,1),
(394,8,'Xe nào được quyền đi trước trong trường hợp này?','Trên sa hình này, không có xe ưu tiên, nên chỉ xét đến đường ưu tiên.Các biển báo đều có biển phụ phía dưới thể hiện rõ đường nào được ưu tiên. Theo đó, mô tô đang ở trên đường ưu tiên nên được quyền đi trước.Đáp án 1 đúng, đáp án 2 sai.',1,1,1,1),
(395,8,'Ô tô con đi theo chiều mũi tên có vi phạm quy tắc giao thông không?','Xe con đang gặp biển hiệu lệnh chỉ được đi thẳng. Do đó, nếu quay đầu xe theo hướng mũi tên là vi phạm quy tắc giao thông.Đáp án 2 đúng, đáp án kia sai.',0,1,1,1),
(396,8,'Xe nào vi phạm quy tắc giao thông?','Xe con vi phạm luật giao thông, do đi đè lên vạch liền phân 2 làn đường ngược chiều nhau. Vạch này cấm xe đè lên.Xe mô tô vượt xe khách hợp lệ, vì không có biển cấm.Vậy đáp án 3 đúng. Các đáp án kia sai.',1,1,1,1),
(397,8,'Các xe đi như thế nào là đúng quy tắc giao thông?','Người điều khiển giao thông giơ tay thẳng đứng để báo hiệu xe ở tất cả các hướng dừng lại, trừ xe đã vào trong ngã tư được phép đi thẳng. Đó là quy định tại Điều 10.2.a Luật GTĐB 2008.Đáp án 3 đúng. Đáp án 1 và 2 sai.',1,1,1,1),
(398,8,'Theo hướng mũi tên, xe nào được phép đi?','Luật GTĐB 2008 Điều 10.2b quy định: Người điều khiên giao thông dang ngang hai tay để báo hiệu:xe ở phía trước và ở phía sau người điều khiển giao thông phải dừng lại; Xe ở phía bên phải và bên trái của người điều khiển giao thông được đi.Như vậy trên sa hình thì mô tô và xe tải được phép đi. Đáp án 3 đúng, đáp án còn lại sai.Ghi chú: câu này hỏi theo hướng mũi tên, nhưng thực ra chỉ quan tâm đến hiệu lệnh của CSGT. Cõ lẽ hơi đánh lừa người đọc một chút.',1,1,1,1),
(399,8,'Xe con vượt xe tải như trường hợp này có đúng không?','Điều 14.4a - Luật GTĐB 2008 quy định: Khi xe phía trước đang rẽ trái, hoặc có tín hiệu rẽ trái thì xe phía sau được vượt lên về bên phải.Trên sa hình, xe tải đang rẽ trái, nên xe con vượt lên về bên phải là đúng luật.Đáp án 1 đúng, đáp án 2 sai.',0,1,1,1),
(400,8,'Xe nào vượt đúng quy tắc giao thông?','Với vạch kẻ đường gồm 1 vạch liền song song với 1 vạch đứt quãng (cách nhau 10cm), phân chia 2 chiều chạy ngược nhau, thì xe bên phía vạch đứt được đè lên vạch để vượt xe khác, còn xe bên phía vạch liền thì không được đè lên vạch. Đây là vạch kẻ đường số 1.11 trong Quy chuẩn 41 về báo hiệu đường bộ.Như vậy, chỉ xe con đi bên phía vạch đứt quãng mới được đè vạch để vượt xe phía trước. Xe tải bên phía vạch liền không được đè vạch.Đáp án 3 đúng. Các đáp án còn lại sai.Tham khảo thêm về Ý nghĩa vạch kẻ đường.',0,1,1,1),
(401,8,'Đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?','Xe cứu hỏa đang làm nhiệm vụ là xe ưu tiên, được phép đi vào đường một chiều, nên không vi phạm luật giao thông.Biển cấm rẽ trái cũng cấm xe quay đầu. Thế nên gặp biển này mà xe tải quay đầu là vi phạm luật giao thông.Vậy đáp án 2 đúng. Đáp án 1 và 3 sai.',0,1,1,1),
(402,8,'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?','Trên sa hình, không có xe ưu tiên, và có biển báo đường đồng cấp, nên chưa tìm được xe nào được ưu tiên đi trước.Xét tiếp tới quyền ưu tiên, tại chỗ giao cắt không phải vòng xuyến, các xe đều phải nhường cho xe bên tay phải. Sa hình này là ngã 3, cả 3 đường nhánh đều có xe, nghĩa là không xe nào được quyền ưu tiên. Tất nhiên, có thể thấy xe con bên phải không có xe, vì không có đường nào, nên cũng không áp dụng được nguyên tác quyền ưu tiên.Vậy sang hướng ưu tiên. Xe nào rẽ phải được đi trước, đó là xe tải (1). Tiếp đó đến xe đi thẳng: xe khách (2). Và cuối cùng mới đến xe rẽ trái: xe con (3).Vậy thứ tự các xe là: xe tải - xe khách - xe con.Đáp án 3 đúng. Các đáp án còn lại sai.Ghi chú: câu này giải có vẽ vẫn hơi thiên cưỡng ở đoạn quyền ưu tiên, vì buộc phải coi xe con không có quyền ưu tiên hơn, do bên phải không có đường. Có lẽ sẽ dễ lập luận hơn, nếu ngã 3 bố trí kiểu chữ Y, khi đó bên phải các xe đều có xe khác, và buộc phải dùng đến nguyên tắc cuối cùng: hướng ưu tiên.',0,1,1,1),
(403,8,'Thứ tự xe đi như thế nào là đúng quy tắc giao thông?','Trên sa hình, không có xe ưu tiên, và có biển báo đường đồng cấp, nên chưa tìm được xe nào được ưu tiên đi trước.Xét tiếp tới quyền ưu tiên, tại chỗ giao cắt không phải vòng xuyến, các xe đều phải nhường cho xe bên tay phải. Sa hình này là ngã 3, cả 3 đường nhánh đều có xe, nghĩa là không xe nào được quyền ưu tiên. Tất nhiên, có thể thấy xe con bên phải không có xe, vì không có đường nào, nên cũng không áp dụng được nguyên tác quyền ưu tiên.Vậy sang hướng ưu tiên. Xe nào rẽ phải được đi trước, đó là xe khách và xe tải (1). Tiếp đó đến xe rẽ trái: xe con (2).Vậy thứ tự các xe là: xe khách và xe tải - xe con.Đáp án 1 đúng. Các đáp án còn lại sai.Ghi chú: câu này cách giải có vẽ vẫn hơi thiên cưỡng ở đoạn quyền ưu tiên, vì buộc phải coi xe con không có quyền ưu tiên hơn, do bên phải không có đường. Có lẽ sẽ dễ lập luận hơn, nếu ngã 3 bố trí kiểu chữ Y, khi đó bên phải các xe đều có xe khác, và buộc phải dùng đến nguyên tắc cuối cùng: hướng ưu tiên.',0,1,1,1),
(404,8,'Những hướng nào ô tô tải được phép đi?','Các hướng 1, 2, và 3 không có biển cấm nên xe ô tô tải được phép đi bình thường. Riêng hướng 4 có biển cấm đi ngược chiều; đi theo hướng 4 là đi ngược chiều, nên ô tô tải không được đi vào.Như vậy đáp án 3 đúng, đáp án 1 và 2 sai.',0,1,1,1),
(405,8,'Theo hướng mũi tên, thứ tự các xe đi như thế nào là đúng?','Xe công an là xe ưu tiên nên được đi trước tiên (1).Sau đó đến xe trên đường ưu tiên: xe con và xe tải. Cần xét tiếp tới quyền ưu tiên: xe nào bên phải không có xe khác thì được đi trước. Như vậy, bên phải xe con đã trống (xe công an đã đi qua rồi) nên xe con được đi trước (2), còn xe tải (3) bên phải có xe khách nên sẽ đi sau xe con.Cuối cùng là tới xe khách (3).Vậy thứ tự các xe: Xe công an, xe con, xe tải, xe khách.',0,1,1,1),
(406,8,'Những hướng nào ô tô tải được phép đi?','Hướng 1 không có biển cấm nên ô tô tải được phép đi bình thường. Hướng 4 có biển cấm máy kéo, nhưng không cấm xe tải, nên xe tải vẫn được đi.Do có biển cấm rẽ trái, cũng cấm quay đầu, nên hướng 2 và 3 ô tô tải không đi được.Vậy đáp án 3 đúng. Đáp án 1 và 2 sai.',0,1,1,1),
(407,8,'Trong hình dưới đây, xe nào chấp hành đúng quy tắc giao thông?','Xe con thứ nhất (bên trái góc dưới) rẽ phải khi đèn xanh, trên làn đường có biển báo rẽ phải là đúng quy tắc. Xe con thứ 2 (góc trên bên phải) đi thẳng khi đèn xanh, trên làn đường có biển báo đi thẳng cũng đúng quy tắc.Tương tự, 2 xe tải cũng đi đúng theo hướng chỉ dẫn của biển báo và đèn giao thông: xe bên phải rẽ phải, xe bên trái đi thẳng. Cả 2 xe tải này đi như vậy là đúng quy tắc.Xe khách và xe mô tô dừng lại trên làn rẽ trái khi gặp đèn đỏ, cũng là đúng quy tắc.Tóm lại, tất cả các xe trên sa hình đều đi đúng luật.Đáp án 2 đúng, đáp án 1 và 3 sai.',1,1,1,1),
(408,8,'Theo hướng mũi tên, những hướng nào xe mô tô được phép đi?','Hướng 1 không có biển cấm nên xe mô tô được đi vào bình thường.Hướng 2 có biển cấm mô tô, nên không được đi. Hướng 3 chỉ cấm ô tô và mô tô 3 bánh, không cấm mô tô 2 bánh.Tóm lại, xe mô tô được đi hướng 1 và 3. Đáp án 3 đúng, đáp án 1 và 2 sai.Ghi chú: cần phân biệt biển cấm xe mô tô (hình chiếc xe có người ngồi trên) với biển cấm xe gắn máy (hình chiếc xe không có người ngồi trên).',1,1,1,1),
(409,8,'Trong trường hợp này, thứ tự xe đi như thế nào là đúng quy tắc giao thông?','Xe quân sự và xe công an cùng là xe ưu tiên ngang cấp theo Điều 22.1 Luật GTĐB 2008, nên cần xét đến nguyên tắc đường ưu tiên để xem xe nào đi trước.Nhìn biển báo chính và phụ trên sa hình ta thấy cả 2 xe này đều ở trên đường ưu tiên, nên cần xét tiếp đến nguyên tắc quyền ưu tiên: vào ngã tư không có vòng xuyến, phải nhường cho xe bên phải. Xe công an phải nhường cho xe quân sự đang đi bên phía tay phải.Vậy xe quân sự đi trước tiên, sau đo đến xe công an. Tiếp theo, xe con và mô tô cùng đi thẳng trên đường không ưu tiên, nên sẽ được đi cùng lúc.Tóm lại thứ tự các xe như sau: Xe quân sự, xe công an, xe con + môtô. Như vậy, đáp án 2 đúng, các đáp án còn lại sai.',1,1,1,1),
(410,8,'Xe nào phải nhường đường là đúng quy tắc giao thông?','Nhìn hình vẽ và biển cảnh báo “dốc lên nguy hiểm” có thể thấy xe B đang lên dốc, xe A đang xuống dốc.Quy tắc nhường đường được quy định tại Điều 17.2 Luật GTĐB 2008 là: xe xuống dốc nhường xe lên dốc. Như vậy xe A phải nhường đường cho xe B.Đáp án 1 đúng, đáp án 2 sai.',0,1,1,1),
(411,8,'Xe con quay đầu đi ngược lại như hình vẽ dưới có vi phạm quy tắc giao thông không?','Xe con quay đầu tại phần đường dành cho người đi bộ là vi phạm quy định tại Điều 15.4 Luật GTĐB 2008: “Không được quay đầu xe ở phần đường dành cho người đi bộ qua đường…”Đáp án 2 đúng, đáp án 1 sai.',0,1,1,1),
(412,8,'Trong hình vẽ dưới, những xe nào vi phạm quy tắc giao thông?','',1,1,1,1),
(413,8,'Để điều khiển cho xe đi thẳng, người lái xe phải làm gì là đúng quy tắc giao thông?','Do xe con kia đã vào ngã 3 trước, nên phải được ưu tiên đi trước. Mặc dù có biển báo đường đồng cấp nhưng chưa xét đến biển này (nguyên tắc nhất chớm - nhì ưu - tam đường - tứ hướng).Đáp án 1 đúng, các đáp án kia sai.',0,1,1,1),
(414,8,'Người lái xe điều khiển xe chạy theo hướng nào là đúng quy tắc giao thông?','Hướng 1 và 2 không có biển cấm nên xe được đi vào. Hướng 3 có biển cấm đi ngược chiều, nên xe không được đi vào. Ngoài ra, do có biển cấm rẽ phải nên không được đi theo hướng 4.Tóm lại, người lái xe chỉ được cho xe chạy theo hướng 1 và 2. Đáp án 2 đúng, các đáp án còn lại sai.',0,1,1,1),
(415,8,'Xe nào phải nhường đường là đúng quy tắc giao thông?','Xe đỏ phía trước đang đi thẳng. Xe xanh đi sau bật xi nhan phải chuyển làn thì phải nhường đường xe đỏ, như vậy mới đảm bảo an toàn và đúng quy định tại Điều 13.1 Luật giao thông đường bộ 2008.Đáp án 2 đúng, đáp án còn lại sai.',0,1,1,1),
(416,8,'Người lái xe điều khiển xe rẽ trái như thế nào là đúng quy tắc giao thông?','Người lái xe phải nhường xe tải vì xe tải đã vào trong ngã tư phải được đi trước. Tiếp đó, xe buýt đi thẳng nên cũng được nhường đi trước (vì cả 2 xe đều cùng ở trên đường không ưu tiên).Tóm lại, người lái xe phải nhường cả xe tải và xe buýt.Đáp án 3 đúng, các đáp án còn lại sai.',0,1,1,1),
(417,8,'Trong hình dưới, những xe nào vi phạm quy tắc giao thông?','Ta xem từng xe đi có đúng làn đường không:Hai xe con A và B đi đúng đường dành cho xe con.Xe mô tô C đi đúng đường dành cho mô tô.Mô tô D đi sai sang làn đường dành cho xe khách. Xe con E đi sai sang làn dành cho mô tô.Vậy có xe con (E) và mô tô (D) vi phạm quy tắc giao thông. Đáp án 3 đúng. Các đáp án còn lại đều sai.Tham khảo thêm bài viết về Làn đường và Lỗi đi sai làn đường.',1,1,1,1),
(418,8,'Xe nào đi trước là đúng quy tắc giao thông?','Nhìn biển báo giao nhau với đường ưu tiên, suy ra xe bạn đang trên đường không ưu tiên. Xe tải đang đi trên đường ưu tiên nên được đi trước. Đáp án 2 đúng, đáp án 1 sai.',0,1,1,1),
(419,8,'Người lái xe có thể quay đầu xe như thế nào là đúng quy tắc giao thông?','Hãy xét từng hướng quay đầu:Theo hướng A: do có biển cấm rẽ trái, cũng là cấm quay đầu, nên không được quay đầu theo hướng này.Theo hướng B: chưa đến vị trí cắm biển báo, nên biển chưa có hiệu lực. Nhưng theo hướng này xe sẽ đè qua vạch kẻ đường loại liền nét, phân chia 2 dòng phương tiện giao thông ngược chiều nhau. Đây là vạch số 1.1 trong Quy chuẩn 41 về báo hiệu đường bộ. Đè lên vạch liền này cũng là vi phạm luật giao thông.Vậy đáp án 3 đúng, đáp án 1 và 2 sai.Tham khảo thêm ý nghĩa Biển báo cấm và Vạch kẻ đường.',0,1,1,1),
(420,8,'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?','Xét theo nguyên tắc nhường đường: tiên - đường - quyền - hướng.Trên hình không có xe ưu tiên, xe của bạn đang đi trên đường ưu tiên (nhìn biển báo) nên được đi trước. Xe con trên đường không ưu tiên, nhưng khi rẽ phải không gây cản trở với xe bạn đi thẳng, nên xe con không phải nhường đường mà đi cùng lúc với xe bạn.Còn lại xe tải trên đường không ưu tiên, vì rẽ trái gây cản trở xe bạn nên phải nhường xe bạn đi trước trên đường ưu tiên. Do đó, xe tải đi sau cùng.Vậy thứ tự là: xe của bạn và xe con - xe tải.Đáp án 3 đúng, các đáp án kia sai.Ghi chú: trên hình vẽ, xe của bạn đang còn cách ngã tư hơi xa, nên rất dễ suy luận rằng 2 xe kia vào ngã tư trước và được đi trước. Có lẽ nên sửa lại hình vẽ để thể hiện cả 3 xe đều cùng đến ngã tư. Còn khi làm bài thì tạm thời bạn đành phải ghi nhớ câu hỏi này thôi.',0,1,1,1),
(421,8,'Khi muốn vượt xe tải, người lái xe phải làm gì là đúng quy tắc giao thông?','Nguyên tắc khi vượt xe khác phải đảm bảo một số điều kiện cơ bản (Điều 14 Luật GTĐB 2008):Báo hiệu bằng đèn hoặc còi;Xe trước đã tránh về bên phải;Đủ điều kiện an toàn;Vượt về bên trái, trừ những trường hợp nhất định.Đáp án 1 sai vì: tăng tốc cho xe vượt qua, khi xe tải phía trước chưa tránh hết về bên phải, chưa đủ an toàn, là vi phạm quy định.Đáp án 3 sai vì: đánh lái sang làn bên trái là đè lên vạch liền phân định 2 dòng xe ngược chiều nhau (vạch số 1.3 trong Quy chuẩn 41 về báo hiệu đường bộ). Vạch này cấm xe đè lên.Đáp án 2 đúng, vì đảm bảo các nguyên tắc vượt xe khác (nêu trên) và không đè vạch 1.3',0,1,1,1),
(422,8,'Theo tín hiệu đèn, xe nào phải dừng lại là đúng quy tắc giao thông?','Xe nào đi trên làn có đèn đỏ thì phải dừng lại. Như vậy đáp án sẽ là xe con và xe tải (đáp án 3 đúng).',1,1,1,1),
(423,8,'Xe nào phải nhường đường là đúng quy tắc giao thông?','Trên sa hình không có xe ưu tiên, đường cùng cấp, nên sẽ xét theo quyền ưu tiên của các xe.Xe tải đang đi thẳng không vướng xe nào, một mình một làn đường, nên không phải nhường đường;Xe con rẽ phải, không vướng xe nào bên phải, nên cũng không phải nhường đường; Xe khách đi thẳng, vướng xe con bên phải nên phải nhường đường (theo quy tắc: tại ngã 3 không có vòng xuyến, phải nhường đường cho xe từ bên phải tới).Vậy đáp án 2 đúng, đáp án 1 và 3 sai.',0,1,1,1),
(424,8,'Người lái xe có được vượt xe tải để đi thẳng trong trường hợp này không?','Người lái xe không được vượt xe tải vì luât cấm vượt xe khác tại nơi đường giao cắt (Điều 14.5d - Luật GTĐB 2008). Đáp án 2 đúng, đáp án kia sai.',0,1,1,1),
(425,8,'Bạn có được phép vượt xe mô tô phía trước không?','Điều 14.5d - Luật GTĐB 2008 cấm vượt xe khác tại nơi đường giao cắt. Đáp án 2 đúng, đáp án 1 sai.',1,1,1,1),
(426,8,'Người lái xe dừng tại vị trí nào là đúng quy tắc giao thông?','Người lái xe không được dừng tại vị trí A, vì như vậy sẽ đè qua vạch liền phân 2 dòng xe ngược chiều, và dừng bên trái đường cũng vi phạm luật.Dừng xe tại vị trí B và C là được phép, vì biển chỉ cấm đỗ, không cấm dừng.Đáp án 3 đúng, các đáp án kia sai.',0,1,1,1),
(427,8,'Bạn được dừng xe ở vị trí nào trong tình huống này?','Trên hình có biển cấm đỗ cấm dừng cả phía trước và phía sau (lưu ý biển phụ). Do đó, xe không được dừng ở vị trí nào. Đáp án 4 đúng, các đáp án còn lại đều sai.',0,1,1,1),
(428,8,'Theo tín hiệu đèn của xe cơ giới, xe nào vi phạm quy tắc giao thông?','Cả 2 xe đều đang bật xi nhan để rẽ, trong khi có biển báo chỉ được đi thẳng. Như vậy cả 2 xe đều vi phạm. Đáp án 1 và 2 đều đúng. Đáp án 3 sai.Ghi chú: lưu ý xe mô tô cũng đang bật đèn xi nhan phải, do hình hơi nhỏ nên dễ bị bỏ qua.',1,1,1,1),
(429,8,'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?','Hãy xem từng xe đi có đúng luật không:Xe khách dừng đèn đỏ: không vi phạm. Xe mô tô dừng đèn đỏ: không vi phạmXe ô tô con góc trái dưới rẽ phải khi đèn xanh trên làn rẽ phải: không vi phạm. Xe ô tô con góc phải trên: đi thẳng khi đèn xanh trên làn đi thẳng: không vi phạmXe tải góc trái dưới rẽ trái trên làn đi thẳng: vi phạm. Xe tải góc phải trên đi thẳng trên làn rẽ phải: vi phạmNhư vậy chỉ có 2 xe tải vi phạm. Đáp án 2 đúng, đáp án 1 và 3 sai.',1,1,1,1),
(430,8,'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?','Hãy xem từng xe đi có đúng luật không:Xe khách đi thẳng trên làn rẽ trái - Vi phạm.Xe tải rẽ trái khi đèn đỏ, và trên làn đi thẳng - Vi phạmXe con rẽ phải khi đèn xanh, trên làn rẽ phải - KHÔNG vi phạmCả 2 xe mô tô rẽ phải khi đèn xanh, trên làn rẽ phải - KHÔNG vi phạm.Như vậy, có xe khách và xe tải vi phạm quy tắc giao thông.Đáp án 3 đúng, đáp án 1 và 2 sai.',1,1,1,1),
(431,8,'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?','Hãy cùng xem từng xe đi có đúng luật không:Xe khách đi thẳng trên làn rẽ phải - Vi phạm.Xe tải đi thẳng khi gặp đèn đỏ - Vi phạmXe con rẽ phải khi đèn xanh, trên làn rẽ phải - KHÔNG vi phạmXe mô tô đi thẳng khi gặp đèn đỏ - Vi phạmNhư vậy, xe tải, xe khách, xe mô tô đều vi phạm quy tắc giao thông.Đáp án 2 đúng, các đáp án còn lại sai.',1,1,1,1),
(432,8,'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?','Hãy cùng xem từng xe đi có đúng luật không:Xe khách đi thẳng khi gặp đèn đỏ, trên làn rẽ trái - Vi phạmXe tải góc trái phía dưới rẽ trái trên làn đi thẳng - Vi phạmXe tải góc phải phía trên rẽ trái khi đèn đỏ, trên làn rẽ phải - Vi phạmXe con đi thẳng khi đèn xanh, trên làn đi thẳng - KHÔNG vi phạmNhư vậy, có xe khách và xe tải vi phạm quy tắc giao thông.Đáp án 1 đúng. Đáp án 2, 3, và 4 sai.',0,1,1,1),
(433,8,'Trong tình huống dưới đây, để tránh xe phía trước bị hỏng đột xuất trên đường, người lái xe phải làm gì?','Xe của bạn được vượt lên, chỉ khi đảm bảo an toàn, và bật tín hiệu bằng đèn hoặc còi.Đáp án 2 đúng, đáp án 1 và 3 sai.',0,1,1,1),
(434,8,'Các xe đi theo hướng mũi tên, xe nào chấp hành đúng quy tắc giao thông?','Hãy xem từng xe đi có đúng luật không:Xe khách rẽ trái khi đèn xanh, trên làn rẽ trái - KHÔNG vi phạmXe tải góc trái phía dưới đi thẳng khi gặp đèn đỏ - Vi phạm. Xe tải góc phải phía trên rẽ phải khi gặp đèn đỏ - Vi phạmXe con góc trái phía dưới rẽ phải khi gặp đèn đỏ - Vi phạm. Xe con góc phải phía trên đi thẳng khi gặp đèn đỏ - Vi phạmXe mô tô rẽ trái khi đèn xanh, trên làn rẽ trai - KHÔNG vi phạmNhư vậy, có xe khách và xe mô tô chấp hành đúng quy tắc giao thông.Đáp án 2 đúng, đáp án còn lại đều sai.',1,1,1,1),
(435,8,'Trong hình dưới, những xe nào vi phạm quy tắc giao thông?','Hãy xem từng xe đi có đúng luật không:Xe con A, C, E, và xe buýt G đang đi thẳng trên làn đường không có biển cấm - Không vi phạmXe con B đang đi đè lên vạch đôi liền phân 2 chiều ngược nhau (Vạch số 1.3), vạch này cấm xe đè lên - VI PHẠMXe tải D đang dừng hoặc đỗ đè lên vạch kẻ đường phân chia 2 làn, nghĩa là dừng đỗ trong phần đường xe chạy - VI PHẠM (Điều 18.3b Luật GTĐB 2008)Như vậy, có xe con B và tải D vi phạm quy tắc giao thông. Vậy đáp án 3 đúng, các đáp án còn lại sai.Ghi chú: Xe tải không có mũi tên phía trước như những xe khác, nghĩa là xe tải đang dừng hoặc đỗVạch liền đơn bên phải (xe tải đang đè lên) không rõ là vạch loại nào. Nếu là Vạch số 1.2 có tác dụng xác định mép phần xe cơ giới với phần xe thô sơ, người đi bộ hoặc lề đường, thì xe buýt đang đi sai làn. Còn nếu không phải vạch số 1.2 thì đó là loại vạch liền nào, có tác dụng gì?',0,1,1,1),
(436,8,'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?','Biển báo chính và phụ thể hiện xe bạn sắp đến chỗ giao cắt với đường ưu tiên mà xe mô tô đang đi. Như vậy xe mô tô được đi trước tiên.Sau đó, còn xe bạn và xe con cùng trên đường không ưu tiên. Nhưng tại ngã tư không có vòng xuyến, xe của bạn phải nhường cho xe con từ bên phải tới. Vì thế xe con sẽ được đi tiếp theo, còn xe bạn đi sau cùng.Tóm lại, thứ tự các xe đi như sau: Mô tô - xe con - xe của bạn.Đáp án 3 đúng, đáp án 1 và 2 sai.',1,1,1,1),
(437,8,'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?','Tại ngã 3 không có xe ưu tiên, không có biển báo đường ưu tiên, không có vòng xuyến, các xe sẽ nhường nhau theo quyền ưu tiên: xe nào bên phải trống thì được đi trước.Xe mô tô khi rẽ trái, gặp xe bạn đi tới từ bên phải, nên phải nhường xe bạn. Còn xe của bạn đi thẳng, nên vướng xe con đi đến bên tay phải nên cũng phải nhường cho xe con. Còn xe con rẽ phải, không bị vướng xe nào khác, nên chẳng phải nhường đường. Do đó, xe con được đi trước tiên.Sau xe con, xe của bạn trống bên phải, nên được đi tiếp theo, và cuối cùng là đến xe mô tô.Vậy thứ tự các xe là: xe con - xe của bạn - xe mô tô.Đáp án đúng là 2, các đáp án 1 và 3 sai.',1,1,1,1),
(438,8,'Xe nào phải dừng lại trong trường hợp này ?','Xe con gặp đèn đỏ phải dừng lại. Xe của bạn rẽ phải ở ngã rẽ trước khi đèn đỏ có tác dụng, nên xe bạn không phải dừng lại.Đáp án 1 đúng, các đáp án còn lại sai.',0,1,1,1),
(439,8,'Xe của bạn được đi theo hướng nào trong trường hợp này?','Xe của bạn đang đi trên làn đường có vạch kẻ đường cho phép đi thẳng hoặc rẽ trái, không được rẽ phải. Đây là Vạch số 1.18 trong Quy chuẩn 41 về báo hiệu đường bộ. Thêm nữa, đèn giao thông đang màu xanh nên không phải dừng lại. Xe cũng không được chuyển sang làn phải để rẽ phải vì sẽ đè vạch liền - vi phạm luật.Do đó, đáp án 1 và 3 đúng, đáp án 2 sai.',0,1,1,1),
(440,8,'Xe của bạn được đi theo hướng nào trong trường hợp này?','Xe của bạn gặp đèn đỏ, phải dừng lại trước vạch sơn. Do đang đi trên làn có vạch kẻ đường (Vạch số 1.18) chỉ cho phép đi thẳng hoặc rẽ trái, nên khi đèn chuyển xanh, bạn có thể cho xe đi thẳng hoặc rẽ trái như vạch kẻ đường chỉ dẫn. Xe cũng không được chuyển sang làn phải vì sẽ đè vạch liền - vi phạm luật.Do đó, đáp án 3 và 4 đúng. Đáp án 1 và 2 sai.',0,1,1,1),
(441,8,'Bạn xử lý như thế nào trong trường hợp này?','Xe tải vào trong ngã tư trước, nên được đi trước, xe bạn phải nhường đường.Xe đạp phía trước đang đi trên phần đường dành riêng cho xe thô sơ (như biển báo và vạch kẻ đường thể hiện). Xe bạn muốn chuyển hướng rẽ phải thì phải nhường đường cho xe đạp này - Quy định tại Luật GTĐB 2008 - Điều 15.2. Do đó, bạn phải giảm tốc độ, đợi rẽ phải sau xe đạp.Tóm lại, xe bạn phải nhường đường cho cả 2 xe kia. Đáp án 2 chính xác, đáp án còn lại sai.',0,1,1,1),
(442,8,'Bạn xử lý như thế nào trong trường hợp này?','Trên sa hình có người đi bộ tại nơi có vạch kẻ đường dành cho người đi bộ. Do đó, xe của bạn phải giảm tốc độ và nhường đường cho họ. Nội dung này quy định tại Điều 11.4 - Luật GTĐB 2008.Ngoài ra, xe con màu xanh đã vào ngã tư trước, nên xe bạn phải nhường đường và đi sau, không được vượt tại ngã tư.Vậy đáp án 3 đúng. Đáp án 1 và 2 sai.',0,1,1,1),
(443,8,'Bạn xử lý như thế nào trong trường hợp này?','Khi gặp biển “Stop”, xe bạn phải dừng lại nhường đường cho xe đạp và xe khách đi trước. Đáp án 3 đúng, đáp án 1 và 2 sai.',0,1,1,1),
(444,8,'Xe nào phải nhường đường trong trường hợp này?','Trên hình vẽ, biển chỉ dẫn và biển phụ thể hiện xe của bạn và xe con đang ở trên đường ưu tiên. Như vậy suy ra xe tải đang ở trên đường không ưu tiên, nên phải nhường đường.Đáp án 2 đúng, đáp án 1 và 3 sai.',0,1,1,1),
(445,8,'Xe nào phải nhường đường trong trường hợp này ?','Khi tránh xe con đi ngược chiều, xe bạn có chướng ngại vật phía trước (là xe tải đang đỗ), nên phải nhường đường cho xe con kia đi trước. Tham khảo quy định tại Điều 17.2c - Luật giao thông đường bộ 2008.Đáp án 2 đúng, đáp án còn lại sai.',0,1,1,1),
(446,8,'Bạn xử lý như thế nào khi lái xe ô tô vượt qua đoàn người đi xe đạp có tổ chức?','Xe của bạn không được vượt qua đoàn người đi xe đạp vì trong đoạn đường định vượt có chướng ngại vật (xe con). Đó là quy định tại Điều 14.2 - Luật giao thông đường bộ 2008.',0,1,1,1),
(447,8,'Phía trước có một xe đang lùi vào nơi đỗ, xe con phía trước đang chuyển sang làn đường bên trái, bạn xử lý như thế nào trong trường hợp này?','Vì phía trước có trướng ngại vật là xe con đang lùi vào nơi đỗ, nên bạn không được vượt xe con phía trước (Đó là quy định tại Điều 14.2 - Luật giao thông đường bộ 2008). Do đó đáp án 3 sai.Để đảm bảo an toàn, bạn cần quan sát phía sau:Nếu không có xe xin vượt, thì bạn xi nhan và chuyển sang làn đường bên trái để tránh xa xe đang lùi;Nếu có xe xin vượt lên, thì bạn nên giảm tốc độ, giữ nguyên làn, và nếu cần thì dừng lại để chờ xe lùi vào chỗ đỗ xong mới đi tiếp.Như vậy đáp án 1 và 2 đúng, đáp án 3 sai.',0,1,1,1),
(448,8,'Bạn xử lý như thế nào khi xe phía trước đang lùi ra khỏi nơi đỗ?','Bạn không được vượt lên để đi tiếp, vì phía trước vừa có chướng ngại vật (xe đang lùi), vừa có xe chạy ngược chiều. Xe bạn cần giảm tốc độ, và nếu cần thì dừng lại nhường đường.Đáp án 3 đúng, đáp án 1 và 2 sai.',0,1,1,1),
(449,8,'Phía trước có một xe màu xanh đang vượt xe màu vàng trên làn đường của bạn, bạn xử lý như thế nào trong trường hợp này?','Vì xe xanh đang lấn làn để vượt xe vàng, nên để đảm bảo an toàn, bạn cần giảm tốc độ và đi sát lề đường bên phải. Đáp án 1 đúng, đáp án 2 và 3 sai.',0,1,1,1),
(450,8,'Phía trước có một xe màu xanh đang vượt xe màu vàng trên làn đường của bạn, bạn xử lý như thế nào trong trường hợp này?','Bạn không được vượt khi xe tải phía trước đang chuyển làn đường sang trái, và phía trước có xe chạy ngược chiều đang đi tới. Đáp án 1 và 3 sai.Để đảm bảo an toàn, bạn cần phanh xe giảm tốc độ chờ xe tải chuyển làn đường. Đáp án 2 đúng. Đáp án 1 và 3 sai.',0,1,1,1);
INSERT INTO 'CauTraLoi' (Id,IdCauHoi,CauTraLoi,DapAn) VALUES
(1,1,'1. Đường, cầu đường bộ.',1),
(2,1,'2. Hầm đường bộ, bến phà đường bộ.',1),
(3,1,'3. Đường, cầu đường bộ, hầm đường bộ, bến phà đường bộ và các công trình phụ trợ khác.',0),
(4,2,'1. Đường bộ, nơi dừng xe, đỗ xe trên đường, đèn tín hiệu, cọc tiêu, biển báo hiệu, dải phân cách.',1),
(5,2,'2. Đường bộ, nơi dừng xe, đỗ xe trên đường, đèn tín hiệu, cọc tiêu, biển báo hiệu, dải phân cách, hệ thống thoát nước và công trình, thiết bị phụ trợ khác.',1),
(6,2,'3. Đường bộ, nơi dừng xe, đỗ xe trong khu vực nhà ga đường sắt, cảng hàng không.',0),
(7,3,'1. Vạch kẻ đường là vạch chỉ sự phân chia làn đường, vị trí hoặc hướng đi, vị trí dừng lại.',1),
(8,3,'2. Vạch kẻ đường là vạch phân biệt vị trí dừng, đỗ trên đường.',0),
(9,3,'3. Tất cả các ý nêu trên.',0),
(10,4,'1. Là phần của đường bộ được sử dụng cho các phương tiện giao thông qua lại.',1),
(11,4,'2. Là phần đường bộ sử dụng cho các phương tiện giao thông qua lại, dải đất hai bên đường để bảo đảm an toàn giao thông.',0),
(12,4,'3. Là phần của đường bộ được sử dụng cho các phương tiện giao thông qua lại, các công trình, các thiết bị phụ trợ khác và dải đất dọc hai bên đường để bảo đảm an toàn giao thông.',0),
(13,5,'1. Là một phần của đường được chia theo chiều ngang của đường, có bề rộng đủ cho xe đỗ an toàn.',0),
(14,5,'2. Là một phần của đường xe chạy được chia theo chiều dọc của đường, có bề rộng đủ cho xe chạy an toàn.',1),
(15,5,'3. Cả 02 ý trên.',0),
(16,6,'1. Là khoảng trống có kích thước giới hạn về chiều cao, chiều rộng của đường, cầu, bến phà, hầm đường bộ để các xe, kể cả hàng hóa xếp trên xe đi qua được an toàn.',1),
(17,6,'2. Là khoảng trống có kích thước giới hạn về chiều rộng của đường, cầu, bến phà, hầm trên đường bộ để các xe, kể cả hàng hóa xếp trên xe đi qua được an toàn.',0),
(18,6,'3. Là khoảng trống có kích thước giới hạn về chiều cao của cầu, bến phà, hầm trên đường bộ để các xe đi qua được an toàn.',0),
(19,7,'1. Đường phố là đường đô thị, gồm lòng đường và hè phố.',1),
(20,7,'2. Đường phố là đường bộ ngoài đô thị có lòng đường đủ rộng cho các phương tiện giao thông qua lại.',0),
(21,7,'3. Cả hai ý nêu trên.',0),
(22,8,'1. Là bộ phận của đường để phân chia mặt đường thành hai chiều xe chạy riêng biệt hoặc để phân chia phần đường của xe cơ giới và xe thô sơ.',1),
(23,8,'2. Là bộ phận của đường để xác định ranh giới của đất dành cho đường bộ theo chiều ngang của đường.',0),
(24,8,'3. Là bộ phận của đường để ngăn cách không cho các loại xe vào những nơi không được phép.',0),
(25,9,'1. Loại cố định',1),
(26,9,'2. Loại di động',1),
