INSERT INTO 'MucXuPhat' (Id,IdPhuongTien,IdLoaiViPham,HanhVi,MucPhat,XuPhatKhac) VALUES
(1,1,1,'Điều khiển xe chạy quá tốc độ quy định từ 05 km/h đến dưới 10 km/h','100.000 - 200.000','Không'),
(2,1,1,'Điều khiển xe chạy dưới tốc độ tối thiểu trên những đoạn đường bộ có quy định tốc độ tối thiểu cho phép','200.000 - 400.000','Không'),
(3,1,1,'Điều khiển xe chạy quá tốc độ quy định từ 10 km/h đến 20 km/h','500.000 - 1.000.000','Không'),
(4,1,1,'Điều khiển xe chạy quá tốc độ quy định trên 20 km/h','2.000.000 – 3.000.000','- Tước GPLX 60 ngày'),
(5,1,1,'Điều khiển xe chạy quá tốc độ thành nhóm từ 2 xe trở lên','5.000.000 – 7.000.000','- Tước GPLX 60 ngày<br>- Tước GPLX không thời hạn nếu tái phạm hoặc vi phạm nhiều lần.<br>- Tịch thu phương tiện'),
(6,1,1,'Không chú ý quan sát, điều khiển xe chạy quá tốc độ quy định gây tai nạn giao thông','2.000.000 – 3.000.000','- Tước GPLX 60 ngày<br>- Tước GPLX không thời hạn nếu gây tai nạn từ mức nghiêm trọng trở lên'),
(7,1,1,'Không chú ý quan sát, điều khiển xe chạy quá tốc độ thành nhóm quy định gây tai nạn giao thông hoặc không chấp hành hiệu lệnh dừng xe, chống người thi hành công vụ','10.000.000 – 14.000.000','- Tước GPLX 60 ngày<br>- Tước GPLX không thời hạn nếu gây tai nạn từ mức nghiêm trọng trở lên<br>- Tước GPLX 120 ngày'),
(8,1,1,'Không giảm tốc độ và nhường đường khi điều khiển xe chạy từ trong ngõ, đường nhánh ra đường chính','100.000 - 200.000','Không'),
(9,1,1,'Điều khiển xe chạy tốc độ thấp mà không đi về bên phải phần đường xe chạy gây cản trở giao thông','100.000 - 200.000','Không'),
(10,1,1,'Tụ tập để cổ vũ, kích động hành vi điều khiển xe chạy quá tốc độ quy định, lạng lách, đánh võng, đuổi nhau trên đường hoặc đua xe trái phép.','1.000.000 – 2.000.000','Không'),
(11,1,1,'Đua xe mô tô, xe gắn máy, xe máy điện trái phép','1.000.000 – 2.000.000','- Tước GPLX 60 nếu gây tai nạn nhưng chưa tới mức nghiêm trọng<br>- Tạm giữ xe 10 ngày'),
(12,1,2,'Điều khiển xe trên đường mà trong máu hoặc hơi thở có nồng độ cồn vượt quá 50 miligram đến 80 miligram trên 100 mililít máu hoặc vượt quá 0.25 miligram đến 0.4 miligram trên 1 lít khí thở','500.000 – 1.000.000','- Tước GPLX 30 ngày.<br>- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày<br>- Tước GPLX không thời hạn nếu tái phạm hoặc vi phạm nhiều lần'),
(13,1,2,'Điều khiển xe trên đường mà trong máu hoặc hơi thở có nồng độ cồn vượt quá 80 miligram trên 100 mililít máu hoặc vượt quá 0.4 miligram trên 1 lít khí thở','2.000.000 – 3.000.000','- Tước GPLX 30 ngày<br>- Tước GPLX 120 ngày<br>- Tước GPLX không thời hạn nếu tái phạm hoặc vi phạm nhiều lần'),
(14,1,2,'Điều khiển xe trên đường mà trong cơ thể có chất ma túy','2.000.000 – 3.000.000','- Tịch thu phương tiện<br>- Tước GPLX không thời hạn nếu tái phạm hoặc vi phạm nhiều lần'),
(15,1,2,'Không chấp hành yêu cầu kiểm tra về chất ma túy, nồng độ cồn của người kiểm soát giao thông hoặc người thi hành công vụ','2.000.000 – 3.000.000','- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày'),
(16,1,2,'Không chấp hành yêu cầu kiểm tra về chất ma túy, nồng độ cồn của người kiểm soát giao thông hoặc người thi hành công vụ trừ các hành vi, vi phạm quy định tại Điểm g Khoản 6 Điều 9','3.000.000 – 5.000.000','- Tước GPLX 60 ngày<br>- Tước GPLX không thời hạn'),
(17,1,3,'Không thực hiện đúng các quy định về dừng xe, đỗ xe tại nơi đường bộ giao nhau cùng mức với đường sắt; dừng xe, đỗ xe trên đường xe điện, điểm dừng đón trả khách của xe buýt, trên cầu, nơi đường bộ giau nhau, trên phần đường dành cho người đi bộ qua đường; dừng xe nơi có biển cấm dừng; đỗ xe tại nơi có biển cấm dừng hoặc biển cấm đỗ','100.000 – 200.000','- Tước GPLX 30 ngày<br>- Tước GPLX 120 ngày'),
(18,1,3,'Dừng xe, đỗ xe ở lòng đường đô thị gây cản trở giao thông; tụ tập từ 3 xe trở lên ở lòng đường, trên cầu, trong hầm đường bộ; đỗ, để xe ở lòng đường đô thị, hè phố trái quy định của pháp luật','100.000 – 200.000','- Tước GPLX 30 ngày<br>- Tước GPLX 120 ngày'),
(19,1,3,'Dừng xe, đỗ xe trên phần đường xe chạy ở đoạn đường ngoài đô thị nơi có lề đường','100.000 – 200.000','- Tước GPLX 30 ngày<br>- Tước GPLX 120 ngày'),
(20,1,3,'Dừng xe, đỗ xe trong hầm đường bộ không đúng nơi quy định','500.000 – 1.000.000','- Tước GPLX 120 ngày'),
(21,1,3,'Dừng xe, đỗ xe trong phạm vi đường ngang, cầu chung','100.000 – 200.000','Không'),
(22,1,4,'Không có báo hiệu xin vượt trước khi vượt','60.000 – 80.000','Không'),
(23,1,4,'Không nhường đường cho xe xin vượt khi có đủ điều kiện an toàn','80.000 – 100.000','- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày'),
(24,1,4,'Vượt phải trong các trường hợp không được phép','200.000 – 400.000','Không'),
(25,1,4,'Vượt xe trong những trường hợp cấm vượt, trừ các hành vi vi phạm quy định tại Điểm h Khoản 4 Điều này','500.000 – 1.000.000','Không'),
(26,1,4,'Vượt xe trong hầm đường bộ không đúng nơi quy định','500.000 – 1.000.000','- Tước GPLX 120 ngày'),
(27,1,4,'Vượt xe trái quy định gây tai nạn giao thông','2.000.000 – 3.000.000','- Tước GPLX 30 ngày<br>- Tước GPLX 120 ngày'),
(28,1,5,'Chuyển hướng không nhường đường cho: các xe đi ngược chiều; người đi bộ, xe lăn của người khuyết tật đang qua đường tại nơi không có vạch kẻ đường cho người đi bộ','60.000 – 80.000','- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày'),
(29,1,5,'Chuyển hướng không nhường quyền đi trước cho: người đi bộ, xe lăn của người khuyết tật qua đường tại nơi có vạch kẻ đường dành cho người đi bộ; xe thô sơ đang đi trên phần đường dành cho xe thô sơ','60.000 – 80.000','- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày'),
(30,1,5,'Chuyển hướng không giảm tốc độ hoặc không có tín hiệu báo hướng rẽ','200.000 – 400.000','- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày'),
(31,1,5,'Quay đầu xe trong hầm đường bộ','500.000 – 1.000.000','- Tước GPLX 120 ngày.'),
(32,1,5,'Quay đầu xe tại nơi cấm quay đầu xe','80.000 – 100.000','- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày'),
(33,1,5,'Chuyển làn đường trái quy định gây tai nạn giao thông','2.000.000 – 3.000.000','- Tước GPLX 30 ngày<br>- Tước GPLX 120 ngày'),
(34,1,5,'Chuyển làn đường không đúng nơi cho phép hoặc không có tín hiệu báo trước','80.000 – 100.000','Không'),
(35,1,6,'Bấm còi trong thời gian từ 22 giờ ngày hôm trước đến 5 giờ ngày hôm sau, trừ các xe ưu tiên đang đi làm nhiệm vụ theo quy định','80.000 – 100.000','Không'),
(36,1,6,'Bấm còi, rú ga liên tục trong đô thị, khu đông dân cư, trừ các xe ưu tiên đang đi làm nhiệm vụ theo quy định','100.000 – 200.000','Không'),
(37,1,6,'Chạy trong hầm đường bộ không sử dụng đèn chiếu sáng gần','500.000 – 1.000.000','- Tước GPLX 120 ngày.'),
(38,1,6,'Không sử dụng đèn chiếu sáng về ban đêm hoặc khi sương mù, thời tiết xấu hạn chế tầm nhìn','80.000 – 100.000','- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày'),
(39,1,6,'Lùi xe mô tô ba bánh không quan sát hoặc không có tín hiệu báo trước','60.000 – 80.000','- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày'),
(40,1,6,'Xe được quyền ưu tiên khi đi làm nhiệm vụ không có tín hiệu còi, cờ, đèn theo đúng quy đinh','80.000 – 100.000','Không'),
(41,1,6,'Xe không được quyền ưu tiên sử dụng tín hiệu còi, cờ, đèn của xe ưu tiên','100.000 – 200.000','- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày'),
(42,1,6,'Sử dụng đèn chiếu xa trong khu đô thị, khi đông dân cư, trừ các xe ưu tiên đang đi làm nhiệm vụ theo quy định','80.000 – 100.000','Không'),
(43,1,6,'Sử dụng đèn chiếu xa khi tránh nhau','60.000 – 80.000','- Tước GPLX 60 ngày<br>- Tước GPLX 120 ngày'),
(44,1,6,'Không chấp hành hiệu lệnh, chỉ dẫn của biển báo hiệu, vạch kẻ đường, trừ các hành vi vi phạm quy định tại Điểm a, Điểm đ, Điểm h Khoản 2; Điểm c, Điểm đ, Điểm h, Điểm o Khoản 3; Điểm c, Điểm d, Điểm e, Điểm g, Điểm i Khoản 4; Điểm a, Điểm c, Điểm d Khoản 5; Điểm đ Khoản 6; Điểm d Khoản 7 Điều 1','60.000 – 80.000','- Tước GPLX 60 ngày nếu gây tai nạn nhưng chưa tới mức nghiêm trọng<br>- Tước GPLX không thời hạn nếu gây tai nạn từ mức nghiêm trọng trở lên'),
(45,1,6,'Không chấp hành hiệu lệnh của đèn tín hiệu giao thông trừ hành vi vi phạm quy định tại Điểm c Khoản 4 Điều 6','200.000 – 400.000','- Tước GPLX 30 ngày<br>- Tước GPLX 60 ngày nếu gây tai nạn nhưng chưa tới mức nghiêm trọng<br>- Tước GPLX không thời hạn nếu gây tai nạn từ mức nghiêm trọng trở lên<br>- Tạm giữ xe 10 ngày'),
(46,1,6,'Không tuân thủ hướng dẫn của người điều khiển giao thông khi qua phà, cầu phao hoặc nơi ùn tắc giao thông; không chấp hành hiệu lệnh của người điều khiển giao thông hoặc người kiểm soát giao thông','500.000 – 1.000.000','- Tước GPLX 60 ngày nếu gây tai nạn nhưng chưa tới mức nghiêm trọng<br>- Tước GPLX không thời hạn nếu gây tai nạn từ mức nghiêm trọng trở lên<br>- Tạm giữ xe 10 ngày'),
(47,1,6,'Đưa tiền, tài sản hoặc lợi ích vật chất khác cho người thi hành công vụ để trốn tránh việc xử lý vi phạm hành chính','3.000.000 – 5.000.000','- Tước GPLX 60 ngày nếu là người điều khiển'),
(48,1,6,'Cản trở hoặc không chấp hành yêu cầu thanh tra, kiểm tra, kiểm soát của người thi hành công vụ về: Các giấy tờ liên quan đến người và phương tiện, điều khiển hoạt động của phương tiện, hoạt động vận tải và dịch vụ hỗ trợ vận tải, đào tạo, sát hạch lái xe, đăng kiểm phương tiện','3.000.000 – 5.000.000','- Tước GPLX 60 ngày<br>- Tạm giữ xe (nếu là người điều khiển xe)'),
(49,1,6,'Có lời nói, hành động đe dọa, lăng mạ, xúc phạm danh dự hoặc chống lại người thi hành công vụ, xúi giục người khác không chấp hành yêu cầu thanh tra, kiểm tra, kiểm soát của người thi hành công vụ, sử dụng uy tín của cá nhân, tổ chức để gây áp lực, cản trở người thi hành công vụ, trừ các khoản hành vi vi phạm quy đinh tại: Điểm b Khoản 8, Khoản 9 Điều 8; Điểm g Khoản 6, Khoản 8 Điều 9; Khoản 2; Điểm a Khoản 57 Điều 37 của Nghị định này','3.000.000 – 5.000.000','- Tước GPLX 60 ngày<br>- Tạm giữ xe (nếu là người điều khiển xe)'),
(50,1,6,'Khi tín hiệu đèn giao thông đã chuyển sang màu đỏ nhưng không dừng lại trước vạch mà vẫn tiếp tục đi, trừ trường hợp đã đi quá vạch dừng trước khi tín hiệu đèn giao thông chuyển sang màu vàng','200.000 – 400.000','- Tước GPLX 30 ngày<br>- Tước GPLX 60 ngày nếu gây tai nạn nhưng chưa tới mức nghiêm trọng'),
(51,1,6,'Người điều khiển xe mô tô, xe gắn máy (kể cả xe máy điện), các loại xe tương tự xe mô tô và các loại xe tương tự xe gắn máy không chấp hành chỉ dẫn hiệu lệnh của biển báo hiệu, vạch kẻ đường khi đi qua đường ngang, cầu chung','100.000 – 200.000','Không'),
(52,1,6,'Người điều khiển xe mô tô, xe gắn máy (kể cả xe máy điện), các loại xe tương tự xe mô tô và các loại xe tương tự xe gắn máy vượt rào chắn đường ngang, cầu chung khi đèn đỏ đã bật sáng; không chấp hành chỉ dẫn hiệu lệnh của biển báo hiệu, vạch kẻ đường khi đi qua đường ngang, cầu chung','200.000 – 400.000','Không'),
